-- Đang đổ dữ liệu cho bảng `products`
--

INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `photo`, `price`, `discount`, `category_id`) VALUES
(3, 'Son Dưỡng Có Màu Bobbi Brown', '<p>Khắc phục hậu quả của đại dịch Covid-19, một s&aacute;ng kiến được đưa ra giữa c&aacute;c nước ASEAN l&agrave; tổ chức &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; để t&aacute;i mở cửa trong khu vực&hellip;</p>\r\n', '<p>S&aacute;ng 9/9, trong khu&ocirc;n khổ chương tr&igrave;nh Đại hội đồng Li&ecirc;n nghị viện ASEAN (AIPA) 41, UB Kinh tế AIPA c&oacute; phi&ecirc;n thảo luận về vai tr&ograve; của Nghị viện trong việc th&uacute;c đẩy gắn kết v&agrave; phục hồi kinh tế ASEAN sau đại dịch Covid-19.</p>\r\n\r\n<p>L&agrave; Trưởng đo&agrave;n Việt Nam tham gia phi&ecirc;n thảo luận, Ph&oacute; Chủ tịch Quốc hội Ph&ugrave;ng Quốc Hiển nhận định, đ&acirc;y l&agrave; chủ đề mang t&iacute;nh thiết thực, c&oacute; t&iacute;nh thời sự cấp b&aacute;ch đối với mọi quốc gia th&agrave;nh vi&ecirc;n AIPA hiện nay. Trong bối cảnh khu vực v&agrave; quốc tế bị ảnh hưởng nặng nề bởi dịch bệnh Covid-19 như hiện nay, Ph&oacute; Chủ tịch Quốc hội b&agrave;y tỏ mong muốn c&aacute;c nước th&agrave;nh vi&ecirc;n sớm vượt qua kh&oacute; khăn, phục hồi nền kinh tế, ổn định đời sống x&atilde; hội v&agrave; &ldquo;kh&ocirc;ng để ai bị bỏ lại ph&iacute;a sau&rdquo;.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-3-1599635216487.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n họp của UB Kinh tế AIPA 41 diễn ra cuối buổi s&aacute;ng ng&agrave;y 9/9/2020.</p>\r\n\r\n<p><strong>Mở cửa lại bi&ecirc;n giới, di chuyển an to&agrave;n trong ASEAN</strong></p>\r\n\r\n<p>Tham gia thảo luận, đại diện của đo&agrave;n Việt Nam n&ecirc;u r&otilde;, đại dịch Covid-19 kh&ocirc;ng chỉ l&agrave; một cuộc khủng hoảng y tế đơn thuần, m&agrave; c&ograve;n l&agrave; một cuộc khủng hoảng đối với sự ph&aacute;t triển khi c&aacute;c chuỗi cung ứng v&agrave; thương mại quốc tế bị ph&aacute; vỡ.</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của Ng&acirc;n h&agrave;ng Ph&aacute;t triển ch&acirc;u &Aacute; (ADB), dự b&aacute;o tăng trưởng tổng sản phẩm quốc nội (GDP) của khu vực Đ&ocirc;ng Nam &Aacute; sẽ giảm từ 4,4% năm 2019 xuống c&ograve;n -2,7% trong năm 2020, trước khi phục hồi l&ecirc;n mức 5,2% v&agrave;o năm 2021, đặc biệt do c&aacute;c mối quan hệ thương mại v&agrave; đầu tư mạnh mẽ của khu vực n&agrave;y với Trung Quốc.</p>\r\n\r\n<p>Quan ngại s&acirc;u sắc về những tổn thất to lớn do dịch bệnh Covid-19 g&acirc;y ra ở c&aacute;c quốc gia ASEAN, trong tư c&aacute;ch chủ nh&agrave;, đo&agrave;n Việt Nam mong muốn cộng đồng ASEAN thống nhất thực hiện một chuỗi giải ph&aacute;p mang t&iacute;nh đột ph&aacute;, khả thi.</p>\r\n\r\n<p>Một l&agrave;, thực hiện th&uacute;c đẩy nhanh việc trao đổi th&ocirc;ng tin li&ecirc;n quan đến du lịch v&agrave; sức khỏe v&agrave; c&aacute;c biện ph&aacute;p cần thiết kh&aacute;c để kiểm so&aacute;t sự l&acirc;y lan của đại dịch Covid-19; củng cố sự an t&acirc;m trong di chuyển, &aacute;p dụng c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; quy tr&igrave;nh y tế hỗ trợ di chuyển xuy&ecirc;n bi&ecirc;n giới của c&ocirc;ng d&acirc;n ASEAN kh&ocirc;ng bị gi&aacute;n đoạn, đồng thời tu&acirc;n thủ c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; y tế quốc tế cũng như c&aacute;c cam kết theo c&aacute;c Hiệp định c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-2-1599635215859.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Ph&oacute; Chủ tịch Quốc hội Việt Nam Ph&ugrave;ng Quốc Hiển tham gia phi&ecirc;n thảo luận.</p>\r\n\r\n<p>Hai l&agrave;, nghi&ecirc;n cứu kh&aacute;i niệm &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; như một s&aacute;ng kiến sơ bộ trong qu&aacute; tr&igrave;nh mở cửa lại bi&ecirc;n giới, h&igrave;nh th&agrave;nh c&aacute;c khu vực di chuyển an to&agrave;n trong ASEAN v&agrave; đề xuất hướng dẫn tham chiếu cho tất cả c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN m&agrave; kh&ocirc;ng l&agrave;m tổn hại đến những cam kết theo c&aacute;c thỏa thuận c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p>Ba l&agrave;, tiếp tục r&agrave; so&aacute;t v&agrave; ho&agrave;n thiện khu&ocirc;n khổ ph&aacute;p l&yacute; nhằm th&uacute;c đẩy thuận lợi h&oacute;a thương mại, bảo đảm lưu th&ocirc;ng h&agrave;ng h&oacute;a v&agrave; c&aacute;c chuỗi cung ứng trong khu vực kh&ocirc;ng bị gi&aacute;n đoạn; x&acirc;y dựng ch&iacute;nh s&aacute;ch đầu tư th&ocirc;ng tho&aacute;ng, bền vững, c&oacute; tr&aacute;ch nhiệm trong khu vực; bảo đảm an ninh lương thực v&agrave; chuỗi gi&aacute; trị n&ocirc;ng nghiệp; tăng cường sử dụng năng lượng t&aacute;i tạo; tiến tới x&acirc;y dựng kinh tế tuần ho&agrave;n.</p>\r\n\r\n<p>Bốn l&agrave;, th&uacute;c đẩy nhanh việc ph&ecirc; chuẩn c&aacute;c hiệp định, thỏa thuận thương mại trong khu vực; ưu ti&ecirc;n tăng cường c&aacute;c nỗ lực để ho&agrave;n tất đ&agrave;m ph&aacute;n v&agrave; k&yacute; kết Hiệp định Đối t&aacute;c Kinh tế To&agrave;n diện Khu vực (RCEP) trong năm 2020; tăng cường năng lực, sự tham gia v&agrave; đẩy mạnh gi&aacute;m s&aacute;t nghị viện đối với việc thực hiện c&aacute;c cam kết về đầu tư v&agrave; thương mại trong khu&ocirc;n khổ khu vực v&agrave; quốc tế, coi đ&acirc;y l&agrave; c&ocirc;ng cụ chủ yếu để th&uacute;c đẩy li&ecirc;n kết kinh tế s&acirc;u rộng hơn trong Cộng đồng ASEAN v&agrave; giữa ASEAN với c&aacute;c đối t&aacute;c kh&aacute;c.</p>\r\n\r\n<p><strong>Khai th&aacute;c t&agrave;i nguy&ecirc;n xuy&ecirc;n bi&ecirc;n giới để đảm bảo an ninh lương thực&nbsp;</strong></p>\r\n\r\n<p>Năm l&agrave;, tăng cường ph&aacute;t triển cơ sở hạ tầng số, kết nối số, an to&agrave;n dữ liệu số, kiến thức v&agrave; kỹ năng số gắn kết giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN để ph&aacute;t triển kinh tế số; chia sẻ th&ocirc;ng tin v&agrave; kinh nghiệm để xử l&yacute; c&aacute;c th&aacute;ch thức m&agrave; c&aacute;c nền tảng thương mại điện tử đang phải đối mặt; tận dụng c&aacute;c cơ hội của C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư nhằm khắc phục bất lợi từ việc đ&oacute;ng cửa nền kinh tế v&agrave; gi&atilde;n c&aacute;ch x&atilde; hội trong dịch bệnh Covid-19 v&agrave; duy tr&igrave; t&iacute;nh cạnh tranh trong kỷ nguy&ecirc;n kinh tế số.</p>\r\n\r\n<p>S&aacute;u l&agrave;, x&acirc;y dựng cơ sở hạ tầng kết nối khu vực, tăng cường kết nối giao th&ocirc;ng v&agrave; sự cần thiết của việc tăng cường hợp t&aacute;c tiểu v&ugrave;ng M&ecirc; C&ocirc;ng v&agrave; c&aacute;c tiểu v&ugrave;ng kh&aacute;c của ASEAN, đặc biệt l&agrave; về bảo vệ m&ocirc;i trường, ph&aacute;t triển n&ocirc;ng nghiệp th&ocirc;ng minh v&agrave; quản l&yacute; bền vững c&aacute;c nguồn t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n xuy&ecirc;n bi&ecirc;n giới nhằm đảm bảo an ninh lương thực, nguồn nước v&agrave; năng lượng tại c&aacute;c tiểu v&ugrave;ng trong v&agrave; sau đại dịch Covid-19.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-5-1599635216200.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n thảo luận diễn ra đồng thời tại điểm cầu của c&aacute;c nước trong cộng đồng ASEAN.</p>\r\n\r\n<p>Ph&aacute;t biểu tại phi&ecirc;n họp, đại diện đo&agrave;n Malaysia cho rằng, c&aacute;c nước trong ASEAN c&oacute; nhiều điểm chung n&ecirc;n ch&iacute;nh s&aacute;ch của bất cứ quốc gia n&agrave;o cũng c&oacute; thể được c&aacute;c quốc gia kh&aacute;c học tập, để c&oacute; thể tr&aacute;nh gặp sai lầm giống nhau.</p>\r\n\r\n<p>Theo nghị sỹ Malaysia, thời điểm n&agrave;y rất cần một c&aacute;ch tiếp cận chung theo hướng địa phương ho&aacute; c&aacute;c hoạt động kinh tế. Hầu hết hoạt động kinh tế đ&atilde; được t&iacute;ch hợp v&agrave;o chuối cũng ứng to&agrave;n cầu nhưng Covid- 19 đ&atilde; l&agrave;m gi&aacute;n đoạn chuỗi cung ứng n&agrave;y.</p>\r\n\r\n<p>B&agrave;i học l&agrave; cần phải giảm thiểu sự to&agrave;n cầu ho&aacute;. Nhiều doanh nghiệp đang địa phương ho&aacute; chuỗi sản xuất của m&igrave;n, ASEAN c&oacute; thể địa phương ho&aacute;, mở rộng sang khu vực ho&aacute;, xử l&yacute; chuỗi cung ứng khu vực để miễn dịch khủng hoảng trong tương lai, đại diện đo&agrave;n nghị sĩ Malaysia n&ecirc;u quan điểm.</p>\r\n\r\n<p>Chia sẻ kinh nghiệm ứng ph&oacute; với đại dịch Covid-19, đại diện Singapore cho biết nước n&agrave;y mở ra 4 loại ng&acirc;n s&aacute;ch, gồm quỹ ki&ecirc;n cường, quỹ chống chịu, quỹ đo&agrave;n kết v&agrave; quỹ thống&nbsp; nhất, bốn quỹ n&agrave;y được Ch&iacute;nh phủ hỗ trợ. B&ecirc;n cạnh đ&oacute; Singapore c&ograve;n c&oacute; ch&iacute;nh s&aacute;ch hỗ trợ để doanh nghiệp giữ việc l&agrave;m cho người lao động, ch&iacute;nh s&aacute;ch nay được gia hạn đến th&aacute;ng 3/2011.</p>\r\n\r\n<p><strong>Th&aacute;i Anh</strong></p>\r\n', 1, '1619061390_son-duong-co-mau-bobbi-brown-5.jpg', 600000, 5, 11),
(4, 'Son Kem Lì Eglips x Barbie Misty Velvet Tint', '<p>Khắc phục hậu quả của đại dịch Covid-19, một s&aacute;ng kiến được đưa ra giữa c&aacute;c nước ASEAN l&agrave; tổ chức &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; để t&aacute;i mở cửa trong khu vực&hellip;</p>\r\n', '<p>S&aacute;ng 9/9, trong khu&ocirc;n khổ chương tr&igrave;nh Đại hội đồng Li&ecirc;n nghị viện ASEAN (AIPA) 41, UB Kinh tế AIPA c&oacute; phi&ecirc;n thảo luận về vai tr&ograve; của Nghị viện trong việc th&uacute;c đẩy gắn kết v&agrave; phục hồi kinh tế ASEAN sau đại dịch Covid-19.</p>\r\n\r\n<p>L&agrave; Trưởng đo&agrave;n Việt Nam tham gia phi&ecirc;n thảo luận, Ph&oacute; Chủ tịch Quốc hội Ph&ugrave;ng Quốc Hiển nhận định, đ&acirc;y l&agrave; chủ đề mang t&iacute;nh thiết thực, c&oacute; t&iacute;nh thời sự cấp b&aacute;ch đối với mọi quốc gia th&agrave;nh vi&ecirc;n AIPA hiện nay. Trong bối cảnh khu vực v&agrave; quốc tế bị ảnh hưởng nặng nề bởi dịch bệnh Covid-19 như hiện nay, Ph&oacute; Chủ tịch Quốc hội b&agrave;y tỏ mong muốn c&aacute;c nước th&agrave;nh vi&ecirc;n sớm vượt qua kh&oacute; khăn, phục hồi nền kinh tế, ổn định đời sống x&atilde; hội v&agrave; &ldquo;kh&ocirc;ng để ai bị bỏ lại ph&iacute;a sau&rdquo;.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-3-1599635216487.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n họp của UB Kinh tế AIPA 41 diễn ra cuối buổi s&aacute;ng ng&agrave;y 9/9/2020.</p>\r\n\r\n<p><strong>Mở cửa lại bi&ecirc;n giới, di chuyển an to&agrave;n trong ASEAN</strong></p>\r\n\r\n<p>Tham gia thảo luận, đại diện của đo&agrave;n Việt Nam n&ecirc;u r&otilde;, đại dịch Covid-19 kh&ocirc;ng chỉ l&agrave; một cuộc khủng hoảng y tế đơn thuần, m&agrave; c&ograve;n l&agrave; một cuộc khủng hoảng đối với sự ph&aacute;t triển khi c&aacute;c chuỗi cung ứng v&agrave; thương mại quốc tế bị ph&aacute; vỡ.</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của Ng&acirc;n h&agrave;ng Ph&aacute;t triển ch&acirc;u &Aacute; (ADB), dự b&aacute;o tăng trưởng tổng sản phẩm quốc nội (GDP) của khu vực Đ&ocirc;ng Nam &Aacute; sẽ giảm từ 4,4% năm 2019 xuống c&ograve;n -2,7% trong năm 2020, trước khi phục hồi l&ecirc;n mức 5,2% v&agrave;o năm 2021, đặc biệt do c&aacute;c mối quan hệ thương mại v&agrave; đầu tư mạnh mẽ của khu vực n&agrave;y với Trung Quốc.</p>\r\n\r\n<p>Quan ngại s&acirc;u sắc về những tổn thất to lớn do dịch bệnh Covid-19 g&acirc;y ra ở c&aacute;c quốc gia ASEAN, trong tư c&aacute;ch chủ nh&agrave;, đo&agrave;n Việt Nam mong muốn cộng đồng ASEAN thống nhất thực hiện một chuỗi giải ph&aacute;p mang t&iacute;nh đột ph&aacute;, khả thi.</p>\r\n\r\n<p>Một l&agrave;, thực hiện th&uacute;c đẩy nhanh việc trao đổi th&ocirc;ng tin li&ecirc;n quan đến du lịch v&agrave; sức khỏe v&agrave; c&aacute;c biện ph&aacute;p cần thiết kh&aacute;c để kiểm so&aacute;t sự l&acirc;y lan của đại dịch Covid-19; củng cố sự an t&acirc;m trong di chuyển, &aacute;p dụng c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; quy tr&igrave;nh y tế hỗ trợ di chuyển xuy&ecirc;n bi&ecirc;n giới của c&ocirc;ng d&acirc;n ASEAN kh&ocirc;ng bị gi&aacute;n đoạn, đồng thời tu&acirc;n thủ c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; y tế quốc tế cũng như c&aacute;c cam kết theo c&aacute;c Hiệp định c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-2-1599635215859.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Ph&oacute; Chủ tịch Quốc hội Việt Nam Ph&ugrave;ng Quốc Hiển tham gia phi&ecirc;n thảo luận.</p>\r\n\r\n<p>Hai l&agrave;, nghi&ecirc;n cứu kh&aacute;i niệm &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; như một s&aacute;ng kiến sơ bộ trong qu&aacute; tr&igrave;nh mở cửa lại bi&ecirc;n giới, h&igrave;nh th&agrave;nh c&aacute;c khu vực di chuyển an to&agrave;n trong ASEAN v&agrave; đề xuất hướng dẫn tham chiếu cho tất cả c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN m&agrave; kh&ocirc;ng l&agrave;m tổn hại đến những cam kết theo c&aacute;c thỏa thuận c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p>Ba l&agrave;, tiếp tục r&agrave; so&aacute;t v&agrave; ho&agrave;n thiện khu&ocirc;n khổ ph&aacute;p l&yacute; nhằm th&uacute;c đẩy thuận lợi h&oacute;a thương mại, bảo đảm lưu th&ocirc;ng h&agrave;ng h&oacute;a v&agrave; c&aacute;c chuỗi cung ứng trong khu vực kh&ocirc;ng bị gi&aacute;n đoạn; x&acirc;y dựng ch&iacute;nh s&aacute;ch đầu tư th&ocirc;ng tho&aacute;ng, bền vững, c&oacute; tr&aacute;ch nhiệm trong khu vực; bảo đảm an ninh lương thực v&agrave; chuỗi gi&aacute; trị n&ocirc;ng nghiệp; tăng cường sử dụng năng lượng t&aacute;i tạo; tiến tới x&acirc;y dựng kinh tế tuần ho&agrave;n.</p>\r\n\r\n<p>Bốn l&agrave;, th&uacute;c đẩy nhanh việc ph&ecirc; chuẩn c&aacute;c hiệp định, thỏa thuận thương mại trong khu vực; ưu ti&ecirc;n tăng cường c&aacute;c nỗ lực để ho&agrave;n tất đ&agrave;m ph&aacute;n v&agrave; k&yacute; kết Hiệp định Đối t&aacute;c Kinh tế To&agrave;n diện Khu vực (RCEP) trong năm 2020; tăng cường năng lực, sự tham gia v&agrave; đẩy mạnh gi&aacute;m s&aacute;t nghị viện đối với việc thực hiện c&aacute;c cam kết về đầu tư v&agrave; thương mại trong khu&ocirc;n khổ khu vực v&agrave; quốc tế, coi đ&acirc;y l&agrave; c&ocirc;ng cụ chủ yếu để th&uacute;c đẩy li&ecirc;n kết kinh tế s&acirc;u rộng hơn trong Cộng đồng ASEAN v&agrave; giữa ASEAN với c&aacute;c đối t&aacute;c kh&aacute;c.</p>\r\n\r\n<p><strong>Khai th&aacute;c t&agrave;i nguy&ecirc;n xuy&ecirc;n bi&ecirc;n giới để đảm bảo an ninh lương thực&nbsp;</strong></p>\r\n\r\n<p>Năm l&agrave;, tăng cường ph&aacute;t triển cơ sở hạ tầng số, kết nối số, an to&agrave;n dữ liệu số, kiến thức v&agrave; kỹ năng số gắn kết giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN để ph&aacute;t triển kinh tế số; chia sẻ th&ocirc;ng tin v&agrave; kinh nghiệm để xử l&yacute; c&aacute;c th&aacute;ch thức m&agrave; c&aacute;c nền tảng thương mại điện tử đang phải đối mặt; tận dụng c&aacute;c cơ hội của C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư nhằm khắc phục bất lợi từ việc đ&oacute;ng cửa nền kinh tế v&agrave; gi&atilde;n c&aacute;ch x&atilde; hội trong dịch bệnh Covid-19 v&agrave; duy tr&igrave; t&iacute;nh cạnh tranh trong kỷ nguy&ecirc;n kinh tế số.</p>\r\n\r\n<p>S&aacute;u l&agrave;, x&acirc;y dựng cơ sở hạ tầng kết nối khu vực, tăng cường kết nối giao th&ocirc;ng v&agrave; sự cần thiết của việc tăng cường hợp t&aacute;c tiểu v&ugrave;ng M&ecirc; C&ocirc;ng v&agrave; c&aacute;c tiểu v&ugrave;ng kh&aacute;c của ASEAN, đặc biệt l&agrave; về bảo vệ m&ocirc;i trường, ph&aacute;t triển n&ocirc;ng nghiệp th&ocirc;ng minh v&agrave; quản l&yacute; bền vững c&aacute;c nguồn t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n xuy&ecirc;n bi&ecirc;n giới nhằm đảm bảo an ninh lương thực, nguồn nước v&agrave; năng lượng tại c&aacute;c tiểu v&ugrave;ng trong v&agrave; sau đại dịch Covid-19.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-5-1599635216200.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n thảo luận diễn ra đồng thời tại điểm cầu của c&aacute;c nước trong cộng đồng ASEAN.</p>\r\n\r\n<p>Ph&aacute;t biểu tại phi&ecirc;n họp, đại diện đo&agrave;n Malaysia cho rằng, c&aacute;c nước trong ASEAN c&oacute; nhiều điểm chung n&ecirc;n ch&iacute;nh s&aacute;ch của bất cứ quốc gia n&agrave;o cũng c&oacute; thể được c&aacute;c quốc gia kh&aacute;c học tập, để c&oacute; thể tr&aacute;nh gặp sai lầm giống nhau.</p>\r\n\r\n<p>Theo nghị sỹ Malaysia, thời điểm n&agrave;y rất cần một c&aacute;ch tiếp cận chung theo hướng địa phương ho&aacute; c&aacute;c hoạt động kinh tế. Hầu hết hoạt động kinh tế đ&atilde; được t&iacute;ch hợp v&agrave;o chuối cũng ứng to&agrave;n cầu nhưng Covid- 19 đ&atilde; l&agrave;m gi&aacute;n đoạn chuỗi cung ứng n&agrave;y.</p>\r\n\r\n<p>B&agrave;i học l&agrave; cần phải giảm thiểu sự to&agrave;n cầu ho&aacute;. Nhiều doanh nghiệp đang địa phương ho&aacute; chuỗi sản xuất của m&igrave;n, ASEAN c&oacute; thể địa phương ho&aacute;, mở rộng sang khu vực ho&aacute;, xử l&yacute; chuỗi cung ứng khu vực để miễn dịch khủng hoảng trong tương lai, đại diện đo&agrave;n nghị sĩ Malaysia n&ecirc;u quan điểm.</p>\r\n\r\n<p>Chia sẻ kinh nghiệm ứng ph&oacute; với đại dịch Covid-19, đại diện Singapore cho biết nước n&agrave;y mở ra 4 loại ng&acirc;n s&aacute;ch, gồm quỹ ki&ecirc;n cường, quỹ chống chịu, quỹ đo&agrave;n kết v&agrave; quỹ thống&nbsp; nhất, bốn quỹ n&agrave;y được Ch&iacute;nh phủ hỗ trợ. B&ecirc;n cạnh đ&oacute; Singapore c&ograve;n c&oacute; ch&iacute;nh s&aacute;ch hỗ trợ để doanh nghiệp giữ việc l&agrave;m cho người lao động, ch&iacute;nh s&aacute;ch nay được gia hạn đến th&aacute;ng 3/2011.</p>\r\n\r\n<p><strong>Th&aacute;i Anh</strong></p>\r\n', 0, '1619061317_son-kem-li-eglips-barbie-misty-velvet-tint.jpg', 230000, 5, 10),
(5, 'Son kem Romand Milk Tea Velvet Tint ', '<p>Khắc phục hậu quả của đại dịch Covid-19, một s&aacute;ng kiến được đưa ra giữa c&aacute;c nước ASEAN l&agrave; tổ chức &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; để t&aacute;i mở cửa trong khu vực&hellip;</p>\r\n', '<p>S&aacute;ng 9/9, trong khu&ocirc;n khổ chương tr&igrave;nh Đại hội đồng Li&ecirc;n nghị viện ASEAN (AIPA) 41, UB Kinh tế AIPA c&oacute; phi&ecirc;n thảo luận về vai tr&ograve; của Nghị viện trong việc th&uacute;c đẩy gắn kết v&agrave; phục hồi kinh tế ASEAN sau đại dịch Covid-19.</p>\r\n\r\n<p>L&agrave; Trưởng đo&agrave;n Việt Nam tham gia phi&ecirc;n thảo luận, Ph&oacute; Chủ tịch Quốc hội Ph&ugrave;ng Quốc Hiển nhận định, đ&acirc;y l&agrave; chủ đề mang t&iacute;nh thiết thực, c&oacute; t&iacute;nh thời sự cấp b&aacute;ch đối với mọi quốc gia th&agrave;nh vi&ecirc;n AIPA hiện nay. Trong bối cảnh khu vực v&agrave; quốc tế bị ảnh hưởng nặng nề bởi dịch bệnh Covid-19 như hiện nay, Ph&oacute; Chủ tịch Quốc hội b&agrave;y tỏ mong muốn c&aacute;c nước th&agrave;nh vi&ecirc;n sớm vượt qua kh&oacute; khăn, phục hồi nền kinh tế, ổn định đời sống x&atilde; hội v&agrave; &ldquo;kh&ocirc;ng để ai bị bỏ lại ph&iacute;a sau&rdquo;.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-3-1599635216487.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n họp của UB Kinh tế AIPA 41 diễn ra cuối buổi s&aacute;ng ng&agrave;y 9/9/2020.</p>\r\n\r\n<p><strong>Mở cửa lại bi&ecirc;n giới, di chuyển an to&agrave;n trong ASEAN</strong></p>\r\n\r\n<p>Tham gia thảo luận, đại diện của đo&agrave;n Việt Nam n&ecirc;u r&otilde;, đại dịch Covid-19 kh&ocirc;ng chỉ l&agrave; một cuộc khủng hoảng y tế đơn thuần, m&agrave; c&ograve;n l&agrave; một cuộc khủng hoảng đối với sự ph&aacute;t triển khi c&aacute;c chuỗi cung ứng v&agrave; thương mại quốc tế bị ph&aacute; vỡ.</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của Ng&acirc;n h&agrave;ng Ph&aacute;t triển ch&acirc;u &Aacute; (ADB), dự b&aacute;o tăng trưởng tổng sản phẩm quốc nội (GDP) của khu vực Đ&ocirc;ng Nam &Aacute; sẽ giảm từ 4,4% năm 2019 xuống c&ograve;n -2,7% trong năm 2020, trước khi phục hồi l&ecirc;n mức 5,2% v&agrave;o năm 2021, đặc biệt do c&aacute;c mối quan hệ thương mại v&agrave; đầu tư mạnh mẽ của khu vực n&agrave;y với Trung Quốc.</p>\r\n\r\n<p>Quan ngại s&acirc;u sắc về những tổn thất to lớn do dịch bệnh Covid-19 g&acirc;y ra ở c&aacute;c quốc gia ASEAN, trong tư c&aacute;ch chủ nh&agrave;, đo&agrave;n Việt Nam mong muốn cộng đồng ASEAN thống nhất thực hiện một chuỗi giải ph&aacute;p mang t&iacute;nh đột ph&aacute;, khả thi.</p>\r\n\r\n<p>Một l&agrave;, thực hiện th&uacute;c đẩy nhanh việc trao đổi th&ocirc;ng tin li&ecirc;n quan đến du lịch v&agrave; sức khỏe v&agrave; c&aacute;c biện ph&aacute;p cần thiết kh&aacute;c để kiểm so&aacute;t sự l&acirc;y lan của đại dịch Covid-19; củng cố sự an t&acirc;m trong di chuyển, &aacute;p dụng c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; quy tr&igrave;nh y tế hỗ trợ di chuyển xuy&ecirc;n bi&ecirc;n giới của c&ocirc;ng d&acirc;n ASEAN kh&ocirc;ng bị gi&aacute;n đoạn, đồng thời tu&acirc;n thủ c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; y tế quốc tế cũng như c&aacute;c cam kết theo c&aacute;c Hiệp định c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-2-1599635215859.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Ph&oacute; Chủ tịch Quốc hội Việt Nam Ph&ugrave;ng Quốc Hiển tham gia phi&ecirc;n thảo luận.</p>\r\n\r\n<p>Hai l&agrave;, nghi&ecirc;n cứu kh&aacute;i niệm &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; như một s&aacute;ng kiến sơ bộ trong qu&aacute; tr&igrave;nh mở cửa lại bi&ecirc;n giới, h&igrave;nh th&agrave;nh c&aacute;c khu vực di chuyển an to&agrave;n trong ASEAN v&agrave; đề xuất hướng dẫn tham chiếu cho tất cả c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN m&agrave; kh&ocirc;ng l&agrave;m tổn hại đến những cam kết theo c&aacute;c thỏa thuận c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p>Ba l&agrave;, tiếp tục r&agrave; so&aacute;t v&agrave; ho&agrave;n thiện khu&ocirc;n khổ ph&aacute;p l&yacute; nhằm th&uacute;c đẩy thuận lợi h&oacute;a thương mại, bảo đảm lưu th&ocirc;ng h&agrave;ng h&oacute;a v&agrave; c&aacute;c chuỗi cung ứng trong khu vực kh&ocirc;ng bị gi&aacute;n đoạn; x&acirc;y dựng ch&iacute;nh s&aacute;ch đầu tư th&ocirc;ng tho&aacute;ng, bền vững, c&oacute; tr&aacute;ch nhiệm trong khu vực; bảo đảm an ninh lương thực v&agrave; chuỗi gi&aacute; trị n&ocirc;ng nghiệp; tăng cường sử dụng năng lượng t&aacute;i tạo; tiến tới x&acirc;y dựng kinh tế tuần ho&agrave;n.</p>\r\n\r\n<p>Bốn l&agrave;, th&uacute;c đẩy nhanh việc ph&ecirc; chuẩn c&aacute;c hiệp định, thỏa thuận thương mại trong khu vực; ưu ti&ecirc;n tăng cường c&aacute;c nỗ lực để ho&agrave;n tất đ&agrave;m ph&aacute;n v&agrave; k&yacute; kết Hiệp định Đối t&aacute;c Kinh tế To&agrave;n diện Khu vực (RCEP) trong năm 2020; tăng cường năng lực, sự tham gia v&agrave; đẩy mạnh gi&aacute;m s&aacute;t nghị viện đối với việc thực hiện c&aacute;c cam kết về đầu tư v&agrave; thương mại trong khu&ocirc;n khổ khu vực v&agrave; quốc tế, coi đ&acirc;y l&agrave; c&ocirc;ng cụ chủ yếu để th&uacute;c đẩy li&ecirc;n kết kinh tế s&acirc;u rộng hơn trong Cộng đồng ASEAN v&agrave; giữa ASEAN với c&aacute;c đối t&aacute;c kh&aacute;c.</p>\r\n\r\n<p><strong>Khai th&aacute;c t&agrave;i nguy&ecirc;n xuy&ecirc;n bi&ecirc;n giới để đảm bảo an ninh lương thực&nbsp;</strong></p>\r\n\r\n<p>Năm l&agrave;, tăng cường ph&aacute;t triển cơ sở hạ tầng số, kết nối số, an to&agrave;n dữ liệu số, kiến thức v&agrave; kỹ năng số gắn kết giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN để ph&aacute;t triển kinh tế số; chia sẻ th&ocirc;ng tin v&agrave; kinh nghiệm để xử l&yacute; c&aacute;c th&aacute;ch thức m&agrave; c&aacute;c nền tảng thương mại điện tử đang phải đối mặt; tận dụng c&aacute;c cơ hội của C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư nhằm khắc phục bất lợi từ việc đ&oacute;ng cửa nền kinh tế v&agrave; gi&atilde;n c&aacute;ch x&atilde; hội trong dịch bệnh Covid-19 v&agrave; duy tr&igrave; t&iacute;nh cạnh tranh trong kỷ nguy&ecirc;n kinh tế số.</p>\r\n\r\n<p>S&aacute;u l&agrave;, x&acirc;y dựng cơ sở hạ tầng kết nối khu vực, tăng cường kết nối giao th&ocirc;ng v&agrave; sự cần thiết của việc tăng cường hợp t&aacute;c tiểu v&ugrave;ng M&ecirc; C&ocirc;ng v&agrave; c&aacute;c tiểu v&ugrave;ng kh&aacute;c của ASEAN, đặc biệt l&agrave; về bảo vệ m&ocirc;i trường, ph&aacute;t triển n&ocirc;ng nghiệp th&ocirc;ng minh v&agrave; quản l&yacute; bền vững c&aacute;c nguồn t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n xuy&ecirc;n bi&ecirc;n giới nhằm đảm bảo an ninh lương thực, nguồn nước v&agrave; năng lượng tại c&aacute;c tiểu v&ugrave;ng trong v&agrave; sau đại dịch Covid-19.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-5-1599635216200.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n thảo luận diễn ra đồng thời tại điểm cầu của c&aacute;c nước trong cộng đồng ASEAN.</p>\r\n\r\n<p>Ph&aacute;t biểu tại phi&ecirc;n họp, đại diện đo&agrave;n Malaysia cho rằng, c&aacute;c nước trong ASEAN c&oacute; nhiều điểm chung n&ecirc;n ch&iacute;nh s&aacute;ch của bất cứ quốc gia n&agrave;o cũng c&oacute; thể được c&aacute;c quốc gia kh&aacute;c học tập, để c&oacute; thể tr&aacute;nh gặp sai lầm giống nhau.</p>\r\n\r\n<p>Theo nghị sỹ Malaysia, thời điểm n&agrave;y rất cần một c&aacute;ch tiếp cận chung theo hướng địa phương ho&aacute; c&aacute;c hoạt động kinh tế. Hầu hết hoạt động kinh tế đ&atilde; được t&iacute;ch hợp v&agrave;o chuối cũng ứng to&agrave;n cầu nhưng Covid- 19 đ&atilde; l&agrave;m gi&aacute;n đoạn chuỗi cung ứng n&agrave;y.</p>\r\n\r\n<p>B&agrave;i học l&agrave; cần phải giảm thiểu sự to&agrave;n cầu ho&aacute;. Nhiều doanh nghiệp đang địa phương ho&aacute; chuỗi sản xuất của m&igrave;n, ASEAN c&oacute; thể địa phương ho&aacute;, mở rộng sang khu vực ho&aacute;, xử l&yacute; chuỗi cung ứng khu vực để miễn dịch khủng hoảng trong tương lai, đại diện đo&agrave;n nghị sĩ Malaysia n&ecirc;u quan điểm.</p>\r\n\r\n<p>Chia sẻ kinh nghiệm ứng ph&oacute; với đại dịch Covid-19, đại diện Singapore cho biết nước n&agrave;y mở ra 4 loại ng&acirc;n s&aacute;ch, gồm quỹ ki&ecirc;n cường, quỹ chống chịu, quỹ đo&agrave;n kết v&agrave; quỹ thống&nbsp; nhất, bốn quỹ n&agrave;y được Ch&iacute;nh phủ hỗ trợ. B&ecirc;n cạnh đ&oacute; Singapore c&ograve;n c&oacute; ch&iacute;nh s&aacute;ch hỗ trợ để doanh nghiệp giữ việc l&agrave;m cho người lao động, ch&iacute;nh s&aacute;ch nay được gia hạn đến th&aacute;ng 3/2011.</p>\r\n\r\n<p><strong>Th&aacute;i Anh</strong></p>\r\n', 1, '1619061276_son-kem-romand-milk-tea-velvet-tint.jpg', 185000, 5, 10),
(6, 'Mặt Nạ JMsolution Cấp Ẩm Và Dưỡng Sáng Da 35ml', '<p>Khắc phục hậu quả của đại dịch Covid-19, một s&aacute;ng kiến được đưa ra giữa c&aacute;c nước ASEAN l&agrave; tổ chức &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; để t&aacute;i mở cửa trong khu vực&hellip;</p>\r\n', '<p>S&aacute;ng 9/9, trong khu&ocirc;n khổ chương tr&igrave;nh Đại hội đồng Li&ecirc;n nghị viện ASEAN (AIPA) 41, UB Kinh tế AIPA c&oacute; phi&ecirc;n thảo luận về vai tr&ograve; của Nghị viện trong việc th&uacute;c đẩy gắn kết v&agrave; phục hồi kinh tế ASEAN sau đại dịch Covid-19.</p>\r\n\r\n<p>L&agrave; Trưởng đo&agrave;n Việt Nam tham gia phi&ecirc;n thảo luận, Ph&oacute; Chủ tịch Quốc hội Ph&ugrave;ng Quốc Hiển nhận định, đ&acirc;y l&agrave; chủ đề mang t&iacute;nh thiết thực, c&oacute; t&iacute;nh thời sự cấp b&aacute;ch đối với mọi quốc gia th&agrave;nh vi&ecirc;n AIPA hiện nay. Trong bối cảnh khu vực v&agrave; quốc tế bị ảnh hưởng nặng nề bởi dịch bệnh Covid-19 như hiện nay, Ph&oacute; Chủ tịch Quốc hội b&agrave;y tỏ mong muốn c&aacute;c nước th&agrave;nh vi&ecirc;n sớm vượt qua kh&oacute; khăn, phục hồi nền kinh tế, ổn định đời sống x&atilde; hội v&agrave; &ldquo;kh&ocirc;ng để ai bị bỏ lại ph&iacute;a sau&rdquo;.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-3-1599635216487.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n họp của UB Kinh tế AIPA 41 diễn ra cuối buổi s&aacute;ng ng&agrave;y 9/9/2020.</p>\r\n\r\n<p><strong>Mở cửa lại bi&ecirc;n giới, di chuyển an to&agrave;n trong ASEAN</strong></p>\r\n\r\n<p>Tham gia thảo luận, đại diện của đo&agrave;n Việt Nam n&ecirc;u r&otilde;, đại dịch Covid-19 kh&ocirc;ng chỉ l&agrave; một cuộc khủng hoảng y tế đơn thuần, m&agrave; c&ograve;n l&agrave; một cuộc khủng hoảng đối với sự ph&aacute;t triển khi c&aacute;c chuỗi cung ứng v&agrave; thương mại quốc tế bị ph&aacute; vỡ.</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của Ng&acirc;n h&agrave;ng Ph&aacute;t triển ch&acirc;u &Aacute; (ADB), dự b&aacute;o tăng trưởng tổng sản phẩm quốc nội (GDP) của khu vực Đ&ocirc;ng Nam &Aacute; sẽ giảm từ 4,4% năm 2019 xuống c&ograve;n -2,7% trong năm 2020, trước khi phục hồi l&ecirc;n mức 5,2% v&agrave;o năm 2021, đặc biệt do c&aacute;c mối quan hệ thương mại v&agrave; đầu tư mạnh mẽ của khu vực n&agrave;y với Trung Quốc.</p>\r\n\r\n<p>Quan ngại s&acirc;u sắc về những tổn thất to lớn do dịch bệnh Covid-19 g&acirc;y ra ở c&aacute;c quốc gia ASEAN, trong tư c&aacute;ch chủ nh&agrave;, đo&agrave;n Việt Nam mong muốn cộng đồng ASEAN thống nhất thực hiện một chuỗi giải ph&aacute;p mang t&iacute;nh đột ph&aacute;, khả thi.</p>\r\n\r\n<p>Một l&agrave;, thực hiện th&uacute;c đẩy nhanh việc trao đổi th&ocirc;ng tin li&ecirc;n quan đến du lịch v&agrave; sức khỏe v&agrave; c&aacute;c biện ph&aacute;p cần thiết kh&aacute;c để kiểm so&aacute;t sự l&acirc;y lan của đại dịch Covid-19; củng cố sự an t&acirc;m trong di chuyển, &aacute;p dụng c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; quy tr&igrave;nh y tế hỗ trợ di chuyển xuy&ecirc;n bi&ecirc;n giới của c&ocirc;ng d&acirc;n ASEAN kh&ocirc;ng bị gi&aacute;n đoạn, đồng thời tu&acirc;n thủ c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; y tế quốc tế cũng như c&aacute;c cam kết theo c&aacute;c Hiệp định c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-2-1599635215859.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Ph&oacute; Chủ tịch Quốc hội Việt Nam Ph&ugrave;ng Quốc Hiển tham gia phi&ecirc;n thảo luận.</p>\r\n\r\n<p>Hai l&agrave;, nghi&ecirc;n cứu kh&aacute;i niệm &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; như một s&aacute;ng kiến sơ bộ trong qu&aacute; tr&igrave;nh mở cửa lại bi&ecirc;n giới, h&igrave;nh th&agrave;nh c&aacute;c khu vực di chuyển an to&agrave;n trong ASEAN v&agrave; đề xuất hướng dẫn tham chiếu cho tất cả c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN m&agrave; kh&ocirc;ng l&agrave;m tổn hại đến những cam kết theo c&aacute;c thỏa thuận c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p>Ba l&agrave;, tiếp tục r&agrave; so&aacute;t v&agrave; ho&agrave;n thiện khu&ocirc;n khổ ph&aacute;p l&yacute; nhằm th&uacute;c đẩy thuận lợi h&oacute;a thương mại, bảo đảm lưu th&ocirc;ng h&agrave;ng h&oacute;a v&agrave; c&aacute;c chuỗi cung ứng trong khu vực kh&ocirc;ng bị gi&aacute;n đoạn; x&acirc;y dựng ch&iacute;nh s&aacute;ch đầu tư th&ocirc;ng tho&aacute;ng, bền vững, c&oacute; tr&aacute;ch nhiệm trong khu vực; bảo đảm an ninh lương thực v&agrave; chuỗi gi&aacute; trị n&ocirc;ng nghiệp; tăng cường sử dụng năng lượng t&aacute;i tạo; tiến tới x&acirc;y dựng kinh tế tuần ho&agrave;n.</p>\r\n\r\n<p>Bốn l&agrave;, th&uacute;c đẩy nhanh việc ph&ecirc; chuẩn c&aacute;c hiệp định, thỏa thuận thương mại trong khu vực; ưu ti&ecirc;n tăng cường c&aacute;c nỗ lực để ho&agrave;n tất đ&agrave;m ph&aacute;n v&agrave; k&yacute; kết Hiệp định Đối t&aacute;c Kinh tế To&agrave;n diện Khu vực (RCEP) trong năm 2020; tăng cường năng lực, sự tham gia v&agrave; đẩy mạnh gi&aacute;m s&aacute;t nghị viện đối với việc thực hiện c&aacute;c cam kết về đầu tư v&agrave; thương mại trong khu&ocirc;n khổ khu vực v&agrave; quốc tế, coi đ&acirc;y l&agrave; c&ocirc;ng cụ chủ yếu để th&uacute;c đẩy li&ecirc;n kết kinh tế s&acirc;u rộng hơn trong Cộng đồng ASEAN v&agrave; giữa ASEAN với c&aacute;c đối t&aacute;c kh&aacute;c.</p>\r\n\r\n<p><strong>Khai th&aacute;c t&agrave;i nguy&ecirc;n xuy&ecirc;n bi&ecirc;n giới để đảm bảo an ninh lương thực&nbsp;</strong></p>\r\n\r\n<p>Năm l&agrave;, tăng cường ph&aacute;t triển cơ sở hạ tầng số, kết nối số, an to&agrave;n dữ liệu số, kiến thức v&agrave; kỹ năng số gắn kết giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN để ph&aacute;t triển kinh tế số; chia sẻ th&ocirc;ng tin v&agrave; kinh nghiệm để xử l&yacute; c&aacute;c th&aacute;ch thức m&agrave; c&aacute;c nền tảng thương mại điện tử đang phải đối mặt; tận dụng c&aacute;c cơ hội của C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư nhằm khắc phục bất lợi từ việc đ&oacute;ng cửa nền kinh tế v&agrave; gi&atilde;n c&aacute;ch x&atilde; hội trong dịch bệnh Covid-19 v&agrave; duy tr&igrave; t&iacute;nh cạnh tranh trong kỷ nguy&ecirc;n kinh tế số.</p>\r\n\r\n<p>S&aacute;u l&agrave;, x&acirc;y dựng cơ sở hạ tầng kết nối khu vực, tăng cường kết nối giao th&ocirc;ng v&agrave; sự cần thiết của việc tăng cường hợp t&aacute;c tiểu v&ugrave;ng M&ecirc; C&ocirc;ng v&agrave; c&aacute;c tiểu v&ugrave;ng kh&aacute;c của ASEAN, đặc biệt l&agrave; về bảo vệ m&ocirc;i trường, ph&aacute;t triển n&ocirc;ng nghiệp th&ocirc;ng minh v&agrave; quản l&yacute; bền vững c&aacute;c nguồn t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n xuy&ecirc;n bi&ecirc;n giới nhằm đảm bảo an ninh lương thực, nguồn nước v&agrave; năng lượng tại c&aacute;c tiểu v&ugrave;ng trong v&agrave; sau đại dịch Covid-19.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-5-1599635216200.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n thảo luận diễn ra đồng thời tại điểm cầu của c&aacute;c nước trong cộng đồng ASEAN.</p>\r\n\r\n<p>Ph&aacute;t biểu tại phi&ecirc;n họp, đại diện đo&agrave;n Malaysia cho rằng, c&aacute;c nước trong ASEAN c&oacute; nhiều điểm chung n&ecirc;n ch&iacute;nh s&aacute;ch của bất cứ quốc gia n&agrave;o cũng c&oacute; thể được c&aacute;c quốc gia kh&aacute;c học tập, để c&oacute; thể tr&aacute;nh gặp sai lầm giống nhau.</p>\r\n\r\n<p>Theo nghị sỹ Malaysia, thời điểm n&agrave;y rất cần một c&aacute;ch tiếp cận chung theo hướng địa phương ho&aacute; c&aacute;c hoạt động kinh tế. Hầu hết hoạt động kinh tế đ&atilde; được t&iacute;ch hợp v&agrave;o chuối cũng ứng to&agrave;n cầu nhưng Covid- 19 đ&atilde; l&agrave;m gi&aacute;n đoạn chuỗi cung ứng n&agrave;y.</p>\r\n\r\n<p>B&agrave;i học l&agrave; cần phải giảm thiểu sự to&agrave;n cầu ho&aacute;. Nhiều doanh nghiệp đang địa phương ho&aacute; chuỗi sản xuất của m&igrave;n, ASEAN c&oacute; thể địa phương ho&aacute;, mở rộng sang khu vực ho&aacute;, xử l&yacute; chuỗi cung ứng khu vực để miễn dịch khủng hoảng trong tương lai, đại diện đo&agrave;n nghị sĩ Malaysia n&ecirc;u quan điểm.</p>\r\n\r\n<p>Chia sẻ kinh nghiệm ứng ph&oacute; với đại dịch Covid-19, đại diện Singapore cho biết nước n&agrave;y mở ra 4 loại ng&acirc;n s&aacute;ch, gồm quỹ ki&ecirc;n cường, quỹ chống chịu, quỹ đo&agrave;n kết v&agrave; quỹ thống&nbsp; nhất, bốn quỹ n&agrave;y được Ch&iacute;nh phủ hỗ trợ. B&ecirc;n cạnh đ&oacute; Singapore c&ograve;n c&oacute; ch&iacute;nh s&aacute;ch hỗ trợ để doanh nghiệp giữ việc l&agrave;m cho người lao động, ch&iacute;nh s&aacute;ch nay được gia hạn đến th&aacute;ng 3/2011.</p>\r\n\r\n<p><strong>Th&aacute;i Anh</strong></p>\r\n', 1, '1619061228_mat-na-jmsolution-duong-am-chuyen-sau-chiet-xuat-hyaluronic.jpg', 25000, 5, 12),
(7, 'Mặt Nạ JMsolution Căng Bóng Da 30ml ', '<p>Khắc phục hậu quả của đại dịch Covid-19, một s&aacute;ng kiến được đưa ra giữa c&aacute;c nước ASEAN l&agrave; tổ chức &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; để t&aacute;i mở cửa trong khu vực&hellip;</p>\r\n', '<p>S&aacute;ng 9/9, trong khu&ocirc;n khổ chương tr&igrave;nh Đại hội đồng Li&ecirc;n nghị viện ASEAN (AIPA) 41, UB Kinh tế AIPA c&oacute; phi&ecirc;n thảo luận về vai tr&ograve; của Nghị viện trong việc th&uacute;c đẩy gắn kết v&agrave; phục hồi kinh tế ASEAN sau đại dịch Covid-19.</p>\r\n\r\n<p>L&agrave; Trưởng đo&agrave;n Việt Nam tham gia phi&ecirc;n thảo luận, Ph&oacute; Chủ tịch Quốc hội Ph&ugrave;ng Quốc Hiển nhận định, đ&acirc;y l&agrave; chủ đề mang t&iacute;nh thiết thực, c&oacute; t&iacute;nh thời sự cấp b&aacute;ch đối với mọi quốc gia th&agrave;nh vi&ecirc;n AIPA hiện nay. Trong bối cảnh khu vực v&agrave; quốc tế bị ảnh hưởng nặng nề bởi dịch bệnh Covid-19 như hiện nay, Ph&oacute; Chủ tịch Quốc hội b&agrave;y tỏ mong muốn c&aacute;c nước th&agrave;nh vi&ecirc;n sớm vượt qua kh&oacute; khăn, phục hồi nền kinh tế, ổn định đời sống x&atilde; hội v&agrave; &ldquo;kh&ocirc;ng để ai bị bỏ lại ph&iacute;a sau&rdquo;.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-3-1599635216487.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n họp của UB Kinh tế AIPA 41 diễn ra cuối buổi s&aacute;ng ng&agrave;y 9/9/2020.</p>\r\n\r\n<p><strong>Mở cửa lại bi&ecirc;n giới, di chuyển an to&agrave;n trong ASEAN</strong></p>\r\n\r\n<p>Tham gia thảo luận, đại diện của đo&agrave;n Việt Nam n&ecirc;u r&otilde;, đại dịch Covid-19 kh&ocirc;ng chỉ l&agrave; một cuộc khủng hoảng y tế đơn thuần, m&agrave; c&ograve;n l&agrave; một cuộc khủng hoảng đối với sự ph&aacute;t triển khi c&aacute;c chuỗi cung ứng v&agrave; thương mại quốc tế bị ph&aacute; vỡ.</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của Ng&acirc;n h&agrave;ng Ph&aacute;t triển ch&acirc;u &Aacute; (ADB), dự b&aacute;o tăng trưởng tổng sản phẩm quốc nội (GDP) của khu vực Đ&ocirc;ng Nam &Aacute; sẽ giảm từ 4,4% năm 2019 xuống c&ograve;n -2,7% trong năm 2020, trước khi phục hồi l&ecirc;n mức 5,2% v&agrave;o năm 2021, đặc biệt do c&aacute;c mối quan hệ thương mại v&agrave; đầu tư mạnh mẽ của khu vực n&agrave;y với Trung Quốc.</p>\r\n\r\n<p>Quan ngại s&acirc;u sắc về những tổn thất to lớn do dịch bệnh Covid-19 g&acirc;y ra ở c&aacute;c quốc gia ASEAN, trong tư c&aacute;ch chủ nh&agrave;, đo&agrave;n Việt Nam mong muốn cộng đồng ASEAN thống nhất thực hiện một chuỗi giải ph&aacute;p mang t&iacute;nh đột ph&aacute;, khả thi.</p>\r\n\r\n<p>Một l&agrave;, thực hiện th&uacute;c đẩy nhanh việc trao đổi th&ocirc;ng tin li&ecirc;n quan đến du lịch v&agrave; sức khỏe v&agrave; c&aacute;c biện ph&aacute;p cần thiết kh&aacute;c để kiểm so&aacute;t sự l&acirc;y lan của đại dịch Covid-19; củng cố sự an t&acirc;m trong di chuyển, &aacute;p dụng c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; quy tr&igrave;nh y tế hỗ trợ di chuyển xuy&ecirc;n bi&ecirc;n giới của c&ocirc;ng d&acirc;n ASEAN kh&ocirc;ng bị gi&aacute;n đoạn, đồng thời tu&acirc;n thủ c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; y tế quốc tế cũng như c&aacute;c cam kết theo c&aacute;c Hiệp định c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-2-1599635215859.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Ph&oacute; Chủ tịch Quốc hội Việt Nam Ph&ugrave;ng Quốc Hiển tham gia phi&ecirc;n thảo luận.</p>\r\n\r\n<p>Hai l&agrave;, nghi&ecirc;n cứu kh&aacute;i niệm &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; như một s&aacute;ng kiến sơ bộ trong qu&aacute; tr&igrave;nh mở cửa lại bi&ecirc;n giới, h&igrave;nh th&agrave;nh c&aacute;c khu vực di chuyển an to&agrave;n trong ASEAN v&agrave; đề xuất hướng dẫn tham chiếu cho tất cả c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN m&agrave; kh&ocirc;ng l&agrave;m tổn hại đến những cam kết theo c&aacute;c thỏa thuận c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p>Ba l&agrave;, tiếp tục r&agrave; so&aacute;t v&agrave; ho&agrave;n thiện khu&ocirc;n khổ ph&aacute;p l&yacute; nhằm th&uacute;c đẩy thuận lợi h&oacute;a thương mại, bảo đảm lưu th&ocirc;ng h&agrave;ng h&oacute;a v&agrave; c&aacute;c chuỗi cung ứng trong khu vực kh&ocirc;ng bị gi&aacute;n đoạn; x&acirc;y dựng ch&iacute;nh s&aacute;ch đầu tư th&ocirc;ng tho&aacute;ng, bền vững, c&oacute; tr&aacute;ch nhiệm trong khu vực; bảo đảm an ninh lương thực v&agrave; chuỗi gi&aacute; trị n&ocirc;ng nghiệp; tăng cường sử dụng năng lượng t&aacute;i tạo; tiến tới x&acirc;y dựng kinh tế tuần ho&agrave;n.</p>\r\n\r\n<p>Bốn l&agrave;, th&uacute;c đẩy nhanh việc ph&ecirc; chuẩn c&aacute;c hiệp định, thỏa thuận thương mại trong khu vực; ưu ti&ecirc;n tăng cường c&aacute;c nỗ lực để ho&agrave;n tất đ&agrave;m ph&aacute;n v&agrave; k&yacute; kết Hiệp định Đối t&aacute;c Kinh tế To&agrave;n diện Khu vực (RCEP) trong năm 2020; tăng cường năng lực, sự tham gia v&agrave; đẩy mạnh gi&aacute;m s&aacute;t nghị viện đối với việc thực hiện c&aacute;c cam kết về đầu tư v&agrave; thương mại trong khu&ocirc;n khổ khu vực v&agrave; quốc tế, coi đ&acirc;y l&agrave; c&ocirc;ng cụ chủ yếu để th&uacute;c đẩy li&ecirc;n kết kinh tế s&acirc;u rộng hơn trong Cộng đồng ASEAN v&agrave; giữa ASEAN với c&aacute;c đối t&aacute;c kh&aacute;c.</p>\r\n\r\n<p><strong>Khai th&aacute;c t&agrave;i nguy&ecirc;n xuy&ecirc;n bi&ecirc;n giới để đảm bảo an ninh lương thực&nbsp;</strong></p>\r\n\r\n<p>Năm l&agrave;, tăng cường ph&aacute;t triển cơ sở hạ tầng số, kết nối số, an to&agrave;n dữ liệu số, kiến thức v&agrave; kỹ năng số gắn kết giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN để ph&aacute;t triển kinh tế số; chia sẻ th&ocirc;ng tin v&agrave; kinh nghiệm để xử l&yacute; c&aacute;c th&aacute;ch thức m&agrave; c&aacute;c nền tảng thương mại điện tử đang phải đối mặt; tận dụng c&aacute;c cơ hội của C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư nhằm khắc phục bất lợi từ việc đ&oacute;ng cửa nền kinh tế v&agrave; gi&atilde;n c&aacute;ch x&atilde; hội trong dịch bệnh Covid-19 v&agrave; duy tr&igrave; t&iacute;nh cạnh tranh trong kỷ nguy&ecirc;n kinh tế số.</p>\r\n\r\n<p>S&aacute;u l&agrave;, x&acirc;y dựng cơ sở hạ tầng kết nối khu vực, tăng cường kết nối giao th&ocirc;ng v&agrave; sự cần thiết của việc tăng cường hợp t&aacute;c tiểu v&ugrave;ng M&ecirc; C&ocirc;ng v&agrave; c&aacute;c tiểu v&ugrave;ng kh&aacute;c của ASEAN, đặc biệt l&agrave; về bảo vệ m&ocirc;i trường, ph&aacute;t triển n&ocirc;ng nghiệp th&ocirc;ng minh v&agrave; quản l&yacute; bền vững c&aacute;c nguồn t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n xuy&ecirc;n bi&ecirc;n giới nhằm đảm bảo an ninh lương thực, nguồn nước v&agrave; năng lượng tại c&aacute;c tiểu v&ugrave;ng trong v&agrave; sau đại dịch Covid-19.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-5-1599635216200.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n thảo luận diễn ra đồng thời tại điểm cầu của c&aacute;c nước trong cộng đồng ASEAN.</p>\r\n\r\n<p>Ph&aacute;t biểu tại phi&ecirc;n họp, đại diện đo&agrave;n Malaysia cho rằng, c&aacute;c nước trong ASEAN c&oacute; nhiều điểm chung n&ecirc;n ch&iacute;nh s&aacute;ch của bất cứ quốc gia n&agrave;o cũng c&oacute; thể được c&aacute;c quốc gia kh&aacute;c học tập, để c&oacute; thể tr&aacute;nh gặp sai lầm giống nhau.</p>\r\n\r\n<p>Theo nghị sỹ Malaysia, thời điểm n&agrave;y rất cần một c&aacute;ch tiếp cận chung theo hướng địa phương ho&aacute; c&aacute;c hoạt động kinh tế. Hầu hết hoạt động kinh tế đ&atilde; được t&iacute;ch hợp v&agrave;o chuối cũng ứng to&agrave;n cầu nhưng Covid- 19 đ&atilde; l&agrave;m gi&aacute;n đoạn chuỗi cung ứng n&agrave;y.</p>\r\n\r\n<p>B&agrave;i học l&agrave; cần phải giảm thiểu sự to&agrave;n cầu ho&aacute;. Nhiều doanh nghiệp đang địa phương ho&aacute; chuỗi sản xuất của m&igrave;n, ASEAN c&oacute; thể địa phương ho&aacute;, mở rộng sang khu vực ho&aacute;, xử l&yacute; chuỗi cung ứng khu vực để miễn dịch khủng hoảng trong tương lai, đại diện đo&agrave;n nghị sĩ Malaysia n&ecirc;u quan điểm.</p>\r\n\r\n<p>Chia sẻ kinh nghiệm ứng ph&oacute; với đại dịch Covid-19, đại diện Singapore cho biết nước n&agrave;y mở ra 4 loại ng&acirc;n s&aacute;ch, gồm quỹ ki&ecirc;n cường, quỹ chống chịu, quỹ đo&agrave;n kết v&agrave; quỹ thống&nbsp; nhất, bốn quỹ n&agrave;y được Ch&iacute;nh phủ hỗ trợ. B&ecirc;n cạnh đ&oacute; Singapore c&ograve;n c&oacute; ch&iacute;nh s&aacute;ch hỗ trợ để doanh nghiệp giữ việc l&agrave;m cho người lao động, ch&iacute;nh s&aacute;ch nay được gia hạn đến th&aacute;ng 3/2011.</p>\r\n\r\n<p><strong>Th&aacute;i Anh</strong></p>\r\n', 0, '1619061183_mat-na-jmsolution-ngan-ngua-lao-hoa-chiet-xuat-gao.jpg', 25000, 20, 12);
INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `photo`, `price`, `discount`, `category_id`) VALUES
(8, 'Mặt Nạ JMSolution Dưỡng Ẩm Chiết Xuất Bơ 35ml', '<p>Khắc phục hậu quả của đại dịch Covid-19, một s&aacute;ng kiến được đưa ra giữa c&aacute;c nước ASEAN l&agrave; tổ chức &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; để t&aacute;i mở cửa trong khu vực&hellip;</p>\r\n', '<p>S&aacute;ng 9/9, trong khu&ocirc;n khổ chương tr&igrave;nh Đại hội đồng Li&ecirc;n nghị viện ASEAN (AIPA) 41, UB Kinh tế AIPA c&oacute; phi&ecirc;n thảo luận về vai tr&ograve; của Nghị viện trong việc th&uacute;c đẩy gắn kết v&agrave; phục hồi kinh tế ASEAN sau đại dịch Covid-19.</p>\r\n\r\n<p>L&agrave; Trưởng đo&agrave;n Việt Nam tham gia phi&ecirc;n thảo luận, Ph&oacute; Chủ tịch Quốc hội Ph&ugrave;ng Quốc Hiển nhận định, đ&acirc;y l&agrave; chủ đề mang t&iacute;nh thiết thực, c&oacute; t&iacute;nh thời sự cấp b&aacute;ch đối với mọi quốc gia th&agrave;nh vi&ecirc;n AIPA hiện nay. Trong bối cảnh khu vực v&agrave; quốc tế bị ảnh hưởng nặng nề bởi dịch bệnh Covid-19 như hiện nay, Ph&oacute; Chủ tịch Quốc hội b&agrave;y tỏ mong muốn c&aacute;c nước th&agrave;nh vi&ecirc;n sớm vượt qua kh&oacute; khăn, phục hồi nền kinh tế, ổn định đời sống x&atilde; hội v&agrave; &ldquo;kh&ocirc;ng để ai bị bỏ lại ph&iacute;a sau&rdquo;.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-3-1599635216487.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n họp của UB Kinh tế AIPA 41 diễn ra cuối buổi s&aacute;ng ng&agrave;y 9/9/2020.</p>\r\n\r\n<p><strong>Mở cửa lại bi&ecirc;n giới, di chuyển an to&agrave;n trong ASEAN</strong></p>\r\n\r\n<p>Tham gia thảo luận, đại diện của đo&agrave;n Việt Nam n&ecirc;u r&otilde;, đại dịch Covid-19 kh&ocirc;ng chỉ l&agrave; một cuộc khủng hoảng y tế đơn thuần, m&agrave; c&ograve;n l&agrave; một cuộc khủng hoảng đối với sự ph&aacute;t triển khi c&aacute;c chuỗi cung ứng v&agrave; thương mại quốc tế bị ph&aacute; vỡ.</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của Ng&acirc;n h&agrave;ng Ph&aacute;t triển ch&acirc;u &Aacute; (ADB), dự b&aacute;o tăng trưởng tổng sản phẩm quốc nội (GDP) của khu vực Đ&ocirc;ng Nam &Aacute; sẽ giảm từ 4,4% năm 2019 xuống c&ograve;n -2,7% trong năm 2020, trước khi phục hồi l&ecirc;n mức 5,2% v&agrave;o năm 2021, đặc biệt do c&aacute;c mối quan hệ thương mại v&agrave; đầu tư mạnh mẽ của khu vực n&agrave;y với Trung Quốc.</p>\r\n\r\n<p>Quan ngại s&acirc;u sắc về những tổn thất to lớn do dịch bệnh Covid-19 g&acirc;y ra ở c&aacute;c quốc gia ASEAN, trong tư c&aacute;ch chủ nh&agrave;, đo&agrave;n Việt Nam mong muốn cộng đồng ASEAN thống nhất thực hiện một chuỗi giải ph&aacute;p mang t&iacute;nh đột ph&aacute;, khả thi.</p>\r\n\r\n<p>Một l&agrave;, thực hiện th&uacute;c đẩy nhanh việc trao đổi th&ocirc;ng tin li&ecirc;n quan đến du lịch v&agrave; sức khỏe v&agrave; c&aacute;c biện ph&aacute;p cần thiết kh&aacute;c để kiểm so&aacute;t sự l&acirc;y lan của đại dịch Covid-19; củng cố sự an t&acirc;m trong di chuyển, &aacute;p dụng c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; quy tr&igrave;nh y tế hỗ trợ di chuyển xuy&ecirc;n bi&ecirc;n giới của c&ocirc;ng d&acirc;n ASEAN kh&ocirc;ng bị gi&aacute;n đoạn, đồng thời tu&acirc;n thủ c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; y tế quốc tế cũng như c&aacute;c cam kết theo c&aacute;c Hiệp định c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-2-1599635215859.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Ph&oacute; Chủ tịch Quốc hội Việt Nam Ph&ugrave;ng Quốc Hiển tham gia phi&ecirc;n thảo luận.</p>\r\n\r\n<p>Hai l&agrave;, nghi&ecirc;n cứu kh&aacute;i niệm &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; như một s&aacute;ng kiến sơ bộ trong qu&aacute; tr&igrave;nh mở cửa lại bi&ecirc;n giới, h&igrave;nh th&agrave;nh c&aacute;c khu vực di chuyển an to&agrave;n trong ASEAN v&agrave; đề xuất hướng dẫn tham chiếu cho tất cả c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN m&agrave; kh&ocirc;ng l&agrave;m tổn hại đến những cam kết theo c&aacute;c thỏa thuận c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p>Ba l&agrave;, tiếp tục r&agrave; so&aacute;t v&agrave; ho&agrave;n thiện khu&ocirc;n khổ ph&aacute;p l&yacute; nhằm th&uacute;c đẩy thuận lợi h&oacute;a thương mại, bảo đảm lưu th&ocirc;ng h&agrave;ng h&oacute;a v&agrave; c&aacute;c chuỗi cung ứng trong khu vực kh&ocirc;ng bị gi&aacute;n đoạn; x&acirc;y dựng ch&iacute;nh s&aacute;ch đầu tư th&ocirc;ng tho&aacute;ng, bền vững, c&oacute; tr&aacute;ch nhiệm trong khu vực; bảo đảm an ninh lương thực v&agrave; chuỗi gi&aacute; trị n&ocirc;ng nghiệp; tăng cường sử dụng năng lượng t&aacute;i tạo; tiến tới x&acirc;y dựng kinh tế tuần ho&agrave;n.</p>\r\n\r\n<p>Bốn l&agrave;, th&uacute;c đẩy nhanh việc ph&ecirc; chuẩn c&aacute;c hiệp định, thỏa thuận thương mại trong khu vực; ưu ti&ecirc;n tăng cường c&aacute;c nỗ lực để ho&agrave;n tất đ&agrave;m ph&aacute;n v&agrave; k&yacute; kết Hiệp định Đối t&aacute;c Kinh tế To&agrave;n diện Khu vực (RCEP) trong năm 2020; tăng cường năng lực, sự tham gia v&agrave; đẩy mạnh gi&aacute;m s&aacute;t nghị viện đối với việc thực hiện c&aacute;c cam kết về đầu tư v&agrave; thương mại trong khu&ocirc;n khổ khu vực v&agrave; quốc tế, coi đ&acirc;y l&agrave; c&ocirc;ng cụ chủ yếu để th&uacute;c đẩy li&ecirc;n kết kinh tế s&acirc;u rộng hơn trong Cộng đồng ASEAN v&agrave; giữa ASEAN với c&aacute;c đối t&aacute;c kh&aacute;c.</p>\r\n\r\n<p><strong>Khai th&aacute;c t&agrave;i nguy&ecirc;n xuy&ecirc;n bi&ecirc;n giới để đảm bảo an ninh lương thực&nbsp;</strong></p>\r\n\r\n<p>Năm l&agrave;, tăng cường ph&aacute;t triển cơ sở hạ tầng số, kết nối số, an to&agrave;n dữ liệu số, kiến thức v&agrave; kỹ năng số gắn kết giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN để ph&aacute;t triển kinh tế số; chia sẻ th&ocirc;ng tin v&agrave; kinh nghiệm để xử l&yacute; c&aacute;c th&aacute;ch thức m&agrave; c&aacute;c nền tảng thương mại điện tử đang phải đối mặt; tận dụng c&aacute;c cơ hội của C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư nhằm khắc phục bất lợi từ việc đ&oacute;ng cửa nền kinh tế v&agrave; gi&atilde;n c&aacute;ch x&atilde; hội trong dịch bệnh Covid-19 v&agrave; duy tr&igrave; t&iacute;nh cạnh tranh trong kỷ nguy&ecirc;n kinh tế số.</p>\r\n\r\n<p>S&aacute;u l&agrave;, x&acirc;y dựng cơ sở hạ tầng kết nối khu vực, tăng cường kết nối giao th&ocirc;ng v&agrave; sự cần thiết của việc tăng cường hợp t&aacute;c tiểu v&ugrave;ng M&ecirc; C&ocirc;ng v&agrave; c&aacute;c tiểu v&ugrave;ng kh&aacute;c của ASEAN, đặc biệt l&agrave; về bảo vệ m&ocirc;i trường, ph&aacute;t triển n&ocirc;ng nghiệp th&ocirc;ng minh v&agrave; quản l&yacute; bền vững c&aacute;c nguồn t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n xuy&ecirc;n bi&ecirc;n giới nhằm đảm bảo an ninh lương thực, nguồn nước v&agrave; năng lượng tại c&aacute;c tiểu v&ugrave;ng trong v&agrave; sau đại dịch Covid-19.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-5-1599635216200.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n thảo luận diễn ra đồng thời tại điểm cầu của c&aacute;c nước trong cộng đồng ASEAN.</p>\r\n\r\n<p>Ph&aacute;t biểu tại phi&ecirc;n họp, đại diện đo&agrave;n Malaysia cho rằng, c&aacute;c nước trong ASEAN c&oacute; nhiều điểm chung n&ecirc;n ch&iacute;nh s&aacute;ch của bất cứ quốc gia n&agrave;o cũng c&oacute; thể được c&aacute;c quốc gia kh&aacute;c học tập, để c&oacute; thể tr&aacute;nh gặp sai lầm giống nhau.</p>\r\n\r\n<p>Theo nghị sỹ Malaysia, thời điểm n&agrave;y rất cần một c&aacute;ch tiếp cận chung theo hướng địa phương ho&aacute; c&aacute;c hoạt động kinh tế. Hầu hết hoạt động kinh tế đ&atilde; được t&iacute;ch hợp v&agrave;o chuối cũng ứng to&agrave;n cầu nhưng Covid- 19 đ&atilde; l&agrave;m gi&aacute;n đoạn chuỗi cung ứng n&agrave;y.</p>\r\n\r\n<p>B&agrave;i học l&agrave; cần phải giảm thiểu sự to&agrave;n cầu ho&aacute;. Nhiều doanh nghiệp đang địa phương ho&aacute; chuỗi sản xuất của m&igrave;n, ASEAN c&oacute; thể địa phương ho&aacute;, mở rộng sang khu vực ho&aacute;, xử l&yacute; chuỗi cung ứng khu vực để miễn dịch khủng hoảng trong tương lai, đại diện đo&agrave;n nghị sĩ Malaysia n&ecirc;u quan điểm.</p>\r\n\r\n<p>Chia sẻ kinh nghiệm ứng ph&oacute; với đại dịch Covid-19, đại diện Singapore cho biết nước n&agrave;y mở ra 4 loại ng&acirc;n s&aacute;ch, gồm quỹ ki&ecirc;n cường, quỹ chống chịu, quỹ đo&agrave;n kết v&agrave; quỹ thống&nbsp; nhất, bốn quỹ n&agrave;y được Ch&iacute;nh phủ hỗ trợ. B&ecirc;n cạnh đ&oacute; Singapore c&ograve;n c&oacute; ch&iacute;nh s&aacute;ch hỗ trợ để doanh nghiệp giữ việc l&agrave;m cho người lao động, ch&iacute;nh s&aacute;ch nay được gia hạn đến th&aacute;ng 3/2011.</p>\r\n\r\n<p><strong>Th&aacute;i Anh</strong></p>\r\n', 0, '1619061146_mat-na-jmsolution-sap-ong.jpg', 22000, 5, 12),
(9, 'Mặt Nạ JMsolution Tinh Chất Hoa Hồng', '<p>Khắc phục hậu quả của đại dịch Covid-19, một s&aacute;ng kiến được đưa ra giữa c&aacute;c nước ASEAN l&agrave; tổ chức &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; để t&aacute;i mở cửa trong khu vực&hellip;</p>\r\n', '<p>S&aacute;ng 9/9, trong khu&ocirc;n khổ chương tr&igrave;nh Đại hội đồng Li&ecirc;n nghị viện ASEAN (AIPA) 41, UB Kinh tế AIPA c&oacute; phi&ecirc;n thảo luận về vai tr&ograve; của Nghị viện trong việc th&uacute;c đẩy gắn kết v&agrave; phục hồi kinh tế ASEAN sau đại dịch Covid-19.</p>\r\n\r\n<p>L&agrave; Trưởng đo&agrave;n Việt Nam tham gia phi&ecirc;n thảo luận, Ph&oacute; Chủ tịch Quốc hội Ph&ugrave;ng Quốc Hiển nhận định, đ&acirc;y l&agrave; chủ đề mang t&iacute;nh thiết thực, c&oacute; t&iacute;nh thời sự cấp b&aacute;ch đối với mọi quốc gia th&agrave;nh vi&ecirc;n AIPA hiện nay. Trong bối cảnh khu vực v&agrave; quốc tế bị ảnh hưởng nặng nề bởi dịch bệnh Covid-19 như hiện nay, Ph&oacute; Chủ tịch Quốc hội b&agrave;y tỏ mong muốn c&aacute;c nước th&agrave;nh vi&ecirc;n sớm vượt qua kh&oacute; khăn, phục hồi nền kinh tế, ổn định đời sống x&atilde; hội v&agrave; &ldquo;kh&ocirc;ng để ai bị bỏ lại ph&iacute;a sau&rdquo;.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-3-1599635216487.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n họp của UB Kinh tế AIPA 41 diễn ra cuối buổi s&aacute;ng ng&agrave;y 9/9/2020.</p>\r\n\r\n<p><strong>Mở cửa lại bi&ecirc;n giới, di chuyển an to&agrave;n trong ASEAN</strong></p>\r\n\r\n<p>Tham gia thảo luận, đại diện của đo&agrave;n Việt Nam n&ecirc;u r&otilde;, đại dịch Covid-19 kh&ocirc;ng chỉ l&agrave; một cuộc khủng hoảng y tế đơn thuần, m&agrave; c&ograve;n l&agrave; một cuộc khủng hoảng đối với sự ph&aacute;t triển khi c&aacute;c chuỗi cung ứng v&agrave; thương mại quốc tế bị ph&aacute; vỡ.</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của Ng&acirc;n h&agrave;ng Ph&aacute;t triển ch&acirc;u &Aacute; (ADB), dự b&aacute;o tăng trưởng tổng sản phẩm quốc nội (GDP) của khu vực Đ&ocirc;ng Nam &Aacute; sẽ giảm từ 4,4% năm 2019 xuống c&ograve;n -2,7% trong năm 2020, trước khi phục hồi l&ecirc;n mức 5,2% v&agrave;o năm 2021, đặc biệt do c&aacute;c mối quan hệ thương mại v&agrave; đầu tư mạnh mẽ của khu vực n&agrave;y với Trung Quốc.</p>\r\n\r\n<p>Quan ngại s&acirc;u sắc về những tổn thất to lớn do dịch bệnh Covid-19 g&acirc;y ra ở c&aacute;c quốc gia ASEAN, trong tư c&aacute;ch chủ nh&agrave;, đo&agrave;n Việt Nam mong muốn cộng đồng ASEAN thống nhất thực hiện một chuỗi giải ph&aacute;p mang t&iacute;nh đột ph&aacute;, khả thi.</p>\r\n\r\n<p>Một l&agrave;, thực hiện th&uacute;c đẩy nhanh việc trao đổi th&ocirc;ng tin li&ecirc;n quan đến du lịch v&agrave; sức khỏe v&agrave; c&aacute;c biện ph&aacute;p cần thiết kh&aacute;c để kiểm so&aacute;t sự l&acirc;y lan của đại dịch Covid-19; củng cố sự an t&acirc;m trong di chuyển, &aacute;p dụng c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; quy tr&igrave;nh y tế hỗ trợ di chuyển xuy&ecirc;n bi&ecirc;n giới của c&ocirc;ng d&acirc;n ASEAN kh&ocirc;ng bị gi&aacute;n đoạn, đồng thời tu&acirc;n thủ c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; y tế quốc tế cũng như c&aacute;c cam kết theo c&aacute;c Hiệp định c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-2-1599635215859.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Ph&oacute; Chủ tịch Quốc hội Việt Nam Ph&ugrave;ng Quốc Hiển tham gia phi&ecirc;n thảo luận.</p>\r\n\r\n<p>Hai l&agrave;, nghi&ecirc;n cứu kh&aacute;i niệm &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; như một s&aacute;ng kiến sơ bộ trong qu&aacute; tr&igrave;nh mở cửa lại bi&ecirc;n giới, h&igrave;nh th&agrave;nh c&aacute;c khu vực di chuyển an to&agrave;n trong ASEAN v&agrave; đề xuất hướng dẫn tham chiếu cho tất cả c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN m&agrave; kh&ocirc;ng l&agrave;m tổn hại đến những cam kết theo c&aacute;c thỏa thuận c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p>Ba l&agrave;, tiếp tục r&agrave; so&aacute;t v&agrave; ho&agrave;n thiện khu&ocirc;n khổ ph&aacute;p l&yacute; nhằm th&uacute;c đẩy thuận lợi h&oacute;a thương mại, bảo đảm lưu th&ocirc;ng h&agrave;ng h&oacute;a v&agrave; c&aacute;c chuỗi cung ứng trong khu vực kh&ocirc;ng bị gi&aacute;n đoạn; x&acirc;y dựng ch&iacute;nh s&aacute;ch đầu tư th&ocirc;ng tho&aacute;ng, bền vững, c&oacute; tr&aacute;ch nhiệm trong khu vực; bảo đảm an ninh lương thực v&agrave; chuỗi gi&aacute; trị n&ocirc;ng nghiệp; tăng cường sử dụng năng lượng t&aacute;i tạo; tiến tới x&acirc;y dựng kinh tế tuần ho&agrave;n.</p>\r\n\r\n<p>Bốn l&agrave;, th&uacute;c đẩy nhanh việc ph&ecirc; chuẩn c&aacute;c hiệp định, thỏa thuận thương mại trong khu vực; ưu ti&ecirc;n tăng cường c&aacute;c nỗ lực để ho&agrave;n tất đ&agrave;m ph&aacute;n v&agrave; k&yacute; kết Hiệp định Đối t&aacute;c Kinh tế To&agrave;n diện Khu vực (RCEP) trong năm 2020; tăng cường năng lực, sự tham gia v&agrave; đẩy mạnh gi&aacute;m s&aacute;t nghị viện đối với việc thực hiện c&aacute;c cam kết về đầu tư v&agrave; thương mại trong khu&ocirc;n khổ khu vực v&agrave; quốc tế, coi đ&acirc;y l&agrave; c&ocirc;ng cụ chủ yếu để th&uacute;c đẩy li&ecirc;n kết kinh tế s&acirc;u rộng hơn trong Cộng đồng ASEAN v&agrave; giữa ASEAN với c&aacute;c đối t&aacute;c kh&aacute;c.</p>\r\n\r\n<p><strong>Khai th&aacute;c t&agrave;i nguy&ecirc;n xuy&ecirc;n bi&ecirc;n giới để đảm bảo an ninh lương thực&nbsp;</strong></p>\r\n\r\n<p>Năm l&agrave;, tăng cường ph&aacute;t triển cơ sở hạ tầng số, kết nối số, an to&agrave;n dữ liệu số, kiến thức v&agrave; kỹ năng số gắn kết giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN để ph&aacute;t triển kinh tế số; chia sẻ th&ocirc;ng tin v&agrave; kinh nghiệm để xử l&yacute; c&aacute;c th&aacute;ch thức m&agrave; c&aacute;c nền tảng thương mại điện tử đang phải đối mặt; tận dụng c&aacute;c cơ hội của C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư nhằm khắc phục bất lợi từ việc đ&oacute;ng cửa nền kinh tế v&agrave; gi&atilde;n c&aacute;ch x&atilde; hội trong dịch bệnh Covid-19 v&agrave; duy tr&igrave; t&iacute;nh cạnh tranh trong kỷ nguy&ecirc;n kinh tế số.</p>\r\n\r\n<p>S&aacute;u l&agrave;, x&acirc;y dựng cơ sở hạ tầng kết nối khu vực, tăng cường kết nối giao th&ocirc;ng v&agrave; sự cần thiết của việc tăng cường hợp t&aacute;c tiểu v&ugrave;ng M&ecirc; C&ocirc;ng v&agrave; c&aacute;c tiểu v&ugrave;ng kh&aacute;c của ASEAN, đặc biệt l&agrave; về bảo vệ m&ocirc;i trường, ph&aacute;t triển n&ocirc;ng nghiệp th&ocirc;ng minh v&agrave; quản l&yacute; bền vững c&aacute;c nguồn t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n xuy&ecirc;n bi&ecirc;n giới nhằm đảm bảo an ninh lương thực, nguồn nước v&agrave; năng lượng tại c&aacute;c tiểu v&ugrave;ng trong v&agrave; sau đại dịch Covid-19.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-5-1599635216200.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n thảo luận diễn ra đồng thời tại điểm cầu của c&aacute;c nước trong cộng đồng ASEAN.</p>\r\n\r\n<p>Ph&aacute;t biểu tại phi&ecirc;n họp, đại diện đo&agrave;n Malaysia cho rằng, c&aacute;c nước trong ASEAN c&oacute; nhiều điểm chung n&ecirc;n ch&iacute;nh s&aacute;ch của bất cứ quốc gia n&agrave;o cũng c&oacute; thể được c&aacute;c quốc gia kh&aacute;c học tập, để c&oacute; thể tr&aacute;nh gặp sai lầm giống nhau.</p>\r\n\r\n<p>Theo nghị sỹ Malaysia, thời điểm n&agrave;y rất cần một c&aacute;ch tiếp cận chung theo hướng địa phương ho&aacute; c&aacute;c hoạt động kinh tế. Hầu hết hoạt động kinh tế đ&atilde; được t&iacute;ch hợp v&agrave;o chuối cũng ứng to&agrave;n cầu nhưng Covid- 19 đ&atilde; l&agrave;m gi&aacute;n đoạn chuỗi cung ứng n&agrave;y.</p>\r\n\r\n<p>B&agrave;i học l&agrave; cần phải giảm thiểu sự to&agrave;n cầu ho&aacute;. Nhiều doanh nghiệp đang địa phương ho&aacute; chuỗi sản xuất của m&igrave;n, ASEAN c&oacute; thể địa phương ho&aacute;, mở rộng sang khu vực ho&aacute;, xử l&yacute; chuỗi cung ứng khu vực để miễn dịch khủng hoảng trong tương lai, đại diện đo&agrave;n nghị sĩ Malaysia n&ecirc;u quan điểm.</p>\r\n\r\n<p>Chia sẻ kinh nghiệm ứng ph&oacute; với đại dịch Covid-19, đại diện Singapore cho biết nước n&agrave;y mở ra 4 loại ng&acirc;n s&aacute;ch, gồm quỹ ki&ecirc;n cường, quỹ chống chịu, quỹ đo&agrave;n kết v&agrave; quỹ thống&nbsp; nhất, bốn quỹ n&agrave;y được Ch&iacute;nh phủ hỗ trợ. B&ecirc;n cạnh đ&oacute; Singapore c&ograve;n c&oacute; ch&iacute;nh s&aacute;ch hỗ trợ để doanh nghiệp giữ việc l&agrave;m cho người lao động, ch&iacute;nh s&aacute;ch nay được gia hạn đến th&aacute;ng 3/2011.</p>\r\n\r\n<p><strong>Th&aacute;i Anh</strong></p>\r\n', 0, '1619061008_mat-na-jmsolution-tinh-chat-hoa-hong.jpg', 22000, 5, 12),
(10, 'Mặt Nạ JMSolution Dưỡng Ẩm Chiết Xuất Kén Tằm Bạc', '<p>Khắc phục hậu quả của đại dịch Covid-19, một s&aacute;ng kiến được đưa ra giữa c&aacute;c nước ASEAN l&agrave; tổ chức &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; để t&aacute;i mở cửa trong khu vực&hellip;</p>\r\n', '<p>S&aacute;ng 9/9, trong khu&ocirc;n khổ chương tr&igrave;nh Đại hội đồng Li&ecirc;n nghị viện ASEAN (AIPA) 41, UB Kinh tế AIPA c&oacute; phi&ecirc;n thảo luận về vai tr&ograve; của Nghị viện trong việc th&uacute;c đẩy gắn kết v&agrave; phục hồi kinh tế ASEAN sau đại dịch Covid-19.</p>\r\n\r\n<p>L&agrave; Trưởng đo&agrave;n Việt Nam tham gia phi&ecirc;n thảo luận, Ph&oacute; Chủ tịch Quốc hội Ph&ugrave;ng Quốc Hiển nhận định, đ&acirc;y l&agrave; chủ đề mang t&iacute;nh thiết thực, c&oacute; t&iacute;nh thời sự cấp b&aacute;ch đối với mọi quốc gia th&agrave;nh vi&ecirc;n AIPA hiện nay. Trong bối cảnh khu vực v&agrave; quốc tế bị ảnh hưởng nặng nề bởi dịch bệnh Covid-19 như hiện nay, Ph&oacute; Chủ tịch Quốc hội b&agrave;y tỏ mong muốn c&aacute;c nước th&agrave;nh vi&ecirc;n sớm vượt qua kh&oacute; khăn, phục hồi nền kinh tế, ổn định đời sống x&atilde; hội v&agrave; &ldquo;kh&ocirc;ng để ai bị bỏ lại ph&iacute;a sau&rdquo;.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-3-1599635216487.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n họp của UB Kinh tế AIPA 41 diễn ra cuối buổi s&aacute;ng ng&agrave;y 9/9/2020.</p>\r\n\r\n<p><strong>Mở cửa lại bi&ecirc;n giới, di chuyển an to&agrave;n trong ASEAN</strong></p>\r\n\r\n<p>Tham gia thảo luận, đại diện của đo&agrave;n Việt Nam n&ecirc;u r&otilde;, đại dịch Covid-19 kh&ocirc;ng chỉ l&agrave; một cuộc khủng hoảng y tế đơn thuần, m&agrave; c&ograve;n l&agrave; một cuộc khủng hoảng đối với sự ph&aacute;t triển khi c&aacute;c chuỗi cung ứng v&agrave; thương mại quốc tế bị ph&aacute; vỡ.</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của Ng&acirc;n h&agrave;ng Ph&aacute;t triển ch&acirc;u &Aacute; (ADB), dự b&aacute;o tăng trưởng tổng sản phẩm quốc nội (GDP) của khu vực Đ&ocirc;ng Nam &Aacute; sẽ giảm từ 4,4% năm 2019 xuống c&ograve;n -2,7% trong năm 2020, trước khi phục hồi l&ecirc;n mức 5,2% v&agrave;o năm 2021, đặc biệt do c&aacute;c mối quan hệ thương mại v&agrave; đầu tư mạnh mẽ của khu vực n&agrave;y với Trung Quốc.</p>\r\n\r\n<p>Quan ngại s&acirc;u sắc về những tổn thất to lớn do dịch bệnh Covid-19 g&acirc;y ra ở c&aacute;c quốc gia ASEAN, trong tư c&aacute;ch chủ nh&agrave;, đo&agrave;n Việt Nam mong muốn cộng đồng ASEAN thống nhất thực hiện một chuỗi giải ph&aacute;p mang t&iacute;nh đột ph&aacute;, khả thi.</p>\r\n\r\n<p>Một l&agrave;, thực hiện th&uacute;c đẩy nhanh việc trao đổi th&ocirc;ng tin li&ecirc;n quan đến du lịch v&agrave; sức khỏe v&agrave; c&aacute;c biện ph&aacute;p cần thiết kh&aacute;c để kiểm so&aacute;t sự l&acirc;y lan của đại dịch Covid-19; củng cố sự an t&acirc;m trong di chuyển, &aacute;p dụng c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; quy tr&igrave;nh y tế hỗ trợ di chuyển xuy&ecirc;n bi&ecirc;n giới của c&ocirc;ng d&acirc;n ASEAN kh&ocirc;ng bị gi&aacute;n đoạn, đồng thời tu&acirc;n thủ c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; y tế quốc tế cũng như c&aacute;c cam kết theo c&aacute;c Hiệp định c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-2-1599635215859.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Ph&oacute; Chủ tịch Quốc hội Việt Nam Ph&ugrave;ng Quốc Hiển tham gia phi&ecirc;n thảo luận.</p>\r\n\r\n<p>Hai l&agrave;, nghi&ecirc;n cứu kh&aacute;i niệm &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; như một s&aacute;ng kiến sơ bộ trong qu&aacute; tr&igrave;nh mở cửa lại bi&ecirc;n giới, h&igrave;nh th&agrave;nh c&aacute;c khu vực di chuyển an to&agrave;n trong ASEAN v&agrave; đề xuất hướng dẫn tham chiếu cho tất cả c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN m&agrave; kh&ocirc;ng l&agrave;m tổn hại đến những cam kết theo c&aacute;c thỏa thuận c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p>Ba l&agrave;, tiếp tục r&agrave; so&aacute;t v&agrave; ho&agrave;n thiện khu&ocirc;n khổ ph&aacute;p l&yacute; nhằm th&uacute;c đẩy thuận lợi h&oacute;a thương mại, bảo đảm lưu th&ocirc;ng h&agrave;ng h&oacute;a v&agrave; c&aacute;c chuỗi cung ứng trong khu vực kh&ocirc;ng bị gi&aacute;n đoạn; x&acirc;y dựng ch&iacute;nh s&aacute;ch đầu tư th&ocirc;ng tho&aacute;ng, bền vững, c&oacute; tr&aacute;ch nhiệm trong khu vực; bảo đảm an ninh lương thực v&agrave; chuỗi gi&aacute; trị n&ocirc;ng nghiệp; tăng cường sử dụng năng lượng t&aacute;i tạo; tiến tới x&acirc;y dựng kinh tế tuần ho&agrave;n.</p>\r\n\r\n<p>Bốn l&agrave;, th&uacute;c đẩy nhanh việc ph&ecirc; chuẩn c&aacute;c hiệp định, thỏa thuận thương mại trong khu vực; ưu ti&ecirc;n tăng cường c&aacute;c nỗ lực để ho&agrave;n tất đ&agrave;m ph&aacute;n v&agrave; k&yacute; kết Hiệp định Đối t&aacute;c Kinh tế To&agrave;n diện Khu vực (RCEP) trong năm 2020; tăng cường năng lực, sự tham gia v&agrave; đẩy mạnh gi&aacute;m s&aacute;t nghị viện đối với việc thực hiện c&aacute;c cam kết về đầu tư v&agrave; thương mại trong khu&ocirc;n khổ khu vực v&agrave; quốc tế, coi đ&acirc;y l&agrave; c&ocirc;ng cụ chủ yếu để th&uacute;c đẩy li&ecirc;n kết kinh tế s&acirc;u rộng hơn trong Cộng đồng ASEAN v&agrave; giữa ASEAN với c&aacute;c đối t&aacute;c kh&aacute;c.</p>\r\n\r\n<p><strong>Khai th&aacute;c t&agrave;i nguy&ecirc;n xuy&ecirc;n bi&ecirc;n giới để đảm bảo an ninh lương thực&nbsp;</strong></p>\r\n\r\n<p>Năm l&agrave;, tăng cường ph&aacute;t triển cơ sở hạ tầng số, kết nối số, an to&agrave;n dữ liệu số, kiến thức v&agrave; kỹ năng số gắn kết giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN để ph&aacute;t triển kinh tế số; chia sẻ th&ocirc;ng tin v&agrave; kinh nghiệm để xử l&yacute; c&aacute;c th&aacute;ch thức m&agrave; c&aacute;c nền tảng thương mại điện tử đang phải đối mặt; tận dụng c&aacute;c cơ hội của C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư nhằm khắc phục bất lợi từ việc đ&oacute;ng cửa nền kinh tế v&agrave; gi&atilde;n c&aacute;ch x&atilde; hội trong dịch bệnh Covid-19 v&agrave; duy tr&igrave; t&iacute;nh cạnh tranh trong kỷ nguy&ecirc;n kinh tế số.</p>\r\n\r\n<p>S&aacute;u l&agrave;, x&acirc;y dựng cơ sở hạ tầng kết nối khu vực, tăng cường kết nối giao th&ocirc;ng v&agrave; sự cần thiết của việc tăng cường hợp t&aacute;c tiểu v&ugrave;ng M&ecirc; C&ocirc;ng v&agrave; c&aacute;c tiểu v&ugrave;ng kh&aacute;c của ASEAN, đặc biệt l&agrave; về bảo vệ m&ocirc;i trường, ph&aacute;t triển n&ocirc;ng nghiệp th&ocirc;ng minh v&agrave; quản l&yacute; bền vững c&aacute;c nguồn t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n xuy&ecirc;n bi&ecirc;n giới nhằm đảm bảo an ninh lương thực, nguồn nước v&agrave; năng lượng tại c&aacute;c tiểu v&ugrave;ng trong v&agrave; sau đại dịch Covid-19.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-5-1599635216200.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n thảo luận diễn ra đồng thời tại điểm cầu của c&aacute;c nước trong cộng đồng ASEAN.</p>\r\n\r\n<p>Ph&aacute;t biểu tại phi&ecirc;n họp, đại diện đo&agrave;n Malaysia cho rằng, c&aacute;c nước trong ASEAN c&oacute; nhiều điểm chung n&ecirc;n ch&iacute;nh s&aacute;ch của bất cứ quốc gia n&agrave;o cũng c&oacute; thể được c&aacute;c quốc gia kh&aacute;c học tập, để c&oacute; thể tr&aacute;nh gặp sai lầm giống nhau.</p>\r\n\r\n<p>Theo nghị sỹ Malaysia, thời điểm n&agrave;y rất cần một c&aacute;ch tiếp cận chung theo hướng địa phương ho&aacute; c&aacute;c hoạt động kinh tế. Hầu hết hoạt động kinh tế đ&atilde; được t&iacute;ch hợp v&agrave;o chuối cũng ứng to&agrave;n cầu nhưng Covid- 19 đ&atilde; l&agrave;m gi&aacute;n đoạn chuỗi cung ứng n&agrave;y.</p>\r\n\r\n<p>B&agrave;i học l&agrave; cần phải giảm thiểu sự to&agrave;n cầu ho&aacute;. Nhiều doanh nghiệp đang địa phương ho&aacute; chuỗi sản xuất của m&igrave;n, ASEAN c&oacute; thể địa phương ho&aacute;, mở rộng sang khu vực ho&aacute;, xử l&yacute; chuỗi cung ứng khu vực để miễn dịch khủng hoảng trong tương lai, đại diện đo&agrave;n nghị sĩ Malaysia n&ecirc;u quan điểm.</p>\r\n\r\n<p>Chia sẻ kinh nghiệm ứng ph&oacute; với đại dịch Covid-19, đại diện Singapore cho biết nước n&agrave;y mở ra 4 loại ng&acirc;n s&aacute;ch, gồm quỹ ki&ecirc;n cường, quỹ chống chịu, quỹ đo&agrave;n kết v&agrave; quỹ thống&nbsp; nhất, bốn quỹ n&agrave;y được Ch&iacute;nh phủ hỗ trợ. B&ecirc;n cạnh đ&oacute; Singapore c&ograve;n c&oacute; ch&iacute;nh s&aacute;ch hỗ trợ để doanh nghiệp giữ việc l&agrave;m cho người lao động, ch&iacute;nh s&aacute;ch nay được gia hạn đến th&aacute;ng 3/2011.</p>\r\n\r\n<p><strong>Th&aacute;i Anh</strong></p>\r\n', 0, '1619060979_mat-na-jmsolution-duong-am-chiet-xuat-ken-tam-bac_1.jpg', 22000, 10, 12),
(11, 'Mặt Nạ JMsolution Dưỡng Ẩm Chuyên Sâu', '<p>Khắc phục hậu quả của đại dịch Covid-19, một s&aacute;ng kiến được đưa ra giữa c&aacute;c nước ASEAN l&agrave; tổ chức &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; để t&aacute;i mở cửa trong khu vực&hellip;</p>\r\n', '<p>S&aacute;ng 9/9, trong khu&ocirc;n khổ chương tr&igrave;nh Đại hội đồng Li&ecirc;n nghị viện ASEAN (AIPA) 41, UB Kinh tế AIPA c&oacute; phi&ecirc;n thảo luận về vai tr&ograve; của Nghị viện trong việc th&uacute;c đẩy gắn kết v&agrave; phục hồi kinh tế ASEAN sau đại dịch Covid-19.</p>\r\n\r\n<p>L&agrave; Trưởng đo&agrave;n Việt Nam tham gia phi&ecirc;n thảo luận, Ph&oacute; Chủ tịch Quốc hội Ph&ugrave;ng Quốc Hiển nhận định, đ&acirc;y l&agrave; chủ đề mang t&iacute;nh thiết thực, c&oacute; t&iacute;nh thời sự cấp b&aacute;ch đối với mọi quốc gia th&agrave;nh vi&ecirc;n AIPA hiện nay. Trong bối cảnh khu vực v&agrave; quốc tế bị ảnh hưởng nặng nề bởi dịch bệnh Covid-19 như hiện nay, Ph&oacute; Chủ tịch Quốc hội b&agrave;y tỏ mong muốn c&aacute;c nước th&agrave;nh vi&ecirc;n sớm vượt qua kh&oacute; khăn, phục hồi nền kinh tế, ổn định đời sống x&atilde; hội v&agrave; &ldquo;kh&ocirc;ng để ai bị bỏ lại ph&iacute;a sau&rdquo;.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-3-1599635216487.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n họp của UB Kinh tế AIPA 41 diễn ra cuối buổi s&aacute;ng ng&agrave;y 9/9/2020.</p>\r\n\r\n<p><strong>Mở cửa lại bi&ecirc;n giới, di chuyển an to&agrave;n trong ASEAN</strong></p>\r\n\r\n<p>Tham gia thảo luận, đại diện của đo&agrave;n Việt Nam n&ecirc;u r&otilde;, đại dịch Covid-19 kh&ocirc;ng chỉ l&agrave; một cuộc khủng hoảng y tế đơn thuần, m&agrave; c&ograve;n l&agrave; một cuộc khủng hoảng đối với sự ph&aacute;t triển khi c&aacute;c chuỗi cung ứng v&agrave; thương mại quốc tế bị ph&aacute; vỡ.</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của Ng&acirc;n h&agrave;ng Ph&aacute;t triển ch&acirc;u &Aacute; (ADB), dự b&aacute;o tăng trưởng tổng sản phẩm quốc nội (GDP) của khu vực Đ&ocirc;ng Nam &Aacute; sẽ giảm từ 4,4% năm 2019 xuống c&ograve;n -2,7% trong năm 2020, trước khi phục hồi l&ecirc;n mức 5,2% v&agrave;o năm 2021, đặc biệt do c&aacute;c mối quan hệ thương mại v&agrave; đầu tư mạnh mẽ của khu vực n&agrave;y với Trung Quốc.</p>\r\n\r\n<p>Quan ngại s&acirc;u sắc về những tổn thất to lớn do dịch bệnh Covid-19 g&acirc;y ra ở c&aacute;c quốc gia ASEAN, trong tư c&aacute;ch chủ nh&agrave;, đo&agrave;n Việt Nam mong muốn cộng đồng ASEAN thống nhất thực hiện một chuỗi giải ph&aacute;p mang t&iacute;nh đột ph&aacute;, khả thi.</p>\r\n\r\n<p>Một l&agrave;, thực hiện th&uacute;c đẩy nhanh việc trao đổi th&ocirc;ng tin li&ecirc;n quan đến du lịch v&agrave; sức khỏe v&agrave; c&aacute;c biện ph&aacute;p cần thiết kh&aacute;c để kiểm so&aacute;t sự l&acirc;y lan của đại dịch Covid-19; củng cố sự an t&acirc;m trong di chuyển, &aacute;p dụng c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; quy tr&igrave;nh y tế hỗ trợ di chuyển xuy&ecirc;n bi&ecirc;n giới của c&ocirc;ng d&acirc;n ASEAN kh&ocirc;ng bị gi&aacute;n đoạn, đồng thời tu&acirc;n thủ c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; y tế quốc tế cũng như c&aacute;c cam kết theo c&aacute;c Hiệp định c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-2-1599635215859.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Ph&oacute; Chủ tịch Quốc hội Việt Nam Ph&ugrave;ng Quốc Hiển tham gia phi&ecirc;n thảo luận.</p>\r\n\r\n<p>Hai l&agrave;, nghi&ecirc;n cứu kh&aacute;i niệm &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; như một s&aacute;ng kiến sơ bộ trong qu&aacute; tr&igrave;nh mở cửa lại bi&ecirc;n giới, h&igrave;nh th&agrave;nh c&aacute;c khu vực di chuyển an to&agrave;n trong ASEAN v&agrave; đề xuất hướng dẫn tham chiếu cho tất cả c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN m&agrave; kh&ocirc;ng l&agrave;m tổn hại đến những cam kết theo c&aacute;c thỏa thuận c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p>Ba l&agrave;, tiếp tục r&agrave; so&aacute;t v&agrave; ho&agrave;n thiện khu&ocirc;n khổ ph&aacute;p l&yacute; nhằm th&uacute;c đẩy thuận lợi h&oacute;a thương mại, bảo đảm lưu th&ocirc;ng h&agrave;ng h&oacute;a v&agrave; c&aacute;c chuỗi cung ứng trong khu vực kh&ocirc;ng bị gi&aacute;n đoạn; x&acirc;y dựng ch&iacute;nh s&aacute;ch đầu tư th&ocirc;ng tho&aacute;ng, bền vững, c&oacute; tr&aacute;ch nhiệm trong khu vực; bảo đảm an ninh lương thực v&agrave; chuỗi gi&aacute; trị n&ocirc;ng nghiệp; tăng cường sử dụng năng lượng t&aacute;i tạo; tiến tới x&acirc;y dựng kinh tế tuần ho&agrave;n.</p>\r\n\r\n<p>Bốn l&agrave;, th&uacute;c đẩy nhanh việc ph&ecirc; chuẩn c&aacute;c hiệp định, thỏa thuận thương mại trong khu vực; ưu ti&ecirc;n tăng cường c&aacute;c nỗ lực để ho&agrave;n tất đ&agrave;m ph&aacute;n v&agrave; k&yacute; kết Hiệp định Đối t&aacute;c Kinh tế To&agrave;n diện Khu vực (RCEP) trong năm 2020; tăng cường năng lực, sự tham gia v&agrave; đẩy mạnh gi&aacute;m s&aacute;t nghị viện đối với việc thực hiện c&aacute;c cam kết về đầu tư v&agrave; thương mại trong khu&ocirc;n khổ khu vực v&agrave; quốc tế, coi đ&acirc;y l&agrave; c&ocirc;ng cụ chủ yếu để th&uacute;c đẩy li&ecirc;n kết kinh tế s&acirc;u rộng hơn trong Cộng đồng ASEAN v&agrave; giữa ASEAN với c&aacute;c đối t&aacute;c kh&aacute;c.</p>\r\n\r\n<p><strong>Khai th&aacute;c t&agrave;i nguy&ecirc;n xuy&ecirc;n bi&ecirc;n giới để đảm bảo an ninh lương thực&nbsp;</strong></p>\r\n\r\n<p>Năm l&agrave;, tăng cường ph&aacute;t triển cơ sở hạ tầng số, kết nối số, an to&agrave;n dữ liệu số, kiến thức v&agrave; kỹ năng số gắn kết giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN để ph&aacute;t triển kinh tế số; chia sẻ th&ocirc;ng tin v&agrave; kinh nghiệm để xử l&yacute; c&aacute;c th&aacute;ch thức m&agrave; c&aacute;c nền tảng thương mại điện tử đang phải đối mặt; tận dụng c&aacute;c cơ hội của C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư nhằm khắc phục bất lợi từ việc đ&oacute;ng cửa nền kinh tế v&agrave; gi&atilde;n c&aacute;ch x&atilde; hội trong dịch bệnh Covid-19 v&agrave; duy tr&igrave; t&iacute;nh cạnh tranh trong kỷ nguy&ecirc;n kinh tế số.</p>\r\n\r\n<p>S&aacute;u l&agrave;, x&acirc;y dựng cơ sở hạ tầng kết nối khu vực, tăng cường kết nối giao th&ocirc;ng v&agrave; sự cần thiết của việc tăng cường hợp t&aacute;c tiểu v&ugrave;ng M&ecirc; C&ocirc;ng v&agrave; c&aacute;c tiểu v&ugrave;ng kh&aacute;c của ASEAN, đặc biệt l&agrave; về bảo vệ m&ocirc;i trường, ph&aacute;t triển n&ocirc;ng nghiệp th&ocirc;ng minh v&agrave; quản l&yacute; bền vững c&aacute;c nguồn t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n xuy&ecirc;n bi&ecirc;n giới nhằm đảm bảo an ninh lương thực, nguồn nước v&agrave; năng lượng tại c&aacute;c tiểu v&ugrave;ng trong v&agrave; sau đại dịch Covid-19.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-5-1599635216200.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n thảo luận diễn ra đồng thời tại điểm cầu của c&aacute;c nước trong cộng đồng ASEAN.</p>\r\n\r\n<p>Ph&aacute;t biểu tại phi&ecirc;n họp, đại diện đo&agrave;n Malaysia cho rằng, c&aacute;c nước trong ASEAN c&oacute; nhiều điểm chung n&ecirc;n ch&iacute;nh s&aacute;ch của bất cứ quốc gia n&agrave;o cũng c&oacute; thể được c&aacute;c quốc gia kh&aacute;c học tập, để c&oacute; thể tr&aacute;nh gặp sai lầm giống nhau.</p>\r\n\r\n<p>Theo nghị sỹ Malaysia, thời điểm n&agrave;y rất cần một c&aacute;ch tiếp cận chung theo hướng địa phương ho&aacute; c&aacute;c hoạt động kinh tế. Hầu hết hoạt động kinh tế đ&atilde; được t&iacute;ch hợp v&agrave;o chuối cũng ứng to&agrave;n cầu nhưng Covid- 19 đ&atilde; l&agrave;m gi&aacute;n đoạn chuỗi cung ứng n&agrave;y.</p>\r\n\r\n<p>B&agrave;i học l&agrave; cần phải giảm thiểu sự to&agrave;n cầu ho&aacute;. Nhiều doanh nghiệp đang địa phương ho&aacute; chuỗi sản xuất của m&igrave;n, ASEAN c&oacute; thể địa phương ho&aacute;, mở rộng sang khu vực ho&aacute;, xử l&yacute; chuỗi cung ứng khu vực để miễn dịch khủng hoảng trong tương lai, đại diện đo&agrave;n nghị sĩ Malaysia n&ecirc;u quan điểm.</p>\r\n\r\n<p>Chia sẻ kinh nghiệm ứng ph&oacute; với đại dịch Covid-19, đại diện Singapore cho biết nước n&agrave;y mở ra 4 loại ng&acirc;n s&aacute;ch, gồm quỹ ki&ecirc;n cường, quỹ chống chịu, quỹ đo&agrave;n kết v&agrave; quỹ thống&nbsp; nhất, bốn quỹ n&agrave;y được Ch&iacute;nh phủ hỗ trợ. B&ecirc;n cạnh đ&oacute; Singapore c&ograve;n c&oacute; ch&iacute;nh s&aacute;ch hỗ trợ để doanh nghiệp giữ việc l&agrave;m cho người lao động, ch&iacute;nh s&aacute;ch nay được gia hạn đến th&aacute;ng 3/2011.</p>\r\n\r\n<p><strong>Th&aacute;i Anh</strong></p>\r\n', 1, '1619060922_mat-na-jmsolution-duong-am-chiet-xuat-bo.jpg', 25000, 5, 12),
(12, 'Xịt thơm body Victoria\'s secret - Spring poppies 250ml', '<p>Khắc phục hậu quả của đại dịch Covid-19, một s&aacute;ng kiến được đưa ra giữa c&aacute;c nước ASEAN l&agrave; tổ chức &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; để t&aacute;i mở cửa trong khu vực&hellip;</p>\r\n', '<p>S&aacute;ng 9/9, trong khu&ocirc;n khổ chương tr&igrave;nh Đại hội đồng Li&ecirc;n nghị viện ASEAN (AIPA) 41, UB Kinh tế AIPA c&oacute; phi&ecirc;n thảo luận về vai tr&ograve; của Nghị viện trong việc th&uacute;c đẩy gắn kết v&agrave; phục hồi kinh tế ASEAN sau đại dịch Covid-19.</p>\r\n\r\n<p>L&agrave; Trưởng đo&agrave;n Việt Nam tham gia phi&ecirc;n thảo luận, Ph&oacute; Chủ tịch Quốc hội Ph&ugrave;ng Quốc Hiển nhận định, đ&acirc;y l&agrave; chủ đề mang t&iacute;nh thiết thực, c&oacute; t&iacute;nh thời sự cấp b&aacute;ch đối với mọi quốc gia th&agrave;nh vi&ecirc;n AIPA hiện nay. Trong bối cảnh khu vực v&agrave; quốc tế bị ảnh hưởng nặng nề bởi dịch bệnh Covid-19 như hiện nay, Ph&oacute; Chủ tịch Quốc hội b&agrave;y tỏ mong muốn c&aacute;c nước th&agrave;nh vi&ecirc;n sớm vượt qua kh&oacute; khăn, phục hồi nền kinh tế, ổn định đời sống x&atilde; hội v&agrave; &ldquo;kh&ocirc;ng để ai bị bỏ lại ph&iacute;a sau&rdquo;.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-3-1599635216487.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n họp của UB Kinh tế AIPA 41 diễn ra cuối buổi s&aacute;ng ng&agrave;y 9/9/2020.</p>\r\n\r\n<p><strong>Mở cửa lại bi&ecirc;n giới, di chuyển an to&agrave;n trong ASEAN</strong></p>\r\n\r\n<p>Tham gia thảo luận, đại diện của đo&agrave;n Việt Nam n&ecirc;u r&otilde;, đại dịch Covid-19 kh&ocirc;ng chỉ l&agrave; một cuộc khủng hoảng y tế đơn thuần, m&agrave; c&ograve;n l&agrave; một cuộc khủng hoảng đối với sự ph&aacute;t triển khi c&aacute;c chuỗi cung ứng v&agrave; thương mại quốc tế bị ph&aacute; vỡ.</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của Ng&acirc;n h&agrave;ng Ph&aacute;t triển ch&acirc;u &Aacute; (ADB), dự b&aacute;o tăng trưởng tổng sản phẩm quốc nội (GDP) của khu vực Đ&ocirc;ng Nam &Aacute; sẽ giảm từ 4,4% năm 2019 xuống c&ograve;n -2,7% trong năm 2020, trước khi phục hồi l&ecirc;n mức 5,2% v&agrave;o năm 2021, đặc biệt do c&aacute;c mối quan hệ thương mại v&agrave; đầu tư mạnh mẽ của khu vực n&agrave;y với Trung Quốc.</p>\r\n\r\n<p>Quan ngại s&acirc;u sắc về những tổn thất to lớn do dịch bệnh Covid-19 g&acirc;y ra ở c&aacute;c quốc gia ASEAN, trong tư c&aacute;ch chủ nh&agrave;, đo&agrave;n Việt Nam mong muốn cộng đồng ASEAN thống nhất thực hiện một chuỗi giải ph&aacute;p mang t&iacute;nh đột ph&aacute;, khả thi.</p>\r\n\r\n<p>Một l&agrave;, thực hiện th&uacute;c đẩy nhanh việc trao đổi th&ocirc;ng tin li&ecirc;n quan đến du lịch v&agrave; sức khỏe v&agrave; c&aacute;c biện ph&aacute;p cần thiết kh&aacute;c để kiểm so&aacute;t sự l&acirc;y lan của đại dịch Covid-19; củng cố sự an t&acirc;m trong di chuyển, &aacute;p dụng c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; quy tr&igrave;nh y tế hỗ trợ di chuyển xuy&ecirc;n bi&ecirc;n giới của c&ocirc;ng d&acirc;n ASEAN kh&ocirc;ng bị gi&aacute;n đoạn, đồng thời tu&acirc;n thủ c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; y tế quốc tế cũng như c&aacute;c cam kết theo c&aacute;c Hiệp định c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-2-1599635215859.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Ph&oacute; Chủ tịch Quốc hội Việt Nam Ph&ugrave;ng Quốc Hiển tham gia phi&ecirc;n thảo luận.</p>\r\n\r\n<p>Hai l&agrave;, nghi&ecirc;n cứu kh&aacute;i niệm &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; như một s&aacute;ng kiến sơ bộ trong qu&aacute; tr&igrave;nh mở cửa lại bi&ecirc;n giới, h&igrave;nh th&agrave;nh c&aacute;c khu vực di chuyển an to&agrave;n trong ASEAN v&agrave; đề xuất hướng dẫn tham chiếu cho tất cả c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN m&agrave; kh&ocirc;ng l&agrave;m tổn hại đến những cam kết theo c&aacute;c thỏa thuận c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p>Ba l&agrave;, tiếp tục r&agrave; so&aacute;t v&agrave; ho&agrave;n thiện khu&ocirc;n khổ ph&aacute;p l&yacute; nhằm th&uacute;c đẩy thuận lợi h&oacute;a thương mại, bảo đảm lưu th&ocirc;ng h&agrave;ng h&oacute;a v&agrave; c&aacute;c chuỗi cung ứng trong khu vực kh&ocirc;ng bị gi&aacute;n đoạn; x&acirc;y dựng ch&iacute;nh s&aacute;ch đầu tư th&ocirc;ng tho&aacute;ng, bền vững, c&oacute; tr&aacute;ch nhiệm trong khu vực; bảo đảm an ninh lương thực v&agrave; chuỗi gi&aacute; trị n&ocirc;ng nghiệp; tăng cường sử dụng năng lượng t&aacute;i tạo; tiến tới x&acirc;y dựng kinh tế tuần ho&agrave;n.</p>\r\n\r\n<p>Bốn l&agrave;, th&uacute;c đẩy nhanh việc ph&ecirc; chuẩn c&aacute;c hiệp định, thỏa thuận thương mại trong khu vực; ưu ti&ecirc;n tăng cường c&aacute;c nỗ lực để ho&agrave;n tất đ&agrave;m ph&aacute;n v&agrave; k&yacute; kết Hiệp định Đối t&aacute;c Kinh tế To&agrave;n diện Khu vực (RCEP) trong năm 2020; tăng cường năng lực, sự tham gia v&agrave; đẩy mạnh gi&aacute;m s&aacute;t nghị viện đối với việc thực hiện c&aacute;c cam kết về đầu tư v&agrave; thương mại trong khu&ocirc;n khổ khu vực v&agrave; quốc tế, coi đ&acirc;y l&agrave; c&ocirc;ng cụ chủ yếu để th&uacute;c đẩy li&ecirc;n kết kinh tế s&acirc;u rộng hơn trong Cộng đồng ASEAN v&agrave; giữa ASEAN với c&aacute;c đối t&aacute;c kh&aacute;c.</p>\r\n\r\n<p><strong>Khai th&aacute;c t&agrave;i nguy&ecirc;n xuy&ecirc;n bi&ecirc;n giới để đảm bảo an ninh lương thực&nbsp;</strong></p>\r\n\r\n<p>Năm l&agrave;, tăng cường ph&aacute;t triển cơ sở hạ tầng số, kết nối số, an to&agrave;n dữ liệu số, kiến thức v&agrave; kỹ năng số gắn kết giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN để ph&aacute;t triển kinh tế số; chia sẻ th&ocirc;ng tin v&agrave; kinh nghiệm để xử l&yacute; c&aacute;c th&aacute;ch thức m&agrave; c&aacute;c nền tảng thương mại điện tử đang phải đối mặt; tận dụng c&aacute;c cơ hội của C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư nhằm khắc phục bất lợi từ việc đ&oacute;ng cửa nền kinh tế v&agrave; gi&atilde;n c&aacute;ch x&atilde; hội trong dịch bệnh Covid-19 v&agrave; duy tr&igrave; t&iacute;nh cạnh tranh trong kỷ nguy&ecirc;n kinh tế số.</p>\r\n\r\n<p>S&aacute;u l&agrave;, x&acirc;y dựng cơ sở hạ tầng kết nối khu vực, tăng cường kết nối giao th&ocirc;ng v&agrave; sự cần thiết của việc tăng cường hợp t&aacute;c tiểu v&ugrave;ng M&ecirc; C&ocirc;ng v&agrave; c&aacute;c tiểu v&ugrave;ng kh&aacute;c của ASEAN, đặc biệt l&agrave; về bảo vệ m&ocirc;i trường, ph&aacute;t triển n&ocirc;ng nghiệp th&ocirc;ng minh v&agrave; quản l&yacute; bền vững c&aacute;c nguồn t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n xuy&ecirc;n bi&ecirc;n giới nhằm đảm bảo an ninh lương thực, nguồn nước v&agrave; năng lượng tại c&aacute;c tiểu v&ugrave;ng trong v&agrave; sau đại dịch Covid-19.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-5-1599635216200.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n thảo luận diễn ra đồng thời tại điểm cầu của c&aacute;c nước trong cộng đồng ASEAN.</p>\r\n\r\n<p>Ph&aacute;t biểu tại phi&ecirc;n họp, đại diện đo&agrave;n Malaysia cho rằng, c&aacute;c nước trong ASEAN c&oacute; nhiều điểm chung n&ecirc;n ch&iacute;nh s&aacute;ch của bất cứ quốc gia n&agrave;o cũng c&oacute; thể được c&aacute;c quốc gia kh&aacute;c học tập, để c&oacute; thể tr&aacute;nh gặp sai lầm giống nhau.</p>\r\n\r\n<p>Theo nghị sỹ Malaysia, thời điểm n&agrave;y rất cần một c&aacute;ch tiếp cận chung theo hướng địa phương ho&aacute; c&aacute;c hoạt động kinh tế. Hầu hết hoạt động kinh tế đ&atilde; được t&iacute;ch hợp v&agrave;o chuối cũng ứng to&agrave;n cầu nhưng Covid- 19 đ&atilde; l&agrave;m gi&aacute;n đoạn chuỗi cung ứng n&agrave;y.</p>\r\n\r\n<p>B&agrave;i học l&agrave; cần phải giảm thiểu sự to&agrave;n cầu ho&aacute;. Nhiều doanh nghiệp đang địa phương ho&aacute; chuỗi sản xuất của m&igrave;n, ASEAN c&oacute; thể địa phương ho&aacute;, mở rộng sang khu vực ho&aacute;, xử l&yacute; chuỗi cung ứng khu vực để miễn dịch khủng hoảng trong tương lai, đại diện đo&agrave;n nghị sĩ Malaysia n&ecirc;u quan điểm.</p>\r\n\r\n<p>Chia sẻ kinh nghiệm ứng ph&oacute; với đại dịch Covid-19, đại diện Singapore cho biết nước n&agrave;y mở ra 4 loại ng&acirc;n s&aacute;ch, gồm quỹ ki&ecirc;n cường, quỹ chống chịu, quỹ đo&agrave;n kết v&agrave; quỹ thống&nbsp; nhất, bốn quỹ n&agrave;y được Ch&iacute;nh phủ hỗ trợ. B&ecirc;n cạnh đ&oacute; Singapore c&ograve;n c&oacute; ch&iacute;nh s&aacute;ch hỗ trợ để doanh nghiệp giữ việc l&agrave;m cho người lao động, ch&iacute;nh s&aacute;ch nay được gia hạn đến th&aacute;ng 3/2011.</p>\r\n\r\n<p><strong>Th&aacute;i Anh</strong></p>\r\n', 0, '1619060896_xit-thom-body-victorias-secret-spring-poppies-250ml-4.jpg', 200000, 5, 18);
INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `photo`, `price`, `discount`, `category_id`) VALUES
(13, 'Xịt thơm body Victoria\'s secret - Spring poppies', '<p>Khắc phục hậu quả của đại dịch Covid-19, một s&aacute;ng kiến được đưa ra giữa c&aacute;c nước ASEAN l&agrave; tổ chức &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; để t&aacute;i mở cửa trong khu vực&hellip;</p>\r\n', '<p>S&aacute;ng 9/9, trong khu&ocirc;n khổ chương tr&igrave;nh Đại hội đồng Li&ecirc;n nghị viện ASEAN (AIPA) 41, UB Kinh tế AIPA c&oacute; phi&ecirc;n thảo luận về vai tr&ograve; của Nghị viện trong việc th&uacute;c đẩy gắn kết v&agrave; phục hồi kinh tế ASEAN sau đại dịch Covid-19.</p>\r\n\r\n<p>L&agrave; Trưởng đo&agrave;n Việt Nam tham gia phi&ecirc;n thảo luận, Ph&oacute; Chủ tịch Quốc hội Ph&ugrave;ng Quốc Hiển nhận định, đ&acirc;y l&agrave; chủ đề mang t&iacute;nh thiết thực, c&oacute; t&iacute;nh thời sự cấp b&aacute;ch đối với mọi quốc gia th&agrave;nh vi&ecirc;n AIPA hiện nay. Trong bối cảnh khu vực v&agrave; quốc tế bị ảnh hưởng nặng nề bởi dịch bệnh Covid-19 như hiện nay, Ph&oacute; Chủ tịch Quốc hội b&agrave;y tỏ mong muốn c&aacute;c nước th&agrave;nh vi&ecirc;n sớm vượt qua kh&oacute; khăn, phục hồi nền kinh tế, ổn định đời sống x&atilde; hội v&agrave; &ldquo;kh&ocirc;ng để ai bị bỏ lại ph&iacute;a sau&rdquo;.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-3-1599635216487.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n họp của UB Kinh tế AIPA 41 diễn ra cuối buổi s&aacute;ng ng&agrave;y 9/9/2020.</p>\r\n\r\n<p><strong>Mở cửa lại bi&ecirc;n giới, di chuyển an to&agrave;n trong ASEAN</strong></p>\r\n\r\n<p>Tham gia thảo luận, đại diện của đo&agrave;n Việt Nam n&ecirc;u r&otilde;, đại dịch Covid-19 kh&ocirc;ng chỉ l&agrave; một cuộc khủng hoảng y tế đơn thuần, m&agrave; c&ograve;n l&agrave; một cuộc khủng hoảng đối với sự ph&aacute;t triển khi c&aacute;c chuỗi cung ứng v&agrave; thương mại quốc tế bị ph&aacute; vỡ.</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của Ng&acirc;n h&agrave;ng Ph&aacute;t triển ch&acirc;u &Aacute; (ADB), dự b&aacute;o tăng trưởng tổng sản phẩm quốc nội (GDP) của khu vực Đ&ocirc;ng Nam &Aacute; sẽ giảm từ 4,4% năm 2019 xuống c&ograve;n -2,7% trong năm 2020, trước khi phục hồi l&ecirc;n mức 5,2% v&agrave;o năm 2021, đặc biệt do c&aacute;c mối quan hệ thương mại v&agrave; đầu tư mạnh mẽ của khu vực n&agrave;y với Trung Quốc.</p>\r\n\r\n<p>Quan ngại s&acirc;u sắc về những tổn thất to lớn do dịch bệnh Covid-19 g&acirc;y ra ở c&aacute;c quốc gia ASEAN, trong tư c&aacute;ch chủ nh&agrave;, đo&agrave;n Việt Nam mong muốn cộng đồng ASEAN thống nhất thực hiện một chuỗi giải ph&aacute;p mang t&iacute;nh đột ph&aacute;, khả thi.</p>\r\n\r\n<p>Một l&agrave;, thực hiện th&uacute;c đẩy nhanh việc trao đổi th&ocirc;ng tin li&ecirc;n quan đến du lịch v&agrave; sức khỏe v&agrave; c&aacute;c biện ph&aacute;p cần thiết kh&aacute;c để kiểm so&aacute;t sự l&acirc;y lan của đại dịch Covid-19; củng cố sự an t&acirc;m trong di chuyển, &aacute;p dụng c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; quy tr&igrave;nh y tế hỗ trợ di chuyển xuy&ecirc;n bi&ecirc;n giới của c&ocirc;ng d&acirc;n ASEAN kh&ocirc;ng bị gi&aacute;n đoạn, đồng thời tu&acirc;n thủ c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; y tế quốc tế cũng như c&aacute;c cam kết theo c&aacute;c Hiệp định c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-2-1599635215859.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Ph&oacute; Chủ tịch Quốc hội Việt Nam Ph&ugrave;ng Quốc Hiển tham gia phi&ecirc;n thảo luận.</p>\r\n\r\n<p>Hai l&agrave;, nghi&ecirc;n cứu kh&aacute;i niệm &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; như một s&aacute;ng kiến sơ bộ trong qu&aacute; tr&igrave;nh mở cửa lại bi&ecirc;n giới, h&igrave;nh th&agrave;nh c&aacute;c khu vực di chuyển an to&agrave;n trong ASEAN v&agrave; đề xuất hướng dẫn tham chiếu cho tất cả c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN m&agrave; kh&ocirc;ng l&agrave;m tổn hại đến những cam kết theo c&aacute;c thỏa thuận c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p>Ba l&agrave;, tiếp tục r&agrave; so&aacute;t v&agrave; ho&agrave;n thiện khu&ocirc;n khổ ph&aacute;p l&yacute; nhằm th&uacute;c đẩy thuận lợi h&oacute;a thương mại, bảo đảm lưu th&ocirc;ng h&agrave;ng h&oacute;a v&agrave; c&aacute;c chuỗi cung ứng trong khu vực kh&ocirc;ng bị gi&aacute;n đoạn; x&acirc;y dựng ch&iacute;nh s&aacute;ch đầu tư th&ocirc;ng tho&aacute;ng, bền vững, c&oacute; tr&aacute;ch nhiệm trong khu vực; bảo đảm an ninh lương thực v&agrave; chuỗi gi&aacute; trị n&ocirc;ng nghiệp; tăng cường sử dụng năng lượng t&aacute;i tạo; tiến tới x&acirc;y dựng kinh tế tuần ho&agrave;n.</p>\r\n\r\n<p>Bốn l&agrave;, th&uacute;c đẩy nhanh việc ph&ecirc; chuẩn c&aacute;c hiệp định, thỏa thuận thương mại trong khu vực; ưu ti&ecirc;n tăng cường c&aacute;c nỗ lực để ho&agrave;n tất đ&agrave;m ph&aacute;n v&agrave; k&yacute; kết Hiệp định Đối t&aacute;c Kinh tế To&agrave;n diện Khu vực (RCEP) trong năm 2020; tăng cường năng lực, sự tham gia v&agrave; đẩy mạnh gi&aacute;m s&aacute;t nghị viện đối với việc thực hiện c&aacute;c cam kết về đầu tư v&agrave; thương mại trong khu&ocirc;n khổ khu vực v&agrave; quốc tế, coi đ&acirc;y l&agrave; c&ocirc;ng cụ chủ yếu để th&uacute;c đẩy li&ecirc;n kết kinh tế s&acirc;u rộng hơn trong Cộng đồng ASEAN v&agrave; giữa ASEAN với c&aacute;c đối t&aacute;c kh&aacute;c.</p>\r\n\r\n<p><strong>Khai th&aacute;c t&agrave;i nguy&ecirc;n xuy&ecirc;n bi&ecirc;n giới để đảm bảo an ninh lương thực&nbsp;</strong></p>\r\n\r\n<p>Năm l&agrave;, tăng cường ph&aacute;t triển cơ sở hạ tầng số, kết nối số, an to&agrave;n dữ liệu số, kiến thức v&agrave; kỹ năng số gắn kết giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN để ph&aacute;t triển kinh tế số; chia sẻ th&ocirc;ng tin v&agrave; kinh nghiệm để xử l&yacute; c&aacute;c th&aacute;ch thức m&agrave; c&aacute;c nền tảng thương mại điện tử đang phải đối mặt; tận dụng c&aacute;c cơ hội của C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư nhằm khắc phục bất lợi từ việc đ&oacute;ng cửa nền kinh tế v&agrave; gi&atilde;n c&aacute;ch x&atilde; hội trong dịch bệnh Covid-19 v&agrave; duy tr&igrave; t&iacute;nh cạnh tranh trong kỷ nguy&ecirc;n kinh tế số.</p>\r\n\r\n<p>S&aacute;u l&agrave;, x&acirc;y dựng cơ sở hạ tầng kết nối khu vực, tăng cường kết nối giao th&ocirc;ng v&agrave; sự cần thiết của việc tăng cường hợp t&aacute;c tiểu v&ugrave;ng M&ecirc; C&ocirc;ng v&agrave; c&aacute;c tiểu v&ugrave;ng kh&aacute;c của ASEAN, đặc biệt l&agrave; về bảo vệ m&ocirc;i trường, ph&aacute;t triển n&ocirc;ng nghiệp th&ocirc;ng minh v&agrave; quản l&yacute; bền vững c&aacute;c nguồn t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n xuy&ecirc;n bi&ecirc;n giới nhằm đảm bảo an ninh lương thực, nguồn nước v&agrave; năng lượng tại c&aacute;c tiểu v&ugrave;ng trong v&agrave; sau đại dịch Covid-19.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-5-1599635216200.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n thảo luận diễn ra đồng thời tại điểm cầu của c&aacute;c nước trong cộng đồng ASEAN.</p>\r\n\r\n<p>Ph&aacute;t biểu tại phi&ecirc;n họp, đại diện đo&agrave;n Malaysia cho rằng, c&aacute;c nước trong ASEAN c&oacute; nhiều điểm chung n&ecirc;n ch&iacute;nh s&aacute;ch của bất cứ quốc gia n&agrave;o cũng c&oacute; thể được c&aacute;c quốc gia kh&aacute;c học tập, để c&oacute; thể tr&aacute;nh gặp sai lầm giống nhau.</p>\r\n\r\n<p>Theo nghị sỹ Malaysia, thời điểm n&agrave;y rất cần một c&aacute;ch tiếp cận chung theo hướng địa phương ho&aacute; c&aacute;c hoạt động kinh tế. Hầu hết hoạt động kinh tế đ&atilde; được t&iacute;ch hợp v&agrave;o chuối cũng ứng to&agrave;n cầu nhưng Covid- 19 đ&atilde; l&agrave;m gi&aacute;n đoạn chuỗi cung ứng n&agrave;y.</p>\r\n\r\n<p>B&agrave;i học l&agrave; cần phải giảm thiểu sự to&agrave;n cầu ho&aacute;. Nhiều doanh nghiệp đang địa phương ho&aacute; chuỗi sản xuất của m&igrave;n, ASEAN c&oacute; thể địa phương ho&aacute;, mở rộng sang khu vực ho&aacute;, xử l&yacute; chuỗi cung ứng khu vực để miễn dịch khủng hoảng trong tương lai, đại diện đo&agrave;n nghị sĩ Malaysia n&ecirc;u quan điểm.</p>\r\n\r\n<p>Chia sẻ kinh nghiệm ứng ph&oacute; với đại dịch Covid-19, đại diện Singapore cho biết nước n&agrave;y mở ra 4 loại ng&acirc;n s&aacute;ch, gồm quỹ ki&ecirc;n cường, quỹ chống chịu, quỹ đo&agrave;n kết v&agrave; quỹ thống&nbsp; nhất, bốn quỹ n&agrave;y được Ch&iacute;nh phủ hỗ trợ. B&ecirc;n cạnh đ&oacute; Singapore c&ograve;n c&oacute; ch&iacute;nh s&aacute;ch hỗ trợ để doanh nghiệp giữ việc l&agrave;m cho người lao động, ch&iacute;nh s&aacute;ch nay được gia hạn đến th&aacute;ng 3/2011.</p>\r\n\r\n<p><strong>Th&aacute;i Anh</strong></p>\r\n', 0, '1619060875_bbw-new-600x600.png', 750000, 5, 18),
(14, 'Nước hoa CHL 5 RG EDP NS 50ml 18 IV', '<p>Khắc phục hậu quả của đại dịch Covid-19, một s&aacute;ng kiến được đưa ra giữa c&aacute;c nước ASEAN l&agrave; tổ chức &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; để t&aacute;i mở cửa trong khu vực&hellip;</p>\r\n', '<p>S&aacute;ng 9/9, trong khu&ocirc;n khổ chương tr&igrave;nh Đại hội đồng Li&ecirc;n nghị viện ASEAN (AIPA) 41, UB Kinh tế AIPA c&oacute; phi&ecirc;n thảo luận về vai tr&ograve; của Nghị viện trong việc th&uacute;c đẩy gắn kết v&agrave; phục hồi kinh tế ASEAN sau đại dịch Covid-19.</p>\r\n\r\n<p>L&agrave; Trưởng đo&agrave;n Việt Nam tham gia phi&ecirc;n thảo luận, Ph&oacute; Chủ tịch Quốc hội Ph&ugrave;ng Quốc Hiển nhận định, đ&acirc;y l&agrave; chủ đề mang t&iacute;nh thiết thực, c&oacute; t&iacute;nh thời sự cấp b&aacute;ch đối với mọi quốc gia th&agrave;nh vi&ecirc;n AIPA hiện nay. Trong bối cảnh khu vực v&agrave; quốc tế bị ảnh hưởng nặng nề bởi dịch bệnh Covid-19 như hiện nay, Ph&oacute; Chủ tịch Quốc hội b&agrave;y tỏ mong muốn c&aacute;c nước th&agrave;nh vi&ecirc;n sớm vượt qua kh&oacute; khăn, phục hồi nền kinh tế, ổn định đời sống x&atilde; hội v&agrave; &ldquo;kh&ocirc;ng để ai bị bỏ lại ph&iacute;a sau&rdquo;.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-3-1599635216487.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n họp của UB Kinh tế AIPA 41 diễn ra cuối buổi s&aacute;ng ng&agrave;y 9/9/2020.</p>\r\n\r\n<p><strong>Mở cửa lại bi&ecirc;n giới, di chuyển an to&agrave;n trong ASEAN</strong></p>\r\n\r\n<p>Tham gia thảo luận, đại diện của đo&agrave;n Việt Nam n&ecirc;u r&otilde;, đại dịch Covid-19 kh&ocirc;ng chỉ l&agrave; một cuộc khủng hoảng y tế đơn thuần, m&agrave; c&ograve;n l&agrave; một cuộc khủng hoảng đối với sự ph&aacute;t triển khi c&aacute;c chuỗi cung ứng v&agrave; thương mại quốc tế bị ph&aacute; vỡ.</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của Ng&acirc;n h&agrave;ng Ph&aacute;t triển ch&acirc;u &Aacute; (ADB), dự b&aacute;o tăng trưởng tổng sản phẩm quốc nội (GDP) của khu vực Đ&ocirc;ng Nam &Aacute; sẽ giảm từ 4,4% năm 2019 xuống c&ograve;n -2,7% trong năm 2020, trước khi phục hồi l&ecirc;n mức 5,2% v&agrave;o năm 2021, đặc biệt do c&aacute;c mối quan hệ thương mại v&agrave; đầu tư mạnh mẽ của khu vực n&agrave;y với Trung Quốc.</p>\r\n\r\n<p>Quan ngại s&acirc;u sắc về những tổn thất to lớn do dịch bệnh Covid-19 g&acirc;y ra ở c&aacute;c quốc gia ASEAN, trong tư c&aacute;ch chủ nh&agrave;, đo&agrave;n Việt Nam mong muốn cộng đồng ASEAN thống nhất thực hiện một chuỗi giải ph&aacute;p mang t&iacute;nh đột ph&aacute;, khả thi.</p>\r\n\r\n<p>Một l&agrave;, thực hiện th&uacute;c đẩy nhanh việc trao đổi th&ocirc;ng tin li&ecirc;n quan đến du lịch v&agrave; sức khỏe v&agrave; c&aacute;c biện ph&aacute;p cần thiết kh&aacute;c để kiểm so&aacute;t sự l&acirc;y lan của đại dịch Covid-19; củng cố sự an t&acirc;m trong di chuyển, &aacute;p dụng c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; quy tr&igrave;nh y tế hỗ trợ di chuyển xuy&ecirc;n bi&ecirc;n giới của c&ocirc;ng d&acirc;n ASEAN kh&ocirc;ng bị gi&aacute;n đoạn, đồng thời tu&acirc;n thủ c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; y tế quốc tế cũng như c&aacute;c cam kết theo c&aacute;c Hiệp định c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-2-1599635215859.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Ph&oacute; Chủ tịch Quốc hội Việt Nam Ph&ugrave;ng Quốc Hiển tham gia phi&ecirc;n thảo luận.</p>\r\n\r\n<p>Hai l&agrave;, nghi&ecirc;n cứu kh&aacute;i niệm &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; như một s&aacute;ng kiến sơ bộ trong qu&aacute; tr&igrave;nh mở cửa lại bi&ecirc;n giới, h&igrave;nh th&agrave;nh c&aacute;c khu vực di chuyển an to&agrave;n trong ASEAN v&agrave; đề xuất hướng dẫn tham chiếu cho tất cả c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN m&agrave; kh&ocirc;ng l&agrave;m tổn hại đến những cam kết theo c&aacute;c thỏa thuận c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p>Ba l&agrave;, tiếp tục r&agrave; so&aacute;t v&agrave; ho&agrave;n thiện khu&ocirc;n khổ ph&aacute;p l&yacute; nhằm th&uacute;c đẩy thuận lợi h&oacute;a thương mại, bảo đảm lưu th&ocirc;ng h&agrave;ng h&oacute;a v&agrave; c&aacute;c chuỗi cung ứng trong khu vực kh&ocirc;ng bị gi&aacute;n đoạn; x&acirc;y dựng ch&iacute;nh s&aacute;ch đầu tư th&ocirc;ng tho&aacute;ng, bền vững, c&oacute; tr&aacute;ch nhiệm trong khu vực; bảo đảm an ninh lương thực v&agrave; chuỗi gi&aacute; trị n&ocirc;ng nghiệp; tăng cường sử dụng năng lượng t&aacute;i tạo; tiến tới x&acirc;y dựng kinh tế tuần ho&agrave;n.</p>\r\n\r\n<p>Bốn l&agrave;, th&uacute;c đẩy nhanh việc ph&ecirc; chuẩn c&aacute;c hiệp định, thỏa thuận thương mại trong khu vực; ưu ti&ecirc;n tăng cường c&aacute;c nỗ lực để ho&agrave;n tất đ&agrave;m ph&aacute;n v&agrave; k&yacute; kết Hiệp định Đối t&aacute;c Kinh tế To&agrave;n diện Khu vực (RCEP) trong năm 2020; tăng cường năng lực, sự tham gia v&agrave; đẩy mạnh gi&aacute;m s&aacute;t nghị viện đối với việc thực hiện c&aacute;c cam kết về đầu tư v&agrave; thương mại trong khu&ocirc;n khổ khu vực v&agrave; quốc tế, coi đ&acirc;y l&agrave; c&ocirc;ng cụ chủ yếu để th&uacute;c đẩy li&ecirc;n kết kinh tế s&acirc;u rộng hơn trong Cộng đồng ASEAN v&agrave; giữa ASEAN với c&aacute;c đối t&aacute;c kh&aacute;c.</p>\r\n\r\n<p><strong>Khai th&aacute;c t&agrave;i nguy&ecirc;n xuy&ecirc;n bi&ecirc;n giới để đảm bảo an ninh lương thực&nbsp;</strong></p>\r\n\r\n<p>Năm l&agrave;, tăng cường ph&aacute;t triển cơ sở hạ tầng số, kết nối số, an to&agrave;n dữ liệu số, kiến thức v&agrave; kỹ năng số gắn kết giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN để ph&aacute;t triển kinh tế số; chia sẻ th&ocirc;ng tin v&agrave; kinh nghiệm để xử l&yacute; c&aacute;c th&aacute;ch thức m&agrave; c&aacute;c nền tảng thương mại điện tử đang phải đối mặt; tận dụng c&aacute;c cơ hội của C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư nhằm khắc phục bất lợi từ việc đ&oacute;ng cửa nền kinh tế v&agrave; gi&atilde;n c&aacute;ch x&atilde; hội trong dịch bệnh Covid-19 v&agrave; duy tr&igrave; t&iacute;nh cạnh tranh trong kỷ nguy&ecirc;n kinh tế số.</p>\r\n\r\n<p>S&aacute;u l&agrave;, x&acirc;y dựng cơ sở hạ tầng kết nối khu vực, tăng cường kết nối giao th&ocirc;ng v&agrave; sự cần thiết của việc tăng cường hợp t&aacute;c tiểu v&ugrave;ng M&ecirc; C&ocirc;ng v&agrave; c&aacute;c tiểu v&ugrave;ng kh&aacute;c của ASEAN, đặc biệt l&agrave; về bảo vệ m&ocirc;i trường, ph&aacute;t triển n&ocirc;ng nghiệp th&ocirc;ng minh v&agrave; quản l&yacute; bền vững c&aacute;c nguồn t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n xuy&ecirc;n bi&ecirc;n giới nhằm đảm bảo an ninh lương thực, nguồn nước v&agrave; năng lượng tại c&aacute;c tiểu v&ugrave;ng trong v&agrave; sau đại dịch Covid-19.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-5-1599635216200.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n thảo luận diễn ra đồng thời tại điểm cầu của c&aacute;c nước trong cộng đồng ASEAN.</p>\r\n\r\n<p>Ph&aacute;t biểu tại phi&ecirc;n họp, đại diện đo&agrave;n Malaysia cho rằng, c&aacute;c nước trong ASEAN c&oacute; nhiều điểm chung n&ecirc;n ch&iacute;nh s&aacute;ch của bất cứ quốc gia n&agrave;o cũng c&oacute; thể được c&aacute;c quốc gia kh&aacute;c học tập, để c&oacute; thể tr&aacute;nh gặp sai lầm giống nhau.</p>\r\n\r\n<p>Theo nghị sỹ Malaysia, thời điểm n&agrave;y rất cần một c&aacute;ch tiếp cận chung theo hướng địa phương ho&aacute; c&aacute;c hoạt động kinh tế. Hầu hết hoạt động kinh tế đ&atilde; được t&iacute;ch hợp v&agrave;o chuối cũng ứng to&agrave;n cầu nhưng Covid- 19 đ&atilde; l&agrave;m gi&aacute;n đoạn chuỗi cung ứng n&agrave;y.</p>\r\n\r\n<p>B&agrave;i học l&agrave; cần phải giảm thiểu sự to&agrave;n cầu ho&aacute;. Nhiều doanh nghiệp đang địa phương ho&aacute; chuỗi sản xuất của m&igrave;n, ASEAN c&oacute; thể địa phương ho&aacute;, mở rộng sang khu vực ho&aacute;, xử l&yacute; chuỗi cung ứng khu vực để miễn dịch khủng hoảng trong tương lai, đại diện đo&agrave;n nghị sĩ Malaysia n&ecirc;u quan điểm.</p>\r\n\r\n<p>Chia sẻ kinh nghiệm ứng ph&oacute; với đại dịch Covid-19, đại diện Singapore cho biết nước n&agrave;y mở ra 4 loại ng&acirc;n s&aacute;ch, gồm quỹ ki&ecirc;n cường, quỹ chống chịu, quỹ đo&agrave;n kết v&agrave; quỹ thống&nbsp; nhất, bốn quỹ n&agrave;y được Ch&iacute;nh phủ hỗ trợ. B&ecirc;n cạnh đ&oacute; Singapore c&ograve;n c&oacute; ch&iacute;nh s&aacute;ch hỗ trợ để doanh nghiệp giữ việc l&agrave;m cho người lao động, ch&iacute;nh s&aacute;ch nay được gia hạn đến th&aacute;ng 3/2011.</p>\r\n\r\n<p><strong>Th&aacute;i Anh</strong></p>\r\n', 0, '1619060836_bleu-3-600x600.jpg', 5000000, 5, 16),
(15, 'Nước Hoa BURBERRY MR BBY EDT 100ML', '<p>Khắc phục hậu quả của đại dịch Covid-19, một s&aacute;ng kiến được đưa ra giữa c&aacute;c nước ASEAN l&agrave; tổ chức &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; để t&aacute;i mở cửa trong khu vực&hellip;</p>\r\n', '<p>S&aacute;ng 9/9, trong khu&ocirc;n khổ chương tr&igrave;nh Đại hội đồng Li&ecirc;n nghị viện ASEAN (AIPA) 41, UB Kinh tế AIPA c&oacute; phi&ecirc;n thảo luận về vai tr&ograve; của Nghị viện trong việc th&uacute;c đẩy gắn kết v&agrave; phục hồi kinh tế ASEAN sau đại dịch Covid-19.</p>\r\n\r\n<p>L&agrave; Trưởng đo&agrave;n Việt Nam tham gia phi&ecirc;n thảo luận, Ph&oacute; Chủ tịch Quốc hội Ph&ugrave;ng Quốc Hiển nhận định, đ&acirc;y l&agrave; chủ đề mang t&iacute;nh thiết thực, c&oacute; t&iacute;nh thời sự cấp b&aacute;ch đối với mọi quốc gia th&agrave;nh vi&ecirc;n AIPA hiện nay. Trong bối cảnh khu vực v&agrave; quốc tế bị ảnh hưởng nặng nề bởi dịch bệnh Covid-19 như hiện nay, Ph&oacute; Chủ tịch Quốc hội b&agrave;y tỏ mong muốn c&aacute;c nước th&agrave;nh vi&ecirc;n sớm vượt qua kh&oacute; khăn, phục hồi nền kinh tế, ổn định đời sống x&atilde; hội v&agrave; &ldquo;kh&ocirc;ng để ai bị bỏ lại ph&iacute;a sau&rdquo;.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-3-1599635216487.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n họp của UB Kinh tế AIPA 41 diễn ra cuối buổi s&aacute;ng ng&agrave;y 9/9/2020.</p>\r\n\r\n<p><strong>Mở cửa lại bi&ecirc;n giới, di chuyển an to&agrave;n trong ASEAN</strong></p>\r\n\r\n<p>Tham gia thảo luận, đại diện của đo&agrave;n Việt Nam n&ecirc;u r&otilde;, đại dịch Covid-19 kh&ocirc;ng chỉ l&agrave; một cuộc khủng hoảng y tế đơn thuần, m&agrave; c&ograve;n l&agrave; một cuộc khủng hoảng đối với sự ph&aacute;t triển khi c&aacute;c chuỗi cung ứng v&agrave; thương mại quốc tế bị ph&aacute; vỡ.</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của Ng&acirc;n h&agrave;ng Ph&aacute;t triển ch&acirc;u &Aacute; (ADB), dự b&aacute;o tăng trưởng tổng sản phẩm quốc nội (GDP) của khu vực Đ&ocirc;ng Nam &Aacute; sẽ giảm từ 4,4% năm 2019 xuống c&ograve;n -2,7% trong năm 2020, trước khi phục hồi l&ecirc;n mức 5,2% v&agrave;o năm 2021, đặc biệt do c&aacute;c mối quan hệ thương mại v&agrave; đầu tư mạnh mẽ của khu vực n&agrave;y với Trung Quốc.</p>\r\n\r\n<p>Quan ngại s&acirc;u sắc về những tổn thất to lớn do dịch bệnh Covid-19 g&acirc;y ra ở c&aacute;c quốc gia ASEAN, trong tư c&aacute;ch chủ nh&agrave;, đo&agrave;n Việt Nam mong muốn cộng đồng ASEAN thống nhất thực hiện một chuỗi giải ph&aacute;p mang t&iacute;nh đột ph&aacute;, khả thi.</p>\r\n\r\n<p>Một l&agrave;, thực hiện th&uacute;c đẩy nhanh việc trao đổi th&ocirc;ng tin li&ecirc;n quan đến du lịch v&agrave; sức khỏe v&agrave; c&aacute;c biện ph&aacute;p cần thiết kh&aacute;c để kiểm so&aacute;t sự l&acirc;y lan của đại dịch Covid-19; củng cố sự an t&acirc;m trong di chuyển, &aacute;p dụng c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; quy tr&igrave;nh y tế hỗ trợ di chuyển xuy&ecirc;n bi&ecirc;n giới của c&ocirc;ng d&acirc;n ASEAN kh&ocirc;ng bị gi&aacute;n đoạn, đồng thời tu&acirc;n thủ c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; y tế quốc tế cũng như c&aacute;c cam kết theo c&aacute;c Hiệp định c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-2-1599635215859.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Ph&oacute; Chủ tịch Quốc hội Việt Nam Ph&ugrave;ng Quốc Hiển tham gia phi&ecirc;n thảo luận.</p>\r\n\r\n<p>Hai l&agrave;, nghi&ecirc;n cứu kh&aacute;i niệm &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; như một s&aacute;ng kiến sơ bộ trong qu&aacute; tr&igrave;nh mở cửa lại bi&ecirc;n giới, h&igrave;nh th&agrave;nh c&aacute;c khu vực di chuyển an to&agrave;n trong ASEAN v&agrave; đề xuất hướng dẫn tham chiếu cho tất cả c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN m&agrave; kh&ocirc;ng l&agrave;m tổn hại đến những cam kết theo c&aacute;c thỏa thuận c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p>Ba l&agrave;, tiếp tục r&agrave; so&aacute;t v&agrave; ho&agrave;n thiện khu&ocirc;n khổ ph&aacute;p l&yacute; nhằm th&uacute;c đẩy thuận lợi h&oacute;a thương mại, bảo đảm lưu th&ocirc;ng h&agrave;ng h&oacute;a v&agrave; c&aacute;c chuỗi cung ứng trong khu vực kh&ocirc;ng bị gi&aacute;n đoạn; x&acirc;y dựng ch&iacute;nh s&aacute;ch đầu tư th&ocirc;ng tho&aacute;ng, bền vững, c&oacute; tr&aacute;ch nhiệm trong khu vực; bảo đảm an ninh lương thực v&agrave; chuỗi gi&aacute; trị n&ocirc;ng nghiệp; tăng cường sử dụng năng lượng t&aacute;i tạo; tiến tới x&acirc;y dựng kinh tế tuần ho&agrave;n.</p>\r\n\r\n<p>Bốn l&agrave;, th&uacute;c đẩy nhanh việc ph&ecirc; chuẩn c&aacute;c hiệp định, thỏa thuận thương mại trong khu vực; ưu ti&ecirc;n tăng cường c&aacute;c nỗ lực để ho&agrave;n tất đ&agrave;m ph&aacute;n v&agrave; k&yacute; kết Hiệp định Đối t&aacute;c Kinh tế To&agrave;n diện Khu vực (RCEP) trong năm 2020; tăng cường năng lực, sự tham gia v&agrave; đẩy mạnh gi&aacute;m s&aacute;t nghị viện đối với việc thực hiện c&aacute;c cam kết về đầu tư v&agrave; thương mại trong khu&ocirc;n khổ khu vực v&agrave; quốc tế, coi đ&acirc;y l&agrave; c&ocirc;ng cụ chủ yếu để th&uacute;c đẩy li&ecirc;n kết kinh tế s&acirc;u rộng hơn trong Cộng đồng ASEAN v&agrave; giữa ASEAN với c&aacute;c đối t&aacute;c kh&aacute;c.</p>\r\n\r\n<p><strong>Khai th&aacute;c t&agrave;i nguy&ecirc;n xuy&ecirc;n bi&ecirc;n giới để đảm bảo an ninh lương thực&nbsp;</strong></p>\r\n\r\n<p>Năm l&agrave;, tăng cường ph&aacute;t triển cơ sở hạ tầng số, kết nối số, an to&agrave;n dữ liệu số, kiến thức v&agrave; kỹ năng số gắn kết giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN để ph&aacute;t triển kinh tế số; chia sẻ th&ocirc;ng tin v&agrave; kinh nghiệm để xử l&yacute; c&aacute;c th&aacute;ch thức m&agrave; c&aacute;c nền tảng thương mại điện tử đang phải đối mặt; tận dụng c&aacute;c cơ hội của C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư nhằm khắc phục bất lợi từ việc đ&oacute;ng cửa nền kinh tế v&agrave; gi&atilde;n c&aacute;ch x&atilde; hội trong dịch bệnh Covid-19 v&agrave; duy tr&igrave; t&iacute;nh cạnh tranh trong kỷ nguy&ecirc;n kinh tế số.</p>\r\n\r\n<p>S&aacute;u l&agrave;, x&acirc;y dựng cơ sở hạ tầng kết nối khu vực, tăng cường kết nối giao th&ocirc;ng v&agrave; sự cần thiết của việc tăng cường hợp t&aacute;c tiểu v&ugrave;ng M&ecirc; C&ocirc;ng v&agrave; c&aacute;c tiểu v&ugrave;ng kh&aacute;c của ASEAN, đặc biệt l&agrave; về bảo vệ m&ocirc;i trường, ph&aacute;t triển n&ocirc;ng nghiệp th&ocirc;ng minh v&agrave; quản l&yacute; bền vững c&aacute;c nguồn t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n xuy&ecirc;n bi&ecirc;n giới nhằm đảm bảo an ninh lương thực, nguồn nước v&agrave; năng lượng tại c&aacute;c tiểu v&ugrave;ng trong v&agrave; sau đại dịch Covid-19.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-5-1599635216200.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n thảo luận diễn ra đồng thời tại điểm cầu của c&aacute;c nước trong cộng đồng ASEAN.</p>\r\n\r\n<p>Ph&aacute;t biểu tại phi&ecirc;n họp, đại diện đo&agrave;n Malaysia cho rằng, c&aacute;c nước trong ASEAN c&oacute; nhiều điểm chung n&ecirc;n ch&iacute;nh s&aacute;ch của bất cứ quốc gia n&agrave;o cũng c&oacute; thể được c&aacute;c quốc gia kh&aacute;c học tập, để c&oacute; thể tr&aacute;nh gặp sai lầm giống nhau.</p>\r\n\r\n<p>Theo nghị sỹ Malaysia, thời điểm n&agrave;y rất cần một c&aacute;ch tiếp cận chung theo hướng địa phương ho&aacute; c&aacute;c hoạt động kinh tế. Hầu hết hoạt động kinh tế đ&atilde; được t&iacute;ch hợp v&agrave;o chuối cũng ứng to&agrave;n cầu nhưng Covid- 19 đ&atilde; l&agrave;m gi&aacute;n đoạn chuỗi cung ứng n&agrave;y.</p>\r\n\r\n<p>B&agrave;i học l&agrave; cần phải giảm thiểu sự to&agrave;n cầu ho&aacute;. Nhiều doanh nghiệp đang địa phương ho&aacute; chuỗi sản xuất của m&igrave;n, ASEAN c&oacute; thể địa phương ho&aacute;, mở rộng sang khu vực ho&aacute;, xử l&yacute; chuỗi cung ứng khu vực để miễn dịch khủng hoảng trong tương lai, đại diện đo&agrave;n nghị sĩ Malaysia n&ecirc;u quan điểm.</p>\r\n\r\n<p>Chia sẻ kinh nghiệm ứng ph&oacute; với đại dịch Covid-19, đại diện Singapore cho biết nước n&agrave;y mở ra 4 loại ng&acirc;n s&aacute;ch, gồm quỹ ki&ecirc;n cường, quỹ chống chịu, quỹ đo&agrave;n kết v&agrave; quỹ thống&nbsp; nhất, bốn quỹ n&agrave;y được Ch&iacute;nh phủ hỗ trợ. B&ecirc;n cạnh đ&oacute; Singapore c&ograve;n c&oacute; ch&iacute;nh s&aacute;ch hỗ trợ để doanh nghiệp giữ việc l&agrave;m cho người lao động, ch&iacute;nh s&aacute;ch nay được gia hạn đến th&aacute;ng 3/2011.</p>\r\n\r\n<p><strong>Th&aacute;i Anh</strong></p>\r\n', 0, '1619060815_burberry-my-burberry-black-1-2-600x600.jpg', 4500000, 10, 16),
(16, 'Nước hoa BOSS THE SCENT FOR HER 50 ML', '<p>Khắc phục hậu quả của đại dịch Covid-19, một s&aacute;ng kiến được đưa ra giữa c&aacute;c nước ASEAN l&agrave; tổ chức &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; để t&aacute;i mở cửa trong khu vực&hellip;</p>\r\n', '<p>S&aacute;ng 9/9, trong khu&ocirc;n khổ chương tr&igrave;nh Đại hội đồng Li&ecirc;n nghị viện ASEAN (AIPA) 41, UB Kinh tế AIPA c&oacute; phi&ecirc;n thảo luận về vai tr&ograve; của Nghị viện trong việc th&uacute;c đẩy gắn kết v&agrave; phục hồi kinh tế ASEAN sau đại dịch Covid-19.</p>\r\n\r\n<p>L&agrave; Trưởng đo&agrave;n Việt Nam tham gia phi&ecirc;n thảo luận, Ph&oacute; Chủ tịch Quốc hội Ph&ugrave;ng Quốc Hiển nhận định, đ&acirc;y l&agrave; chủ đề mang t&iacute;nh thiết thực, c&oacute; t&iacute;nh thời sự cấp b&aacute;ch đối với mọi quốc gia th&agrave;nh vi&ecirc;n AIPA hiện nay. Trong bối cảnh khu vực v&agrave; quốc tế bị ảnh hưởng nặng nề bởi dịch bệnh Covid-19 như hiện nay, Ph&oacute; Chủ tịch Quốc hội b&agrave;y tỏ mong muốn c&aacute;c nước th&agrave;nh vi&ecirc;n sớm vượt qua kh&oacute; khăn, phục hồi nền kinh tế, ổn định đời sống x&atilde; hội v&agrave; &ldquo;kh&ocirc;ng để ai bị bỏ lại ph&iacute;a sau&rdquo;.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-3-1599635216487.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n họp của UB Kinh tế AIPA 41 diễn ra cuối buổi s&aacute;ng ng&agrave;y 9/9/2020.</p>\r\n\r\n<p><strong>Mở cửa lại bi&ecirc;n giới, di chuyển an to&agrave;n trong ASEAN</strong></p>\r\n\r\n<p>Tham gia thảo luận, đại diện của đo&agrave;n Việt Nam n&ecirc;u r&otilde;, đại dịch Covid-19 kh&ocirc;ng chỉ l&agrave; một cuộc khủng hoảng y tế đơn thuần, m&agrave; c&ograve;n l&agrave; một cuộc khủng hoảng đối với sự ph&aacute;t triển khi c&aacute;c chuỗi cung ứng v&agrave; thương mại quốc tế bị ph&aacute; vỡ.</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của Ng&acirc;n h&agrave;ng Ph&aacute;t triển ch&acirc;u &Aacute; (ADB), dự b&aacute;o tăng trưởng tổng sản phẩm quốc nội (GDP) của khu vực Đ&ocirc;ng Nam &Aacute; sẽ giảm từ 4,4% năm 2019 xuống c&ograve;n -2,7% trong năm 2020, trước khi phục hồi l&ecirc;n mức 5,2% v&agrave;o năm 2021, đặc biệt do c&aacute;c mối quan hệ thương mại v&agrave; đầu tư mạnh mẽ của khu vực n&agrave;y với Trung Quốc.</p>\r\n\r\n<p>Quan ngại s&acirc;u sắc về những tổn thất to lớn do dịch bệnh Covid-19 g&acirc;y ra ở c&aacute;c quốc gia ASEAN, trong tư c&aacute;ch chủ nh&agrave;, đo&agrave;n Việt Nam mong muốn cộng đồng ASEAN thống nhất thực hiện một chuỗi giải ph&aacute;p mang t&iacute;nh đột ph&aacute;, khả thi.</p>\r\n\r\n<p>Một l&agrave;, thực hiện th&uacute;c đẩy nhanh việc trao đổi th&ocirc;ng tin li&ecirc;n quan đến du lịch v&agrave; sức khỏe v&agrave; c&aacute;c biện ph&aacute;p cần thiết kh&aacute;c để kiểm so&aacute;t sự l&acirc;y lan của đại dịch Covid-19; củng cố sự an t&acirc;m trong di chuyển, &aacute;p dụng c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; quy tr&igrave;nh y tế hỗ trợ di chuyển xuy&ecirc;n bi&ecirc;n giới của c&ocirc;ng d&acirc;n ASEAN kh&ocirc;ng bị gi&aacute;n đoạn, đồng thời tu&acirc;n thủ c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; y tế quốc tế cũng như c&aacute;c cam kết theo c&aacute;c Hiệp định c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-2-1599635215859.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Ph&oacute; Chủ tịch Quốc hội Việt Nam Ph&ugrave;ng Quốc Hiển tham gia phi&ecirc;n thảo luận.</p>\r\n\r\n<p>Hai l&agrave;, nghi&ecirc;n cứu kh&aacute;i niệm &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; như một s&aacute;ng kiến sơ bộ trong qu&aacute; tr&igrave;nh mở cửa lại bi&ecirc;n giới, h&igrave;nh th&agrave;nh c&aacute;c khu vực di chuyển an to&agrave;n trong ASEAN v&agrave; đề xuất hướng dẫn tham chiếu cho tất cả c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN m&agrave; kh&ocirc;ng l&agrave;m tổn hại đến những cam kết theo c&aacute;c thỏa thuận c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p>Ba l&agrave;, tiếp tục r&agrave; so&aacute;t v&agrave; ho&agrave;n thiện khu&ocirc;n khổ ph&aacute;p l&yacute; nhằm th&uacute;c đẩy thuận lợi h&oacute;a thương mại, bảo đảm lưu th&ocirc;ng h&agrave;ng h&oacute;a v&agrave; c&aacute;c chuỗi cung ứng trong khu vực kh&ocirc;ng bị gi&aacute;n đoạn; x&acirc;y dựng ch&iacute;nh s&aacute;ch đầu tư th&ocirc;ng tho&aacute;ng, bền vững, c&oacute; tr&aacute;ch nhiệm trong khu vực; bảo đảm an ninh lương thực v&agrave; chuỗi gi&aacute; trị n&ocirc;ng nghiệp; tăng cường sử dụng năng lượng t&aacute;i tạo; tiến tới x&acirc;y dựng kinh tế tuần ho&agrave;n.</p>\r\n\r\n<p>Bốn l&agrave;, th&uacute;c đẩy nhanh việc ph&ecirc; chuẩn c&aacute;c hiệp định, thỏa thuận thương mại trong khu vực; ưu ti&ecirc;n tăng cường c&aacute;c nỗ lực để ho&agrave;n tất đ&agrave;m ph&aacute;n v&agrave; k&yacute; kết Hiệp định Đối t&aacute;c Kinh tế To&agrave;n diện Khu vực (RCEP) trong năm 2020; tăng cường năng lực, sự tham gia v&agrave; đẩy mạnh gi&aacute;m s&aacute;t nghị viện đối với việc thực hiện c&aacute;c cam kết về đầu tư v&agrave; thương mại trong khu&ocirc;n khổ khu vực v&agrave; quốc tế, coi đ&acirc;y l&agrave; c&ocirc;ng cụ chủ yếu để th&uacute;c đẩy li&ecirc;n kết kinh tế s&acirc;u rộng hơn trong Cộng đồng ASEAN v&agrave; giữa ASEAN với c&aacute;c đối t&aacute;c kh&aacute;c.</p>\r\n\r\n<p><strong>Khai th&aacute;c t&agrave;i nguy&ecirc;n xuy&ecirc;n bi&ecirc;n giới để đảm bảo an ninh lương thực&nbsp;</strong></p>\r\n\r\n<p>Năm l&agrave;, tăng cường ph&aacute;t triển cơ sở hạ tầng số, kết nối số, an to&agrave;n dữ liệu số, kiến thức v&agrave; kỹ năng số gắn kết giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN để ph&aacute;t triển kinh tế số; chia sẻ th&ocirc;ng tin v&agrave; kinh nghiệm để xử l&yacute; c&aacute;c th&aacute;ch thức m&agrave; c&aacute;c nền tảng thương mại điện tử đang phải đối mặt; tận dụng c&aacute;c cơ hội của C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư nhằm khắc phục bất lợi từ việc đ&oacute;ng cửa nền kinh tế v&agrave; gi&atilde;n c&aacute;ch x&atilde; hội trong dịch bệnh Covid-19 v&agrave; duy tr&igrave; t&iacute;nh cạnh tranh trong kỷ nguy&ecirc;n kinh tế số.</p>\r\n\r\n<p>S&aacute;u l&agrave;, x&acirc;y dựng cơ sở hạ tầng kết nối khu vực, tăng cường kết nối giao th&ocirc;ng v&agrave; sự cần thiết của việc tăng cường hợp t&aacute;c tiểu v&ugrave;ng M&ecirc; C&ocirc;ng v&agrave; c&aacute;c tiểu v&ugrave;ng kh&aacute;c của ASEAN, đặc biệt l&agrave; về bảo vệ m&ocirc;i trường, ph&aacute;t triển n&ocirc;ng nghiệp th&ocirc;ng minh v&agrave; quản l&yacute; bền vững c&aacute;c nguồn t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n xuy&ecirc;n bi&ecirc;n giới nhằm đảm bảo an ninh lương thực, nguồn nước v&agrave; năng lượng tại c&aacute;c tiểu v&ugrave;ng trong v&agrave; sau đại dịch Covid-19.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-5-1599635216200.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n thảo luận diễn ra đồng thời tại điểm cầu của c&aacute;c nước trong cộng đồng ASEAN.</p>\r\n\r\n<p>Ph&aacute;t biểu tại phi&ecirc;n họp, đại diện đo&agrave;n Malaysia cho rằng, c&aacute;c nước trong ASEAN c&oacute; nhiều điểm chung n&ecirc;n ch&iacute;nh s&aacute;ch của bất cứ quốc gia n&agrave;o cũng c&oacute; thể được c&aacute;c quốc gia kh&aacute;c học tập, để c&oacute; thể tr&aacute;nh gặp sai lầm giống nhau.</p>\r\n\r\n<p>Theo nghị sỹ Malaysia, thời điểm n&agrave;y rất cần một c&aacute;ch tiếp cận chung theo hướng địa phương ho&aacute; c&aacute;c hoạt động kinh tế. Hầu hết hoạt động kinh tế đ&atilde; được t&iacute;ch hợp v&agrave;o chuối cũng ứng to&agrave;n cầu nhưng Covid- 19 đ&atilde; l&agrave;m gi&aacute;n đoạn chuỗi cung ứng n&agrave;y.</p>\r\n\r\n<p>B&agrave;i học l&agrave; cần phải giảm thiểu sự to&agrave;n cầu ho&aacute;. Nhiều doanh nghiệp đang địa phương ho&aacute; chuỗi sản xuất của m&igrave;n, ASEAN c&oacute; thể địa phương ho&aacute;, mở rộng sang khu vực ho&aacute;, xử l&yacute; chuỗi cung ứng khu vực để miễn dịch khủng hoảng trong tương lai, đại diện đo&agrave;n nghị sĩ Malaysia n&ecirc;u quan điểm.</p>\r\n\r\n<p>Chia sẻ kinh nghiệm ứng ph&oacute; với đại dịch Covid-19, đại diện Singapore cho biết nước n&agrave;y mở ra 4 loại ng&acirc;n s&aacute;ch, gồm quỹ ki&ecirc;n cường, quỹ chống chịu, quỹ đo&agrave;n kết v&agrave; quỹ thống&nbsp; nhất, bốn quỹ n&agrave;y được Ch&iacute;nh phủ hỗ trợ. B&ecirc;n cạnh đ&oacute; Singapore c&ograve;n c&oacute; ch&iacute;nh s&aacute;ch hỗ trợ để doanh nghiệp giữ việc l&agrave;m cho người lao động, ch&iacute;nh s&aacute;ch nay được gia hạn đến th&aacute;ng 3/2011.</p>\r\n\r\n<p><strong>Th&aacute;i Anh</strong></p>\r\n', 0, '1619059603_nuoc-hoa-burberry-her-2.png', 2300000, 5, 16),
(17, 'Nước Hoa Gucci Bloom Acqua di Fiori 50ml', '<p>Khắc phục hậu quả của đại dịch Covid-19, một s&aacute;ng kiến được đưa ra giữa c&aacute;c nước ASEAN l&agrave; tổ chức &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; để t&aacute;i mở cửa trong khu vực&hellip;</p>\r\n', '<p>S&aacute;ng 9/9, trong khu&ocirc;n khổ chương tr&igrave;nh Đại hội đồng Li&ecirc;n nghị viện ASEAN (AIPA) 41, UB Kinh tế AIPA c&oacute; phi&ecirc;n thảo luận về vai tr&ograve; của Nghị viện trong việc th&uacute;c đẩy gắn kết v&agrave; phục hồi kinh tế ASEAN sau đại dịch Covid-19.</p>\r\n\r\n<p>L&agrave; Trưởng đo&agrave;n Việt Nam tham gia phi&ecirc;n thảo luận, Ph&oacute; Chủ tịch Quốc hội Ph&ugrave;ng Quốc Hiển nhận định, đ&acirc;y l&agrave; chủ đề mang t&iacute;nh thiết thực, c&oacute; t&iacute;nh thời sự cấp b&aacute;ch đối với mọi quốc gia th&agrave;nh vi&ecirc;n AIPA hiện nay. Trong bối cảnh khu vực v&agrave; quốc tế bị ảnh hưởng nặng nề bởi dịch bệnh Covid-19 như hiện nay, Ph&oacute; Chủ tịch Quốc hội b&agrave;y tỏ mong muốn c&aacute;c nước th&agrave;nh vi&ecirc;n sớm vượt qua kh&oacute; khăn, phục hồi nền kinh tế, ổn định đời sống x&atilde; hội v&agrave; &ldquo;kh&ocirc;ng để ai bị bỏ lại ph&iacute;a sau&rdquo;.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-3-1599635216487.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 1\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n họp của UB Kinh tế AIPA 41 diễn ra cuối buổi s&aacute;ng ng&agrave;y 9/9/2020.</p>\r\n\r\n<p><strong>Mở cửa lại bi&ecirc;n giới, di chuyển an to&agrave;n trong ASEAN</strong></p>\r\n\r\n<p>Tham gia thảo luận, đại diện của đo&agrave;n Việt Nam n&ecirc;u r&otilde;, đại dịch Covid-19 kh&ocirc;ng chỉ l&agrave; một cuộc khủng hoảng y tế đơn thuần, m&agrave; c&ograve;n l&agrave; một cuộc khủng hoảng đối với sự ph&aacute;t triển khi c&aacute;c chuỗi cung ứng v&agrave; thương mại quốc tế bị ph&aacute; vỡ.</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của Ng&acirc;n h&agrave;ng Ph&aacute;t triển ch&acirc;u &Aacute; (ADB), dự b&aacute;o tăng trưởng tổng sản phẩm quốc nội (GDP) của khu vực Đ&ocirc;ng Nam &Aacute; sẽ giảm từ 4,4% năm 2019 xuống c&ograve;n -2,7% trong năm 2020, trước khi phục hồi l&ecirc;n mức 5,2% v&agrave;o năm 2021, đặc biệt do c&aacute;c mối quan hệ thương mại v&agrave; đầu tư mạnh mẽ của khu vực n&agrave;y với Trung Quốc.</p>\r\n\r\n<p>Quan ngại s&acirc;u sắc về những tổn thất to lớn do dịch bệnh Covid-19 g&acirc;y ra ở c&aacute;c quốc gia ASEAN, trong tư c&aacute;ch chủ nh&agrave;, đo&agrave;n Việt Nam mong muốn cộng đồng ASEAN thống nhất thực hiện một chuỗi giải ph&aacute;p mang t&iacute;nh đột ph&aacute;, khả thi.</p>\r\n\r\n<p>Một l&agrave;, thực hiện th&uacute;c đẩy nhanh việc trao đổi th&ocirc;ng tin li&ecirc;n quan đến du lịch v&agrave; sức khỏe v&agrave; c&aacute;c biện ph&aacute;p cần thiết kh&aacute;c để kiểm so&aacute;t sự l&acirc;y lan của đại dịch Covid-19; củng cố sự an t&acirc;m trong di chuyển, &aacute;p dụng c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; quy tr&igrave;nh y tế hỗ trợ di chuyển xuy&ecirc;n bi&ecirc;n giới của c&ocirc;ng d&acirc;n ASEAN kh&ocirc;ng bị gi&aacute;n đoạn, đồng thời tu&acirc;n thủ c&aacute;c ti&ecirc;u chuẩn an to&agrave;n v&agrave; y tế quốc tế cũng như c&aacute;c cam kết theo c&aacute;c Hiệp định c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-2-1599635215859.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 2\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Ph&oacute; Chủ tịch Quốc hội Việt Nam Ph&ugrave;ng Quốc Hiển tham gia phi&ecirc;n thảo luận.</p>\r\n\r\n<p>Hai l&agrave;, nghi&ecirc;n cứu kh&aacute;i niệm &ldquo;du lịch cầu h&agrave;ng kh&ocirc;ng&rdquo; giữa &ldquo;c&aacute;c nước xanh l&aacute; c&acirc;y&rdquo; như một s&aacute;ng kiến sơ bộ trong qu&aacute; tr&igrave;nh mở cửa lại bi&ecirc;n giới, h&igrave;nh th&agrave;nh c&aacute;c khu vực di chuyển an to&agrave;n trong ASEAN v&agrave; đề xuất hướng dẫn tham chiếu cho tất cả c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN m&agrave; kh&ocirc;ng l&agrave;m tổn hại đến những cam kết theo c&aacute;c thỏa thuận c&oacute; li&ecirc;n quan giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN.</p>\r\n\r\n<p>Ba l&agrave;, tiếp tục r&agrave; so&aacute;t v&agrave; ho&agrave;n thiện khu&ocirc;n khổ ph&aacute;p l&yacute; nhằm th&uacute;c đẩy thuận lợi h&oacute;a thương mại, bảo đảm lưu th&ocirc;ng h&agrave;ng h&oacute;a v&agrave; c&aacute;c chuỗi cung ứng trong khu vực kh&ocirc;ng bị gi&aacute;n đoạn; x&acirc;y dựng ch&iacute;nh s&aacute;ch đầu tư th&ocirc;ng tho&aacute;ng, bền vững, c&oacute; tr&aacute;ch nhiệm trong khu vực; bảo đảm an ninh lương thực v&agrave; chuỗi gi&aacute; trị n&ocirc;ng nghiệp; tăng cường sử dụng năng lượng t&aacute;i tạo; tiến tới x&acirc;y dựng kinh tế tuần ho&agrave;n.</p>\r\n\r\n<p>Bốn l&agrave;, th&uacute;c đẩy nhanh việc ph&ecirc; chuẩn c&aacute;c hiệp định, thỏa thuận thương mại trong khu vực; ưu ti&ecirc;n tăng cường c&aacute;c nỗ lực để ho&agrave;n tất đ&agrave;m ph&aacute;n v&agrave; k&yacute; kết Hiệp định Đối t&aacute;c Kinh tế To&agrave;n diện Khu vực (RCEP) trong năm 2020; tăng cường năng lực, sự tham gia v&agrave; đẩy mạnh gi&aacute;m s&aacute;t nghị viện đối với việc thực hiện c&aacute;c cam kết về đầu tư v&agrave; thương mại trong khu&ocirc;n khổ khu vực v&agrave; quốc tế, coi đ&acirc;y l&agrave; c&ocirc;ng cụ chủ yếu để th&uacute;c đẩy li&ecirc;n kết kinh tế s&acirc;u rộng hơn trong Cộng đồng ASEAN v&agrave; giữa ASEAN với c&aacute;c đối t&aacute;c kh&aacute;c.</p>\r\n\r\n<p><strong>Khai th&aacute;c t&agrave;i nguy&ecirc;n xuy&ecirc;n bi&ecirc;n giới để đảm bảo an ninh lương thực&nbsp;</strong></p>\r\n\r\n<p>Năm l&agrave;, tăng cường ph&aacute;t triển cơ sở hạ tầng số, kết nối số, an to&agrave;n dữ liệu số, kiến thức v&agrave; kỹ năng số gắn kết giữa c&aacute;c nước th&agrave;nh vi&ecirc;n ASEAN để ph&aacute;t triển kinh tế số; chia sẻ th&ocirc;ng tin v&agrave; kinh nghiệm để xử l&yacute; c&aacute;c th&aacute;ch thức m&agrave; c&aacute;c nền tảng thương mại điện tử đang phải đối mặt; tận dụng c&aacute;c cơ hội của C&aacute;ch mạng c&ocirc;ng nghiệp lần thứ tư nhằm khắc phục bất lợi từ việc đ&oacute;ng cửa nền kinh tế v&agrave; gi&atilde;n c&aacute;ch x&atilde; hội trong dịch bệnh Covid-19 v&agrave; duy tr&igrave; t&iacute;nh cạnh tranh trong kỷ nguy&ecirc;n kinh tế số.</p>\r\n\r\n<p>S&aacute;u l&agrave;, x&acirc;y dựng cơ sở hạ tầng kết nối khu vực, tăng cường kết nối giao th&ocirc;ng v&agrave; sự cần thiết của việc tăng cường hợp t&aacute;c tiểu v&ugrave;ng M&ecirc; C&ocirc;ng v&agrave; c&aacute;c tiểu v&ugrave;ng kh&aacute;c của ASEAN, đặc biệt l&agrave; về bảo vệ m&ocirc;i trường, ph&aacute;t triển n&ocirc;ng nghiệp th&ocirc;ng minh v&agrave; quản l&yacute; bền vững c&aacute;c nguồn t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n xuy&ecirc;n bi&ecirc;n giới nhằm đảm bảo an ninh lương thực, nguồn nước v&agrave; năng lượng tại c&aacute;c tiểu v&ugrave;ng trong v&agrave; sau đại dịch Covid-19.</p>\r\n\r\n<p><img alt=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" src=\"https://icdn.dantri.com.vn/thumb_w/640/2020/09/09/ub-kinh-te-5-1599635216200.jpg\" title=\"Du lịch cầu hàng không giữa “các nước xanh lá cây” để tái mở cửa ASEAN - 3\" /></p>\r\n\r\n<p>Nhấn để ph&oacute;ng to ảnh</p>\r\n\r\n<p>Phi&ecirc;n thảo luận diễn ra đồng thời tại điểm cầu của c&aacute;c nước trong cộng đồng ASEAN.</p>\r\n\r\n<p>Ph&aacute;t biểu tại phi&ecirc;n họp, đại diện đo&agrave;n Malaysia cho rằng, c&aacute;c nước trong ASEAN c&oacute; nhiều điểm chung n&ecirc;n ch&iacute;nh s&aacute;ch của bất cứ quốc gia n&agrave;o cũng c&oacute; thể được c&aacute;c quốc gia kh&aacute;c học tập, để c&oacute; thể tr&aacute;nh gặp sai lầm giống nhau.</p>\r\n\r\n<p>Theo nghị sỹ Malaysia, thời điểm n&agrave;y rất cần một c&aacute;ch tiếp cận chung theo hướng địa phương ho&aacute; c&aacute;c hoạt động kinh tế. Hầu hết hoạt động kinh tế đ&atilde; được t&iacute;ch hợp v&agrave;o chuối cũng ứng to&agrave;n cầu nhưng Covid- 19 đ&atilde; l&agrave;m gi&aacute;n đoạn chuỗi cung ứng n&agrave;y.</p>\r\n\r\n<p>B&agrave;i học l&agrave; cần phải giảm thiểu sự to&agrave;n cầu ho&aacute;. Nhiều doanh nghiệp đang địa phương ho&aacute; chuỗi sản xuất của m&igrave;n, ASEAN c&oacute; thể địa phương ho&aacute;, mở rộng sang khu vực ho&aacute;, xử l&yacute; chuỗi cung ứng khu vực để miễn dịch khủng hoảng trong tương lai, đại diện đo&agrave;n nghị sĩ Malaysia n&ecirc;u quan điểm.</p>\r\n\r\n<p>Chia sẻ kinh nghiệm ứng ph&oacute; với đại dịch Covid-19, đại diện Singapore cho biết nước n&agrave;y mở ra 4 loại ng&acirc;n s&aacute;ch, gồm quỹ ki&ecirc;n cường, quỹ chống chịu, quỹ đo&agrave;n kết v&agrave; quỹ thống&nbsp; nhất, bốn quỹ n&agrave;y được Ch&iacute;nh phủ hỗ trợ. B&ecirc;n cạnh đ&oacute; Singapore c&ograve;n c&oacute; ch&iacute;nh s&aacute;ch hỗ trợ để doanh nghiệp giữ việc l&agrave;m cho người lao động, ch&iacute;nh s&aacute;ch nay được gia hạn đến th&aacute;ng 3/2011.</p>\r\n\r\n<p><strong>Th&aacute;i Anh</strong></p>\r\n', 1, '1619059561_gucci-bloom-1.jpg', 3000000, 5, 17);
INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `photo`, `price`, `discount`, `category_id`) VALUES
(18, 'Nước hoa MKC SPARKLING BLUSH 100ML', '<p><strong>Nước Hoa Nữ MKC Sparkling Blush 100ml</strong>&nbsp;đại diện cho những người phụ nữ thực sự tự tin v&agrave; biết c&aacute;ch l&agrave;m cho m&igrave;nh tỏa s&aacute;ng. Rạng rỡ, tươi s&aacute;ng v&agrave; quyến rũ, Sparkling Blush như một vi&ecirc;n ngọc qu&yacute;, được m&agrave;i dũa tinh tế với những mặt cắt phản chiếu nhiều t&iacute;nh c&aacute;ch của người phụ n</p>\r\n', '<p>Nh&oacute;m hương Woody Floral, ngọt ng&agrave;o v&agrave; ấm &aacute;p<br />\r\nHương thơm của những quả vải mọng nước ướp trong c&aacute;nh hoa hồng<br />\r\nLoại nước hoa: EDP<br />\r\nKiểu d&aacute;ng chai được lấy cảm hứng từ một vi&ecirc;n hồng ngọc lộng lẫy</p>\r\n\r\n<p><img alt=\"9f00c2dbbeff6284e3ad0c81175deea8\" src=\"https://cocoshop.vn/uploads/shops/2020_06/9f00c2dbbeff6284e3ad0c81175deea8.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><strong>Nước Hoa Nữ MKC Sparkling Blush 100ml</strong>&nbsp;đại diện cho những người phụ nữ thực sự tự tin v&agrave; biết c&aacute;ch l&agrave;m cho m&igrave;nh tỏa s&aacute;ng. Rạng rỡ, tươi s&aacute;ng v&agrave; quyến rũ, Sparkling Blush như một vi&ecirc;n ngọc qu&yacute;, được m&agrave;i dũa tinh tế với những mặt cắt phản chiếu nhiều t&iacute;nh c&aacute;ch của người phụ nữ.</li>\r\n	<li>Hương thơm: Nh&oacute;m hương Woody Floral, ngọt ng&agrave;o v&agrave; ấm &aacute;p. Hương thơm của những quả vải mọng nước ướp trong c&aacute;nh hoa hồng, được bao bọc bởi hổ ph&aacute;ch quyến rũ.</li>\r\n	<li>Loại nước hoa: EDP</li>\r\n	<li>Kiểu d&aacute;ng chai: h&igrave;nh dạng, đường n&eacute;t, m&agrave;u sắc, c&aacute;c mặt cắt được lấy cảm hứng từ một vi&ecirc;n hồng ngọc lộng lẫy. Nắp chai vẫn l&agrave; h&igrave;nh ảnh chiếc mề đay v&agrave;ng đồng s&aacute;ng b&oacute;ng, nguy&ecirc;n bản tr&ecirc;n từng sản phẩm của Michael Kors.</li>\r\n	<li>Năm ra đời: 2018</li>\r\n</ul>\r\n\r\n<p><img alt=\"5T76 01 9999 2\" src=\"https://cocoshop.vn/uploads/shops/2020_06/5t76-01-9999_2.jpg\" style=\"height:804px; width:600px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Th&agrave;nh phần:&nbsp;</strong></p>\r\n\r\n<ul>\r\n	<li>Hương đầu: quả vải, Bergamot, l&ecirc;, ti&ecirc;u hồng</li>\r\n	<li>Hương giữa: hoa nh&agrave;i, hoa hồng, lily, hoa sứ, mộc lan&nbsp;</li>\r\n	<li>Hương cuối: hổ ph&aacute;ch, vertiver, đ&agrave;n hương v&agrave; vani</li>\r\n</ul>\r\n\r\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. Tuy nhi&ecirc;n tuỳ v&agrave;o từng loại sản phẩm hoặc phương thức, địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, ...</p>\r\n', 0, '1619018384_nuoc-hoa-mkc-sparkling-blush-100ml.jpg', 2800000, 50, 16),
(19, 'Nước Hoa Marc Jacobs Daisy Love EDT 100ml', '<p>Nước hoa nữ Marc Jacobs Daisy Love EDT với hương thơm nhẹ nh&agrave;ng v&agrave; ngọt ng&agrave;o cho bạn th&ecirc;m phần quyến rũ .<br />\r\nNước hoa nữ Marc Jacobs Daisy Love EDT phong c&aacute;ch: quyến rũ, ngọt ng&agrave;o, l&ocirc;i cuốn</p>\r\n', '<p>nữ Marc Jacobs Daisy Love EDT l&agrave; một sản phẩm đến từ thương hiệu Marc Jacobs của Mỹ. Marc Jacobs vốn nổi tiếng với c&aacute;c sản phẩm cao cấp kh&ocirc;ng dừng lại chỉ ở nước hoa, hơn nữa thương hiệu n&agrave;y cũng l&agrave; một trong những nh&atilde;n h&agrave;ng c&oacute; lượng fan rất đ&ocirc;ng đảo. Một số sản phẩm nước hoa của Marc Jacobs m&agrave;&nbsp;<a href=\"https://cocoshop.vn/\">COCO SHOP</a>&nbsp;gợi &yacute; th&ecirc;m cho bạn như&nbsp;<a href=\"https://cocoshop.vn/group/marc-jacobs/\">Marc Jacobs</a>&nbsp;Daisy Dream EDT, Marc Jacobs Daisy Eau So Fresh EDT, Marc Jacobs Decadence EDP,&hellip;<br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"download (3)\" src=\"https://cocoshop.vn/uploads/shops/2020_12/download-3.jpg\" style=\"height:800px; width:652px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<br />\r\n<br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"db5cf42f4980afe600f685a914999774\" src=\"https://cocoshop.vn/uploads/shops/2020_12/db5cf42f4980afe600f685a914999774.jpg\" style=\"height:450px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<strong>C&Ocirc;NG DỤNG</strong><br />\r\n<br />\r\nNước hoa nữ Marc Jacobs Daisy Love EDT l&agrave; một m&ugrave;i nước hoa sở hữu nh&oacute;m hương hoa cỏ gỗ xạ hương &ndash; Floral Woody Musk. Với c&aacute;i t&ecirc;n Daisy Love ch&iacute;nh l&agrave; chỉ đến những đ&oacute;a hoa c&uacute;c m&ugrave;a thu v&agrave; cũng l&agrave; &acirc;m hưởng giai điệu của những bản t&igrave;nh ca ngọt ng&agrave;o. Hương thơm của nước hoa n&agrave;y bắt đầu từ hương thơm của thi&ecirc;n nhi&ecirc;n như quả bưởi huyết, d&acirc;u dại v&agrave; l&aacute; hoa t&iacute;m cho một m&ugrave;i thơm thanh m&aacute;t mang hương vị rất ri&ecirc;ng.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"download (3)\" src=\"https://cocoshop.vn/uploads/shops/2020_12/download-3.jpg\" style=\"height:800px; width:652px\" /></p>\r\n\r\n<p><br />\r\nNước hoa nữ Marc Jacobs Daisy Love EDT 4ml th&iacute;ch hợp sử dụng trong m&ugrave;a xu&acirc;n hoặc tiết đầu m&ugrave;a hạ, lọ nước hoa nhỏ xinh, sang trọng, kh&ocirc;ng cần cầm l&ecirc;n bạn vẫn c&oacute; thể nh&igrave;n r&otilde; m&agrave;u sắc của nước hoa b&ecirc;n trong.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"751D3395 C9EE 470A AF53 6462BC2ED45F 1 big\" src=\"https://cocoshop.vn/uploads/shops/2020_12/751d3395-c9ee-470a-af53-6462bc2ed45f_1_big.png\" style=\"height:413px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n-Tiếp đến l&agrave; m&ugrave;i hương giữa từ hoa sơn chi, hoa t&iacute;m v&agrave; hoa nh&agrave;i của sản phẩm sau khi hương đầu nhạt dần. Sự chuyển đổi m&ugrave;i hương linh hoạt trong c&ugrave;ng một sản phẩm nước hoa cho thấy người pha chế đ&atilde; tốn kh&aacute; nhiều c&ocirc;ng sức cho c&ocirc;ng đoạn n&agrave;y. Kết th&uacute;c của bất kỳ hương nước hoa n&agrave;o cũng l&agrave; một m&ugrave;i hương g&acirc;y thương nhớ từ gỗ trắng, hương Vani, xạ hương. Hương cuối n&agrave;y cũng l&agrave; m&ugrave;i hương kh&eacute;p lại to&agrave;n bộ những hương thơm đằng trước k&egrave;m theo độ lưu hương tốt hơn, b&aacute;m l&acirc;u tr&ecirc;n cơ thể hơn.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"marc jacobs daisy love eau de toilette 100ml\" src=\"https://cocoshop.vn/uploads/shops/2020_12/marc-jacobs-daisy-love-eau-de-toilette-100ml.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n-Nước hoa Marc Jacobs Daisy Love l&agrave; sự kết hợp của c&aacute;c loại tinh dầu thi&ecirc;n nhi&ecirc;n mang đến cho bạn sự trải nghiệm m&ugrave;i hương tinh tế đến từ thi&ecirc;n nhi&ecirc;n bốn m&ugrave;a mang lại, kết hợp c&ugrave;ng với hương vanilla gi&uacute;p giữ m&ugrave;i l&acirc;u hơn.&nbsp;<br />\r\n-Nồng độ Eau de toilette (EDT) của nước hoa n&agrave;y cho khả năng tỏa hương v&agrave; lưu hương cũng như độ b&aacute;m m&ugrave;i tr&ecirc;n cơ thể ở mức ổn. Th&iacute;ch hợp sử dụng trong m&ugrave;a xu&acirc;n hoặc tiết đầu m&ugrave;a hạ cho cảm gi&aacute;c m&aacute;t mẻ xua tan đi c&aacute;i n&oacute;ng của m&ugrave;a hạ v&agrave; ch&uacute;t ẩm ướt của m&ugrave;a xu&acirc;n.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nuoc hoa mini marc jacobs daisy love edt 4ml boshop 3\" src=\"https://cocoshop.vn/uploads/shops/2020_12/nuoc-hoa-mini-marc-jacobs-daisy-love-edt-4ml-boshop-3.jpg\" style=\"height:408px; width:600px\" /></p>\r\n\r\n<p><br />\r\nNước hoa nữ Marc Jacobs Daisy Love EDT phong c&aacute;ch: quyến rũ, ngọt ng&agrave;o, l&ocirc;i cuốn<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"3614225476570 5 1200x1200\" src=\"https://cocoshop.vn/uploads/shops/2020_12/3614225476570-5-1200x1200.jpg\" style=\"height:600px; width:600px\" /></p>\r\n', 0, '1619071847_nuoc-hoa-burberry-her-2.png', 2850000, 5, 16),
(20, 'Nước Hoa Chloe Nomade 75ml', '<p>Độ lưu hương: L&acirc;u - 7 giờ đến 12 giờ<br />\r\nĐộ toả hương: Gần - Toả hương trong v&ograve;ng một c&aacute;nh tay<br />\r\nThời điểm khuy&ecirc;n d&ugrave;ng: Ng&agrave;y, Đ&ecirc;m, Xu&acirc;</p>\r\n', '<p>omade l&agrave; hương thơm mới nhất được&nbsp;<a href=\"https://cocoshop.vn/group/chloe/\">Chlo&eacute;</a>&nbsp;giới thiệu v&agrave;o năm 2018, kh&ocirc;ng chỉ l&agrave; một d&ograve;ng nước hoa mới m&agrave; đ&oacute; c&ograve;n được xem l&agrave; thời đại phong c&aacute;ch đầy nổi loạn kh&aacute;c biệt với những g&igrave; m&agrave; Chlo&eacute; đ&atilde; từng l&agrave;m trước đ&oacute;.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nuoc hoa chloe nomade 5\" src=\"https://cocoshop.vn/uploads/shops/2020_11/nuoc-hoa-chloe-nomade-5.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nNh&agrave; s&aacute;ng tạo ra loại nước hoa n&agrave;y muốn mang đến một tinh thần g&igrave; đ&oacute; thật đậm sắc, ấn tượng m&agrave; vẫn nữ t&iacute;nh cho người sử dụng với lời k&ecirc;u gọi mọi c&ocirc; g&aacute;i h&atilde;y tho&aacute;t khỏi những điều g&ograve; b&oacute; bản th&acirc;n để thoải m&aacute;i tận hưởng kh&ocirc;ng gian bao la của cuộc sống.<br />\r\n<br />\r\nNếu như phi&ecirc;n bản m&ugrave;i hương Chlo&eacute; EDP kinh điển l&agrave; tiếng n&oacute;i ngọt ng&agrave;o của ngập tr&agrave;n hoa cỏ th&igrave; đến với Nomade đ&oacute; lại l&agrave; hỗn hợp ho&agrave; quyện của hương gỗ v&agrave; tr&aacute;i c&acirc;y, tiết chế lại hương hoa để l&agrave;m n&ecirc;n một kh&uacute;c biến tấu mới lạ, hiện đại hơn &ndash; đ&oacute; chắc hẳn sẽ l&agrave; trải nghiệm mới mẻ cho tất cả những ai đ&atilde; y&ecirc;u th&iacute;ch nước hoa từ nh&agrave; Chlo&eacute;.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nuoc hoa chloe nomade 3\" src=\"https://cocoshop.vn/uploads/shops/2020_11/nuoc-hoa-chloe-nomade-3.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nLọ nước hoa n&agrave;y được thiết kế với h&igrave;nh d&aacute;ng của một chiếc t&uacute;i lữ h&agrave;nh với những chi tiết da thuộc đầy cảm hứng. Từ m&ugrave;i hương cho đến vẻ ngo&agrave;i đều khiến những ai kho&aacute;c l&ecirc;n m&igrave;nh Chlo&eacute; Nomade đều nhận ra được niềm vui v&agrave; kh&aacute;t vọng của bản th&acirc;n, niềm mong mỏi vượt mọi h&agrave;nh tr&igrave;nh để t&igrave;m về bản năng của ri&ecirc;ng người phụ nữ.<br />\r\n<br />\r\nĐộ lưu hương: L&acirc;u - 7 giờ đến 12 giờ<br />\r\nĐộ toả hương: Gần - Toả hương trong v&ograve;ng một c&aacute;nh tay<br />\r\nThời điểm khuy&ecirc;n d&ugrave;ng: Ng&agrave;y, Đ&ecirc;m, Xu&acirc;n<br />\r\n<br />\r\n<strong>Hương Đầu:</strong>&nbsp;Mận v&agrave;ng, Cam Bergamot, Quả chanh v&agrave;ng, Quả cam<br />\r\n<strong>Hương giữa:</strong>&nbsp;Hoa lan Nam Phi, Hoa nh&agrave;i, Quả đ&agrave;o, Hoa hồng<br />\r\n<strong>Hương cuối:</strong>&nbsp;R&ecirc;u sồi, Amberwood, C&acirc;y hoắc hương, Gỗ đ&agrave;n hương, Xạ hương trắng<br />\r\n<br />\r\n<a href=\"https://cocoshop.vn/nuoc-hoa-body/\">Nước hoa nữ</a>&nbsp;Nomade ph&ugrave; hợp với người tr&ecirc;n 25 tuổi.Đ&acirc;y l&agrave; d&ograve;ng nước hoa Chlo&eacute; n&agrave;y c&oacute; độ lưu hương l&acirc;u - 7 giờ đến 12 giờ. v&agrave; độ tỏa hương thuộc dạng gần - toả hương trong v&ograve;ng một c&aacute;nh tay. Perfumista.vn khuyến c&aacute;o Nomade ph&ugrave; hợp để sử dụng trong cả ng&agrave;y lẫn đ&ecirc;m v&agrave;o m&ugrave;a xu&acirc;n.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nuoc hoa chloe nomade 2\" src=\"https://cocoshop.vn/uploads/shops/2020_11/nuoc-hoa-chloe-nomade-2.jpg\" style=\"height:500px; width:500px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nĐ&acirc;y l&agrave; d&ograve;ng nước hoa Chlo&eacute; thuộc nh&oacute;m Chypre Floral (Hương hoa cỏ S&Iacute;P). Nomade được cho ra mắt v&agrave;o năm 2018. B&ecirc;n cạnh đ&oacute;, R&ecirc;u sồi v&agrave; Mận v&agrave;ng l&agrave; hai hương bạn c&oacute; thể dễ d&agrave;ng cảm nhận được nhất khi sử dụng nước hoa n&agrave;y.<br />\r\n<br />\r\n<strong>C&Aacute;CH D&Ugrave;NG</strong><br />\r\n<br />\r\n- Lắc đều, xịt c&aacute;ch cơ thể khoảng 15 &ndash; 20 cm.<br />\r\n- Hạn chế xịt trực tiếp l&ecirc;n quần &aacute;o trắng v&agrave; đồ trang sức.<br />\r\n- N&ecirc;n xịt nước hoa v&agrave;o những v&ugrave;ng da toả nhiệt nhiều hơn tr&ecirc;n cơ thể để nước hoa lưu được l&acirc;u v&agrave; c&oacute; thể ngửi m&ugrave;i r&otilde; nhất như: cổ tay, khuỷu tay, ngực, ph&iacute;a sau g&aacute;y, giữa ngực thậm ch&iacute; cả phần khoeo ch&acirc;n.<br />\r\n- Xịt nước hoa ở những nơi da ẩm nhất v&agrave; c&oacute; thể khuyếch t&aacute;n hương thơm tốt nhất.</p>\r\n', 0, '1619018118_nuoc-hoa-chloe-leau-1.jpg', 2000000, 5, 16),
(21, 'Nước Hoa Chloe Nomade 75ml', '<p>Độ lưu hương: L&acirc;u - 7 giờ đến 12 giờ<br />\r\nĐộ toả hương: Gần - Toả hương trong v&ograve;ng một c&aacute;nh tay<br />\r\nThời điểm khuy&ecirc;n d&ugrave;ng: Ng&agrave;y, Đ&ecirc;m, Xu&acirc;n</p>\r\n', '<p>&nbsp;</p>\r\n\r\n<p>omade l&agrave; hương thơm mới nhất được&nbsp;<a href=\"https://cocoshop.vn/group/chloe/\">Chlo&eacute;</a>&nbsp;giới thiệu v&agrave;o năm 2018, kh&ocirc;ng chỉ l&agrave; một d&ograve;ng nước hoa mới m&agrave; đ&oacute; c&ograve;n được xem l&agrave; thời đại phong c&aacute;ch đầy nổi loạn kh&aacute;c biệt với những g&igrave; m&agrave; Chlo&eacute; đ&atilde; từng l&agrave;m trước đ&oacute;.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nuoc hoa chloe nomade 5\" src=\"https://cocoshop.vn/uploads/shops/2020_11/nuoc-hoa-chloe-nomade-5.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nNh&agrave; s&aacute;ng tạo ra loại nước hoa n&agrave;y muốn mang đến một tinh thần g&igrave; đ&oacute; thật đậm sắc, ấn tượng m&agrave; vẫn nữ t&iacute;nh cho người sử dụng với lời k&ecirc;u gọi mọi c&ocirc; g&aacute;i h&atilde;y tho&aacute;t khỏi những điều g&ograve; b&oacute; bản th&acirc;n để thoải m&aacute;i tận hưởng kh&ocirc;ng gian bao la của cuộc sống.<br />\r\n<br />\r\nNếu như phi&ecirc;n bản m&ugrave;i hương Chlo&eacute; EDP kinh điển l&agrave; tiếng n&oacute;i ngọt ng&agrave;o của ngập tr&agrave;n hoa cỏ th&igrave; đến với Nomade đ&oacute; lại l&agrave; hỗn hợp ho&agrave; quyện của hương gỗ v&agrave; tr&aacute;i c&acirc;y, tiết chế lại hương hoa để l&agrave;m n&ecirc;n một kh&uacute;c biến tấu mới lạ, hiện đại hơn &ndash; đ&oacute; chắc hẳn sẽ l&agrave; trải nghiệm mới mẻ cho tất cả những ai đ&atilde; y&ecirc;u th&iacute;ch nước hoa từ nh&agrave; Chlo&eacute;.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nuoc hoa chloe nomade 3\" src=\"https://cocoshop.vn/uploads/shops/2020_11/nuoc-hoa-chloe-nomade-3.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nLọ nước hoa n&agrave;y được thiết kế với h&igrave;nh d&aacute;ng của một chiếc t&uacute;i lữ h&agrave;nh với những chi tiết da thuộc đầy cảm hứng. Từ m&ugrave;i hương cho đến vẻ ngo&agrave;i đều khiến những ai kho&aacute;c l&ecirc;n m&igrave;nh Chlo&eacute; Nomade đều nhận ra được niềm vui v&agrave; kh&aacute;t vọng của bản th&acirc;n, niềm mong mỏi vượt mọi h&agrave;nh tr&igrave;nh để t&igrave;m về bản năng của ri&ecirc;ng người phụ nữ.<br />\r\n<br />\r\nĐộ lưu hương: L&acirc;u - 7 giờ đến 12 giờ<br />\r\nĐộ toả hương: Gần - Toả hương trong v&ograve;ng một c&aacute;nh tay<br />\r\nThời điểm khuy&ecirc;n d&ugrave;ng: Ng&agrave;y, Đ&ecirc;m, Xu&acirc;n<br />\r\n<br />\r\n<strong>Hương Đầu:</strong>&nbsp;Mận v&agrave;ng, Cam Bergamot, Quả chanh v&agrave;ng, Quả cam<br />\r\n<strong>Hương giữa:</strong>&nbsp;Hoa lan Nam Phi, Hoa nh&agrave;i, Quả đ&agrave;o, Hoa hồng<br />\r\n<strong>Hương cuối:</strong>&nbsp;R&ecirc;u sồi, Amberwood, C&acirc;y hoắc hương, Gỗ đ&agrave;n hương, Xạ hương trắng<br />\r\n<br />\r\n<a href=\"https://cocoshop.vn/nuoc-hoa-body/\">Nước hoa nữ</a>&nbsp;Nomade ph&ugrave; hợp với người tr&ecirc;n 25 tuổi.Đ&acirc;y l&agrave; d&ograve;ng nước hoa Chlo&eacute; n&agrave;y c&oacute; độ lưu hương l&acirc;u - 7 giờ đến 12 giờ. v&agrave; độ tỏa hương thuộc dạng gần - toả hương trong v&ograve;ng một c&aacute;nh tay. Perfumista.vn khuyến c&aacute;o Nomade ph&ugrave; hợp để sử dụng trong cả ng&agrave;y lẫn đ&ecirc;m v&agrave;o m&ugrave;a xu&acirc;n.<br />\r\n&nbsp;</p>\r\n', 1, '1619071785_nuoc-hoa-chloe-nomade-1.jpg', 3535000, 10, 5),
(22, 'Nước Hoa Gucci Guilty Absolute Pour Homme 50ml', '<p>- Phong c&aacute;ch: Mạnh mẽ, nam t&iacute;nh<br />\r\n- Hương đầu: Da thuộc<br />\r\n- Hương giữa: C&acirc;y hoắc hương, c&acirc;y b&aacute;ch<br />\r\n- Hương cuối: Hương gỗ, cỏ hương b&agrave;i</p>\r\n', '<p>hương hiệu thời trang nổi tiếng thế giới&nbsp;<a href=\"https://cocoshop.vn/group/gucci/\">Gucci</a>&nbsp;giới thiệu d&ograve;ng nước hoa nam mới c&oacute; t&ecirc;n Gucci Guilty Absolute v&agrave;o đầu năm 2017. D&ograve;ng sản phẩm mới n&agrave;y ch&iacute;nh l&agrave; kết quả của sự hợp t&aacute;c kh&aacute; ăn &yacute; của gi&aacute;m đốc s&aacute;ng tạo nh&agrave; Gucci Alessandro v&agrave; nh&agrave; s&aacute;ng chế nước hoa Alberto Morillas.&nbsp;<br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nuoc hoa gucci guilty absolute pour homme 1\" src=\"https://cocoshop.vn/uploads/shops/2020_11/nuoc-hoa-gucci-guilty-absolute-pour-homme-1.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nHương thơm của Gucci Guilty Absolute th&iacute;ch hợp cho những ch&agrave;ng trai bản lĩnh, mạnh mẽ, y&ecirc;u c&aacute;i đẹp v&agrave; th&iacute;ch sự tinh tế. Cỏ hương b&agrave;i v&agrave; da thuộc l&agrave; hai chất x&uacute;c t&aacute;c m&atilde;nh mẹ tạo n&ecirc;n độ lưu hương l&acirc;u v&agrave; vẻ l&ocirc;i cuốn, mời gọi cho hương thơm.<br />\r\n<br />\r\nNgo&agrave;i hương thơm nịnh mũi th&igrave; thiết kế chai cũng kh&aacute; được l&ograve;ng c&aacute;c ch&agrave;ng. Với m&agrave;u cam n&oacute;ng bỏng v&agrave; rực rỡ, Guilty Absolute mang một n&eacute;t mới mẻ, hiện đại nhưng vẫn để lại dấu ấn kh&ocirc;ng thể nhầm lẫn được của những &quot;đ&agrave;n anh&quot; đi trước. Biểu tượng hai chữ G l&ograve;ng v&agrave;o nhau l&agrave;m điểm nhấn cho th&acirc;n chai v&agrave; l&agrave; lời khẳng định mạnh mẽ của &ocirc;ng tr&ugrave;m thời trang Gucci trong lĩnh vực hương thơm đa dạng, tinh tế n&agrave;y.<br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nuoc hoa gucci guilty absolute pour homme 2\" src=\"https://cocoshop.vn/uploads/shops/2020_11/nuoc-hoa-gucci-guilty-absolute-pour-homme-2.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nNh&oacute;m hương ch&iacute;nh: Hương thơm gỗ phương Đ&ocirc;ng<br />\r\n<br />\r\n<strong>Hương đầu:&nbsp;</strong>Da thuộc<br />\r\n<strong>Hương giữa:</strong>&nbsp;C&acirc;y hoắc hương, c&acirc;y b&aacute;ch<br />\r\n<strong>Hương cuối:</strong>&nbsp;Hương gỗ, cỏ hương b&agrave;i<br />\r\n<br />\r\nNồng độ : Eau De Parfum<br />\r\nĐộ lưu hương: Khoảng 12 giờ<br />\r\nĐộ tỏa hương: Trong b&aacute;n k&iacute;nh 2m<br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nuoc hoa gucci guilty absolute pour homme 3\" src=\"https://cocoshop.vn/uploads/shops/2020_11/nuoc-hoa-gucci-guilty-absolute-pour-homme-3.jpg\" style=\"height:800px; width:800px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<strong>C&Aacute;CH D&Ugrave;NG</strong><br />\r\n<br />\r\nLắc đều, xịt c&aacute;ch cơ thể khoảng 15 &ndash; 20 cm.<br />\r\n- Hạn chế xịt trực tiếp l&ecirc;n quần &aacute;o trắng v&agrave; đồ trang sức.<br />\r\n-N&ecirc;n xịt nước hoa v&agrave;o những v&ugrave;ng da toả nhiệt nhiều hơn tr&ecirc;n cơ thể để nước hoa lưu được l&acirc;u v&agrave; c&oacute; thể ngửi m&ugrave;i r&otilde; nhất như: cổ tay, khuỷu tay, ngực, ph&iacute;a sau g&aacute;y, giữa ngực thậm ch&iacute; cả phần khoeo ch&acirc;n.<br />\r\n- Xịt nước hoa ở những nơi da ẩm nhất v&agrave; c&oacute; thể khuyếch t&aacute;n hương thơm tốt nhất.</p>\r\n', 0, '1619071942_nuoc-hoa-gucci-guilty-absolute-pour-homme-1.jpg', 4320000, 10, 17),
(23, 'Son Burberry Kisses', '<p>Với độ dưỡng ẩm cao, chất son satin mịn m&agrave;ng như bơ gi&uacute;p tăng độ l&ecirc;n m&agrave;u sẽ mang đến cho bạn đ&ocirc;i m&ocirc;i mềm mại v&agrave; gợi cảm nhất, bạn sẽ kh&ocirc;ng lo m&ocirc;i bị th&acirc;m bị nếp hay m&agrave;u bị bột nặng m&ocirc;i khắc phục được nhược điểm g&acirc;y kh&oacute; chịu của d&ograve;ng son l&igrave;.</p>\r\n', '<p>urberry Kisses l&agrave; d&ograve;ng sản phẩm cao cấp từ thương hiệu thời trang h&agrave;ng đầu Burberry đến từ xứ sở sương m&ugrave; Anh Quốc. Được l&agrave;m từ những nguy&ecirc;n liệu tốt nhất, son Burberry Kisses mang đến một đ&ocirc;i m&ocirc;i thực sự quyến rũ mềm mại thể hiện đẳng cấp của ph&aacute;i đẹp.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"son burberry 1\" src=\"https://cocoshop.vn/uploads/shops/2021_01/son-burberry-1.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nĐ&acirc;y l&agrave; một trong những thỏi son High-end l&agrave;m &quot;lay động&quot; tr&aacute;i tim chị em ch&uacute;ng m&igrave;nh bởi sự sang trọng, đẳng cấp, thu h&uacute;t mọi &aacute;nh nh&igrave;n<br />\r\n<br />\r\nVẫn sở hữu h&igrave;nh dạng hộp vu&ocirc;ng vắn, kho&aacute;c l&ecirc;n m&igrave;nh 1 tone m&agrave;u đen x&aacute;m huyền b&iacute; v&agrave; thiết kế kẻ sọc đặc trưng của Burberry cực sang chảnh. Phần nắp của em &yacute; dạng nam ch&acirc;m, th&acirc;n son cực chắc chắn, ko lỏng lẻo n&ecirc;n c&aacute;c n&agrave;ng ho&agrave;n to&agrave;n c&oacute; thể y&ecirc;n t&acirc;m nh&eacute;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"son burberry\" src=\"https://cocoshop.vn/uploads/shops/2021_01/son-burberry.jpg\" style=\"height:900px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nD&ograve;ng Kisses c&oacute; chất son mềm mượt, b&oacute;ng nhẹ, đ&aacute;nh l&ecirc;n m&ocirc;i cực dễ t&aacute;n v&agrave; độ l&ecirc;n m&agrave;u chuẩn khỏi b&agrave;n lu&ocirc;n ạ. Em n&agrave;y c&oacute; dưỡng n&ecirc;n cả nh&agrave; khỏi lo bị kh&ocirc; m&ocirc;i nha, nhưng với đặc t&iacute;nh c&oacute; nhiều dưỡng th&igrave; độ bền m&agrave;u của em n&agrave;y sẽ kh&ocirc;ng được l&acirc;u, ăn uống dễ tr&ocirc;i cần phải t&uacute;t t&aacute;t lại<br />\r\n<br />\r\nBurberry Kisses với thiết kế hoa văn kẻ &ocirc; đặc trưng của Burberry. Thỏi son cầm nặng, chắc tay, vặn son l&ecirc;n xuống rất chắc chắn, kh&ocirc;ng hề lỏng lẻo, phần đ&oacute;ng mở c&oacute; nam ch&acirc;m rất chắc tay.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"son burberry 2\" src=\"https://cocoshop.vn/uploads/shops/2021_01/son-burberry-2.jpg\" style=\"height:750px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nVới độ dưỡng ẩm cao, chất son satin mịn m&agrave;ng như bơ gi&uacute;p tăng độ l&ecirc;n m&agrave;u sẽ mang đến cho bạn đ&ocirc;i m&ocirc;i mềm mại v&agrave; gợi cảm nhất, bạn sẽ kh&ocirc;ng lo m&ocirc;i bị th&acirc;m bị nếp hay m&agrave;u bị bột nặng m&ocirc;i khắc phục được nhược điểm g&acirc;y kh&oacute; chịu của d&ograve;ng son l&igrave;.<br />\r\n<br />\r\nSon Burberry Kisses d&ograve;ng satin l&agrave; sự lựa chọn ho&agrave;n hảo hơn son sheer với m&agrave;u sắc mạnh mẽ hơn v&agrave; son matte với độ dưỡng ẩm cao hơn rất nhiều.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"son burberry 5\" src=\"https://cocoshop.vn/uploads/shops/2021_01/son-burberry-5.jpg\" style=\"height:750px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nSở hữu thỏi son Burberry Kisses c&aacute;c n&agrave;ng sẽ &nbsp;cảm nhận ngay chất son mềm mại như satin, cho m&agrave;u sắc b&oacute;ng đẹp v&agrave; gi&agrave;u dưỡng ẩm. C&ocirc;ng thức nhẹ m&ocirc;i với th&agrave;nh phần gồm c&aacute;c loại dầu v&agrave; s&aacute;p dưỡng ẩm, c&ugrave;ng c&aacute;c ceramides chống l&atilde;o h&oacute;a, cho đ&ocirc;i m&ocirc;i căng mọng hơn.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"son burberry 4\" src=\"https://cocoshop.vn/uploads/shops/2021_01/son-burberry-4.jpg\" style=\"height:599px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Coco</strong>&nbsp;đang c&oacute; những m&agrave;u son đặc sắc<br />\r\n<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;73 Bright Coral - Cam tươi tắn, trẻ trung<br />\r\n<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;105 Poppy Red - Đỏ hồng rạng rỡ<br />\r\n<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;109 Military Red - Đỏ tươi quyến rũ<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"son burberry 5\" src=\"https://cocoshop.vn/uploads/shops/2021_01/son-burberry-5.jpg\" style=\"height:750px; width:600px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n', 0, '1619072357_son-burberry-kisses-73-bright-coral.jpg', 700000, 0, 9),
(24, 'Bọt Rửa Mặt Dove Tinh Chất - Serum Cấp Ẩm', '<p>- Cấp ẩm cho da bằng c&aacute;ch h&uacute;t lấy chất ẩm từ m&ocirc;i trường v&agrave;o da và giữ ẩm trong da.<br />\r\n- Chất dưỡng ẩm dạng ưa nước - cấp ẩm hiệu qủa và gi&uacute;p t&aacute;i tạo lớp sừng.<br />\r\n- X&acirc;y dựng lớp lipid nội b&agrave;o gi&uacute;p giữ ẩm.</p>\r\n', '<p>Dạng Bọt Tinh Chất Serum Dove Beauty Serum Facial Cleansing Mousse l&agrave; d&ograve;ng sản phẩm mới ra mắt từ thương hiệu mỹ phẩm chăm s&oacute;c da Dove của Mỹ, được sản xuất tại Nhật Bản theo ti&ecirc;u chuẩn khắt khe của thị trường nội địa Nhật. D&ograve;ng sản phẩm c&oacute; kết cấu dạng mousse rửa mặt bọt mịn chứa 40% tinh chất Serum dưỡng ẩm da, gi&uacute;p giữ lại tối đa dưỡng chất thiết yếu v&agrave; bảo vệ h&agrave;ng r&agrave;o độ ẩm tự nhi&ecirc;n của da sau mỗi lần rửa mặt. Tại Nhật, cứ mỗi 5 gi&acirc;y lại c&oacute; 1 chai Mousse rửa mặt Dove được b&aacute;n ra.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"sua rua mat dove giup se lo chan long kiem dau 2\" src=\"https://cocoshop.vn/uploads/shops/2020_12/sua-rua-mat-dove-giup-se-lo-chan-long-kiem-dau-2.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nMột l&agrave;n da khỏe mạnh l&agrave; một l&agrave;n da đủ độ ẩm, căng mượt v&agrave; đ&agrave;n hồi. Khi da thiếu ẩm, da sẽ trở n&ecirc;n xỉn m&agrave;u, thiếu sức sống, bề mặt da sần s&ugrave;i, lỗ ch&acirc;n l&ocirc;ng nở to v&agrave; da c&oacute; xu hướng tiết dầu nhiều hơn b&igrave;nh thường. T&igrave;nh trạng thiếu ẩm c&oacute; thể xảy ra ở mọi loại da, từ da kh&ocirc; cho đến da dầu đều c&oacute; khả năng bị thiếu ẩm, mất nước. Đặc biệt khi da dầu thiếu ẩm sẽ c&agrave;ng tiết th&ecirc;m nhiều dầu hơn để c&acirc;n bằng lại độ ẩm tr&ecirc;n da, nhưng vẫn c&oacute; cảm gi&aacute;c kh&ocirc; căng kh&oacute; chịu. Thường xuy&ecirc;n thiếu ẩm sẽ dẫn đến da kh&ocirc;, bong tr&oacute;c, dễ bị k&iacute;ch ứng, vi&ecirc;m nhiễm&hellip; do c&aacute;c nh&acirc;n tố g&acirc;y hại đến từ b&ecirc;n ngo&agrave;i. Về l&acirc;u d&agrave;i, việc mất độ ẩm sẽ l&agrave;m đẩy nhanh qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a của da.<br />\r\n<br />\r\nSữa Rửa Mặt Dạng Bọt Tinh Chất Serum&nbsp;<a href=\"https://cocoshop.vn/group/dove-dermaseries/\">Dove</a>&nbsp;chứa đến 40% tinh chất Serum gi&uacute;p mang lại hiệu quả giữ ẩm s&acirc;u, kh&ocirc;i phục độ ẩm cho l&agrave;n da v&agrave; ngăn ngừa c&aacute;c t&aacute;c nh&acirc;n g&acirc;y l&atilde;o h&oacute;a. Trong đ&oacute; bao gồm c&oacute;:<br />\r\n<br />\r\n- Stearic acid c&oacute; cấu tr&uacute;c tương tự như lipid tự nhi&ecirc;n c&oacute; trong da người, được sử dụng trong c&ocirc;ng thức sản phẩm như một chất hoạt động bề mặt dịu nhẹ, gi&uacute;p l&agrave;m mềm da v&agrave; mang lại cảm gi&aacute;c m&aacute;t mẻ dễ chịu khi sử dụng, đồng thời ngăn ngừa thất tho&aacute;t, hư tổn lipid ở ở h&agrave;ng r&agrave;o bảo v&ecirc;̣ tự nhi&ecirc;n của da, bảo vệ l&agrave;n da mỏng manh nhạy cảm.<br />\r\n- Glycerin cấp ẩm cho da<br />\r\n- HEU (Hydroxyethyl Urea) c&oacute; khả năng thẩm thấu s&acirc;u v&agrave;o lớp sừng để dưỡng ẩm v&agrave; th&uacute;c đẩy sản xuất AMPs (anti-microbia peptides) tăng cường h&agrave;ng r&agrave;o bảo v&ecirc;̣ tự nhi&ecirc;n của da khỏe mạnh.<br />\r\n- Vitamin B3 (Niacinamide) gi&uacute;p kh&aacute;ng vi&ecirc;m &amp; l&agrave;m dịu da, giảm hiện tượng đỏ da &amp; đốm n&acirc;u.<br />\r\n- Ceramides gi&uacute;p phục hồi độ ẩm cho da, tăng cường h&agrave;ng r&agrave;o ẩm v&agrave; gi&uacute;p bảo vệ da khỏi t&aacute;c hại của m&ocirc;i trường b&ecirc;n ngo&agrave;i. Đặc biệt đ&acirc;y cũng l&agrave; th&agrave;nh phần dưỡng da rất được người ti&ecirc;u d&ugrave;ng tại Nhật ưa chuộng v&agrave; t&igrave;m kiếm khi mua c&aacute;c sản phẩm chăm s&oacute;c da.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"bot rua mat dove tinh chat serum cap am cho da mem muot 1\" src=\"https://cocoshop.vn/uploads/shops/2020_12/bot-rua-mat-dove-tinh-chat-serum-cap-am-cho-da-mem-muot-1.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nKết cấu sản phẩm dạng Mousse tạo bọt sẵn si&ecirc;u mịn, lướt &ecirc;m nhẹ nh&agrave;ng tr&ecirc;n da, hạn chế việc ng&oacute;n tay tiếp x&uacute;c trực tiếp với da tay, từ đ&oacute; gi&uacute;p l&agrave;m sạch da tối ưu m&agrave; kh&ocirc;ng g&acirc;y tổn thương hay k&iacute;ch ứng, ngăn ngừa việc da bị chảy xệ hay ch&ugrave;ng nh&atilde;o trong tương lai, đồng thời duy tr&igrave; độ ẩm tự nhi&ecirc;n thiết yếu cho l&agrave;n da mềm mại kh&ocirc;ng kh&ocirc; căng sau khi rửa mặt.&nbsp;<br />\r\n<br />\r\nSản phẩm được sản xuất tại Nhật Bản. C&ocirc;ng thức đ&atilde; được kiểm nghiệm da liễu, kh&ocirc;ng chứa paraben, kh&ocirc;ng m&agrave;u nh&acirc;n tạo. Độ pH c&acirc;n bằng, an to&agrave;n cho mọi loại da.<br />\r\n<br />\r\n<strong>Mousse Rửa Mặt Beauty Moisture (M&agrave;u Xanh Dương) &ndash; D&agrave;nh cho da kh&ocirc; &amp; thiếu ẩm.</strong><br />\r\n<br />\r\nSữa rửa mặt dạng bọt si&ecirc;u mịn với tinh chất - Serum dưỡng ẩm c&ugrave;ng với dưỡng chất Ceramides, vừa gi&uacute;p l&agrave;m sạch da v&agrave; lớp trang điểm nhẹ, vừa dưỡng ẩm cho l&agrave;n da. Sản phẩm c&oacute; hương thơm đặc trưng, sống động v&agrave; hiện đại với tầng hương đầu Citrus thanh m&aacute;t đầy sức sống kết hợp với tầng hương giữa của c&aacute;c loại hoa đầy nữ t&iacute;nh như hoa Hồng h&ograve;a quyện trong tầng hương cuối thơm ng&aacute;t nồng n&agrave;n của xạ hương &amp; gỗ.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"bot rua mat dove tinh chat serum cap am cho da mem muot 3\" src=\"https://cocoshop.vn/uploads/shops/2020_12/bot-rua-mat-dove-tinh-chat-serum-cap-am-cho-da-mem-muot-3.jpg\" style=\"height:403px; width:600px\" /></p>\r\n', 0, '1619072452_sua-rua-mat-dove-giup-se-lo-chan-long-kiem-dau.jpg', 195000, 5, 13),
(25, 'Kem trị mụn, phục hồi da Medi-peel Centella Mezzo', '<p><strong>Kem trị mụn, phục hồi da Medi-peel Centella Mezzo</strong><br />\r\n- T&aacute;i tạo, phục hồi bề mặt da<br />\r\n- Liền sẹo, mờ th&acirc;m<br />\r\n- Dưỡng ẩm, mềm mịn da<br />\r\n- L&agrave;m trắng da<br />\r\n- Chống oxy h&oacute;a, ngừa nếp nhăn<br />\r\n- Giảm mụn v&agrave; ti&ecirc;u mụn</p>\r\n', '<p>&quot;thần tốc&quot; chiết xuất từ rau m&aacute; được ưa chuộng tại H&agrave;n v&agrave; được nhiều chị em review 5 sao<br />\r\n<br />\r\nChất kem mịn, kh&ocirc;ng qu&aacute; lỏng cũng kh&ocirc;ng qu&aacute; đặc, dễ t&aacute;n, khi apply l&ecirc;n da sẽ m&aacute;t nhẹ, kh&ocirc;ng g&acirc;y nhờn r&iacute;t hay bết d&iacute;nh. M&ugrave;i hương dược mỹ phẩm thơm dịu<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"kem tri mun phuc hoi da medi peel centella mezzo 1\" src=\"https://cocoshop.vn/uploads/shops/2020_09/kem-tri-mun-phuc-hoi-da-medi-peel-centella-mezzo-1.jpg\" style=\"height:831px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nTh&agrave;nh phần chiết xuất rau m&aacute;, c&agrave; t&iacute;m + c&aacute;c yếu tố tăng trưởng tế b&agrave;o biểu b&igrave; EGF, peptide v&agrave; c&aacute;c loại thảo mộc gi&uacute;p điều trị, l&agrave;m ti&ecirc;u nh&acirc;n mụn. Kh&aacute;ng vi&ecirc;m, chống sưng tấy, đỏ da do mụn g&acirc;y n&ecirc;n<br />\r\n<br />\r\nHoạt chất Clindamycin Phosphate gi&uacute;p trị mụn hiệu quả m&agrave; kh&ocirc;ng g&acirc;y tổn thương cho da<br />\r\n<br />\r\nTh&agrave;nh phần Madecassol gi&uacute;p phục hồi, t&aacute;i tạo l&agrave;n da tổn thương, da sau khi nặn mụn, tạo h&agrave;ng r&agrave;o bảo vệ da khỏe mạnh<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"kem tri mun phuc hoi da medi peel centella mezzo 2\" src=\"https://cocoshop.vn/uploads/shops/2020_09/kem-tri-mun-phuc-hoi-da-medi-peel-centella-mezzo-2.jpg\" style=\"height:652px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nChi&ecirc;́t xu&acirc;́t nha đam và các loại tinh d&acirc;̀u gi&uacute;p dưỡng &acirc;̉m, chống oxy h&oacute;a, cho l&agrave;n da mềm mịn &gt;&gt;xem th&ecirc;m&nbsp;<a href=\"https://chonmyphamtot.com/kem-tri-mun-centella.htm\">kem trị mụn&nbsp;centella</a><br />\r\n<br />\r\n<strong>TH&Agrave;NH PHẦN</strong><br />\r\n<br />\r\n- Chiết xuất RAU M&Aacute;, C&Agrave; T&Iacute;M: l&agrave;m mềm m&aacute;t da, kh&aacute;ng khuẩn, giảm th&acirc;m sẹo<br />\r\n- Asiatic Acid, Asiaticoside, Madecassol v&agrave; Madecassic Acid gi&uacute;p k&iacute;ch th&iacute;ch t&aacute;i tạo da, liền c&aacute;c vết thương, l&agrave;m mờ vết sẹo v&agrave; vết th&acirc;m<br />\r\n- Chiết xuất nha đam: l&agrave;m m&aacute;t v&agrave; mềm mịn da tức th&igrave;<br />\r\n- Sodium Hyaluronate &amp; Vitamin B : giữ ẩm v&agrave; l&agrave;m mềm mịn da, giảm sưng tấy<br />\r\n- Chứa c&aacute;c tinh dầu hạt nho, tinh dầu hạt mắc-ca ... dưỡng ẩm, cho l&agrave;n da săn b&oacute;ng v&agrave; mượt m&agrave;<br />\r\n- Chiết xuất c&aacute;c loại thảo mộc như: c&acirc;y hoa ti&ecirc;u, c&acirc;y bạch đầu &ocirc;ng H&agrave;n Quốc, địa y, hoa sen... kh&aacute;ng vi&ecirc;m, ti&ecirc;u mụn<br />\r\n- Niacinamide v&agrave; Adenosine: l&agrave;m trắng v&agrave; chống nhăn da hiệu quả<br />\r\n- Chứa 5 loại peptide hỗ trợ phục hồi, t&aacute;i tạo da từ b&ecirc;n trong<br />\r\n- Yếu tố EGF: protein xuất hiện tự nhi&ecirc;n c&oacute; khả năng k&iacute;ch th&iacute;ch tăng sinh v&agrave; ph&acirc;n h&oacute;a tế b&agrave;o. Đặc biệt, l&agrave; c&aacute;c tế b&agrave;o biểu b&igrave; tr&ecirc;n da.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"kem tri mun phuc hoi da medi peel centella mezzo 3\" src=\"https://cocoshop.vn/uploads/shops/2020_09/kem-tri-mun-phuc-hoi-da-medi-peel-centella-mezzo-3.jpg\" style=\"height:535px; width:600px\" /></p>\r\n', 0, '1619072579_kem-tri-mun-phuc-hoi-da-medi-peel-centella-mezzo.jpg', 255000, 10, 15),
(26, 'Gel Rửa Mặt-Tắm Giúp Làm Sạch Bã Nhờn, Giảm Mụn,', '<p><strong>Gel Rửa Mặt-Tắm Gi&uacute;p L&agrave;m Sạch B&atilde; Nhờn, Giảm Mụn, Th&acirc;m Sẹo-Ngăn Ngừa Mụn T&aacute;i Ph&aacute;t La Roche-Posay</strong><br />\r\n- Giảm 51% mụn.<br />\r\n- Giảm 57% vết th&acirc;m.<br />\r\n- Hiệu quả được kiểm chứng chỉ sau 4 tuần sử dụng.</p>\r\n', '<p>&nbsp;</p>\r\n\r\n<p>Hỗ Trợ Giảm Mụn Cho Mặt &amp; To&agrave;n Th&acirc;n Effaclar Micro-Peeling Purifying Gel l&agrave; sản phẩm l&agrave;m sạch da mới nhất vừa được ra mắt từ thương hiệu dược mỹ phẩm La Roche-Posay, thuộc d&ograve;ng Effaclar chăm s&oacute;c da dầu mụn, với t&aacute;c động k&eacute;p - hiệu quả 2 trong 1 d&ugrave;ng được cho mặt v&agrave; to&agrave;n th&acirc;n, kh&ocirc;ng chỉ gi&uacute;p l&agrave;m sạch dịu nhẹ m&agrave; c&ograve;n hỗ trợ giảm mụn, vết th&acirc;m v&agrave; ngăn ngừa mụn t&aacute;i ph&aacute;t.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"gel rua mat tam la roche posay 2\" src=\"https://cocoshop.vn/uploads/shops/2020_08/gel-rua-mat-tam-la-roche-posay-2.jpg\" style=\"height:392px; width:700px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nLoại da ph&ugrave; hợp: Sản phẩm ph&ugrave; hợp cho da dầu, da mụn v&agrave; c&oacute; thể sử dụng cho cả da mặt &amp; da cơ thể.<br />\r\n<br />\r\nGiải ph&aacute;p cho t&igrave;nh trạng da:<br />\r\n- Da dầu, lỗ ch&acirc;n l&ocirc;ng to.<br />\r\n- Da mụn v&agrave; dễ bị mụn, bao gồm mụn trứng c&aacute;, mụn sưng vi&ecirc;m, mụn ẩn, mụn đầu đen...<br />\r\n- Mụn v&ugrave;ng lưng, ngực do tăng tiết b&atilde; nhờn.<br />\r\n- Vết th&acirc;m do mụn để lại.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"gel rua mat tam la roche posay 5\" src=\"https://cocoshop.vn/uploads/shops/2020_08/gel-rua-mat-tam-la-roche-posay-5.jpg\" style=\"height:738px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<strong>TH&Agrave;NH PHẦN</strong><br />\r\n<br />\r\nAqua / Water, Sodium Laureth Sulfate, Decyl Glucoside, Glycerin, Sodium Chloride, Coco-betaine, Salicylic Acid, Peg-150 Pentaerythrityl Tetrastearate, Peg-6 Caprylic/capric Glycerides, Zinc Gluconate, Sodium Hydroxide, Capryloyl Salicylic Acid, Tetrasodium Edta, Citric Acid, Menthol, Polyquaternium-47, Hexylene Glycol, Sodium Benzoate.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"gel rua mat tam la roche posay 4\" src=\"https://cocoshop.vn/uploads/shops/2020_08/gel-rua-mat-tam-la-roche-posay-4.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<strong>C&Ocirc;NG DỤNG</strong><br />\r\n<br />\r\n- Giảm 51% mụn.<br />\r\n- Giảm 57% vết th&acirc;m.<br />\r\n- Hiệu quả được kiểm chứng chỉ sau 4 tuần sử dụng.<br />\r\n- Hiệu quả nhanh trong việc giảm mụn sưng vi&ecirc;m v&agrave; th&acirc;m sau mụn. Gi&uacute;p ngăn ngừa mụn t&aacute;i ph&aacute;t v&agrave; quay trở lại.<br />\r\n- Ph&ugrave; hợp với da dầu, da bị mụn sưng vi&ecirc;m v&agrave; nhạy cảm. D&ugrave;ng được cho mặt &amp; cơ thể (mụn lưng, ngực).<br />\r\n- Sản phẩm đ&atilde; được kiểm nghiệm kh&ocirc;ng g&acirc;y k&iacute;ch ứng dưới sự gi&aacute;m s&aacute;t của b&aacute;c sĩ da liễu. Đặc biệt, Ph&ugrave; hợp với kh&aacute;ch h&agrave;ng h&agrave;ng nam đang gặp vấn đề về mụn.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"gel rua mat tam la roche posay 6\" src=\"https://cocoshop.vn/uploads/shops/2020_08/gel-rua-mat-tam-la-roche-posay-6.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<strong>C&Aacute;CH D&Ugrave;NG</strong><br />\r\n<br />\r\n- Cho một lượng nhỏ gel rửa mặt ra l&ograve;ng b&agrave;n tay, tạo bọt sau đ&oacute; xoa đều l&ecirc;n da mặt.<br />\r\n- Massage nhẹ nh&agrave;ng trong 1 ph&uacute;t.<br />\r\n- Rửa sạch lại với nước.<br />\r\n- C&oacute; thể thực hiện tương tự cho v&ugrave;ng da cơ thể.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"gel rua mat tam la roche posay 3\" src=\"https://cocoshop.vn/uploads/shops/2020_08/gel-rua-mat-tam-la-roche-posay-3.jpg\" style=\"height:600px; width:600px\" /></p>\r\n', 0, '1619072687_gel-rua-mat-tam-la-roche-posay-1.jpg', 525000, 10, 13),
(27, 'Sữa Dưỡng Thể Vaseline Dịu Mát Sáng Da 350ml', '<p><strong>Sữa Dưỡng Thể Vaseline Dịu M&aacute;t S&aacute;ng Da 350ml</strong><br />\r\n- Tinh chất Bạc H&agrave; (Menthol) cho cảm gi&aacute;c m&aacute;t lạnh tức th&igrave; v&agrave; l&agrave;m da dịu m&aacute;t suốt cả ng&agrave;y d&agrave;i, ngay cả ở v&ugrave;ng kh&iacute; hậu n&oacute;ng ẩm.<br />\r\n- Hỗ trợ bảo vệ da trước t&aacute;c hại của &aacute;nh nắng mặt trời, duy tr&igrave; vẻ s&aacute;ng mịn của l&agrave;n da.</p>\r\n', '<p>&nbsp;S&aacute;ng Da Body Lotion 200ml l&agrave; sản phẩm dưỡng thể đến từ thương hiệu mỹ phẩm VASELINE của Mỹ, với c&ocirc;ng thức cải tiến d&ograve;ng sản phẩm White Lotion mang lại hiệu quả trắng s&aacute;ng vượt trội nhưng vẫn giữ độ ẩm cần thiết cho da<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"sua duong the vaseline diu mat sang da 2\" src=\"https://cocoshop.vn/uploads/shops/2020_09/sua-duong-the-vaseline-diu-mat-sang-da-2.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nSữa Dưỡng Thể Vaseline Dịu M&aacute;t S&aacute;ng Da Healthy White Fresh &amp; Fair Cooling UV Gel Lotion l&agrave; d&ograve;ng sản phẩm chăm s&oacute;c cơ thể mới nhất vừa được thương hiệu Vaseline cho ra mắt tại thị trường Việt Nam. Sản phẩm chứa gấp 10 lần h&agrave;m lượng Niacinamide gi&uacute;p dưỡng da s&aacute;ng khỏe tự nhi&ecirc;n tr&ocirc;ng thấy chỉ sau 2 tuần, kết hợp c&ugrave;ng tinh chất&nbsp;<a href=\"https://cocoshop.vn/group/vaseline/\">Vaseline</a>&nbsp;Jelly gi&uacute;p kh&oacute;a ẩm cho da căng mượt. Kết cấu dạng gel mỏng nhẹ, thấm si&ecirc;u nhanh, si&ecirc;u s&acirc;u c&ugrave;ng cảm gi&aacute;c m&aacute;t lạnh tức th&igrave; sau khi thoa chắc chắn sẽ l&agrave; lựa chọn tuyệt vời d&agrave;nh cho m&ugrave;a h&egrave; n&oacute;ng nực.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"sua duong the vaseline diu mat sang da 1\" src=\"https://cocoshop.vn/uploads/shops/2020_09/sua-duong-the-vaseline-diu-mat-sang-da-1.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<strong>C&Ocirc;NG DỤNG</strong><br />\r\n<br />\r\n- Chứa gấp 10 lần Niacinamide (Vitamin B3) l&agrave; th&agrave;nh phần dưỡng s&aacute;ng v&agrave; l&agrave;m đều m&agrave;u da, ức chế h&igrave;nh th&agrave;nh melanin, cải thiện sắc tố da hiệu quả, gi&uacute;p da s&aacute;ng l&ecirc;n tr&ocirc;ng thấy chỉ sau 14 ng&agrave;y sử dụng đều đặn.<br />\r\n- Tinh chất Vaseline Jelly độc quyền chứa c&aacute;c giọt s&aacute;p Vaseline Jelly si&ecirc;u nhỏ gi&uacute;p kh&oacute;a ẩm v&agrave; hỗ trợ phục hồi hư tổn từ s&acirc;u b&ecirc;n trong, kh&ocirc;i phục lại h&agrave;ng r&agrave;o bảo vệ da khỏe mạnh.<br />\r\n- Tinh chất Bạc H&agrave; (Menthol) cho cảm gi&aacute;c m&aacute;t lạnh tức th&igrave; v&agrave; l&agrave;m da dịu m&aacute;t suốt cả ng&agrave;y d&agrave;i, ngay cả ở v&ugrave;ng kh&iacute; hậu n&oacute;ng ẩm.<br />\r\n- Hỗ trợ bảo vệ da trước t&aacute;c hại của &aacute;nh nắng mặt trời, duy tr&igrave; vẻ s&aacute;ng mịn của l&agrave;n da.<br />\r\n- Kết cấu sản phẩm dạng gel mỏng nhẹ như những b&ocirc;ng tuyết &quot;tan tr&ecirc;n da&quot;, vỡ ra th&agrave;nh nước m&aacute;t lạnh, thấm si&ecirc;u nhanh, si&ecirc;u s&acirc;u, ho&agrave;n to&agrave;n kh&ocirc;ng g&acirc;y bết d&iacute;nh.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"sua duong the vaseline diu mat sang da 3\" src=\"https://cocoshop.vn/uploads/shops/2020_09/sua-duong-the-vaseline-diu-mat-sang-da-3.jpg\" style=\"height:690px; width:600px\" /></p>\r\n', 0, '1619072877_kem-cham-mun-effaclar-a-i-la-roche-posay-mb121700.jpg', 195000, 10, 14),
(28, 'Sữa Rửa Mặt Hatomugi The Medicated Facial Foam', '<p>Sữa rửa ngừa mụn, dưỡng ẩm v&agrave; l&agrave;m s&aacute;ng da Hatomugi The Medicated Facial Foam 130g Nhật Bản được đ&aacute;nh gi&aacute; rất cao về mặt chất lượng v&agrave; được người ti&ecirc;u d&ugrave;ng tin tưởng sử dụng, đ&acirc;y l&agrave; sản phẩm được chiết xuất từ th&agrave;nh phần hạt &yacute; dĩ nguy&ecirc;n chất gi&agrave;u vitamin B v&agrave; kho&aacute;ng chất,&nbsp;</p>\r\n', '<p>&nbsp;</p>\r\n\r\n<p>Rửa mặt l&agrave; việc l&agrave;m cần thiết gi&uacute;p l&agrave;m sạch da v&agrave; khiến lỗ ch&acirc;n l&ocirc;ng trở n&ecirc;n th&ocirc;ng tho&aacute;ng, ngăn ngừa mụn hiệu quả, tuy nhi&ecirc;n nếu chỉ rửa mặt bằng nước sạch th&ocirc;i th&igrave; chưa đủ, muốn đạt được hiệu quả tối ưu th&igrave; cần c&oacute; sự hỗ trợ từ c&aacute;c sản phẩm sữa rửa mặt.<br />\r\n<a href=\"https://cocoshop.vn/\">-&nbsp;Sữa rửa mặt</a>&nbsp;ngừa mụn, dưỡng ẩm v&agrave; l&agrave;m s&aacute;ng da Hatomugi The Medicated Facial Foam 130g Nhật Bản được đ&aacute;nh gi&aacute; rất cao về mặt chất lượng v&agrave; được người ti&ecirc;u d&ugrave;ng tin tưởng sử dụng, đ&acirc;y l&agrave; sản phẩm được chiết xuất từ th&agrave;nh phần hạt &yacute; dĩ nguy&ecirc;n chất gi&agrave;u vitamin B v&agrave; kho&aacute;ng chất, c&oacute; t&aacute;c dụng sạch khuẩn, lấy đi lớp da nhờn, bụi bẩn, dưỡng ẩm, cấp nước cho da, chống l&atilde;o h&oacute;a gi&uacute;p da mềm mại, s&aacute;ng mịn từ b&ecirc;n trong hiệu quả v&agrave; nhanh ch&oacute;ng.<br />\r\n-&nbsp;Sữa rửa ngừa mụn, dưỡng ẩm v&agrave; l&agrave;m s&aacute;ng da&nbsp;<a href=\"https://cocoshop.vn/group/hatomugi/\">Hatomugi&nbsp;</a>The Medicated Facial Foam 130g Nhật Bản gi&agrave;u protein, kho&aacute;ng chất v&agrave; hơn 8 loại acid amin gi&uacute;p da ngăn ngừa, l&agrave;m sạch, s&aacute;ng da vượt trội v&agrave; l&agrave;m l&agrave;nh c&aacute;c vết thương hiệu quả n&ecirc;n l&agrave; nguy&ecirc;n liệu th&iacute;ch hợp cho nhiều c&ocirc;ng thức dưỡng da hiệu quả.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"sua rua mat hatomugi the medicated facial foam\" src=\"https://cocoshop.vn/uploads/shops/2020_12/sua-rua-mat-hatomugi-the-medicated-facial-foam.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<strong>C&Ocirc;NG DỤNG&nbsp;</strong><br />\r\n<br />\r\n- C&aacute;c th&agrave;nh phần dưỡng ẩm tự nhi&ecirc;n kết hợp với chiết xuất để l&agrave;n da tươi trẻ trong suốt.<br />\r\n- Th&agrave;nh phần dưỡng ẩm: chiết xuất thực vật, chiết xuất Hematogi, chiết xuất l&aacute; đ&agrave;o.<br />\r\n- Th&agrave;nh phần dưỡng ẩm tự nhi&ecirc;n<br />\r\n- Chiết xuất Hamatogi được bổ sung sang trọng để cung cấp độ ẩm cho da.<br />\r\n- Th&agrave;nh phần hoạt t&iacute;nh: Kết hợp với dipotassium glycyrrhizinate, n&oacute; ngăn ngừa mụn trứng c&aacute; v&agrave; vết lo&eacute;t tr&ecirc;n da.<br />\r\n- Th&agrave;nh phần dưỡng ẩm cao: Axit hyaluronic, axit amin bảo vệ da kh&ocirc;ng bị kh&ocirc;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"sua rua mat hatomugi the medicated facial foam 3\" src=\"https://cocoshop.vn/uploads/shops/2020_12/sua-rua-mat-hatomugi-the-medicated-facial-foam-3.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<strong>TH&Agrave;NH PHẦN&nbsp;</strong><br />\r\n<br />\r\nSynthetic fluorophlogopite, sulfuric acid Ba, (HDI / trimethylol hexyl lactone) cross polymer, silica, tri (caprylic acid / capric acid) glyceryl, (diphenyl dimethicone / vinyl diphenyl dimethicone / silsesquioxane) cross polymer, boron nitride , Menthol, menthoxypropanediol, (dimethicone / vinyl dimethicone) crosspolymer, (vinyl dimethicone / methicone silsesquioxane) crosspolymer, tribehenine, trimethylsiloxysilicate, hydroxyapatite, Neubara fruit extract, silylated silica, stearyl dimethicone , Sea salt, ceramide NP, squalane, arch chalk leaf extract, dilauroyl glutamate (octyldodecyl / phytosteryl / behenyl), ascorbyl glucoside, BG, alumina, Al distearate, diph Enylsiloxyphenyl trimethicone, dimyristic acid Al, stearic acid, stearic acid Mg, dimer linoleic acid (phytosteryl / isostearyl / cetyl / stearyl / behenyl), polyisoglycol-2 tetraisostearate, tocopherol, triethoxycaprylylsilane, Hydrogen dimethicone, myristic acid Mg, Al chloride, tin oxide, cerium oxide, water, Al hydroxide, phenoxyethanol, dimethicone, talc, perfluorooctyltriethoxysilane, mica, lauroyl lysine, titanium oxide, zinc oxide, iron oxide.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"sua rua mat hatomugi the medicated facial foam 4\" src=\"https://cocoshop.vn/uploads/shops/2020_12/sua-rua-mat-hatomugi-the-medicated-facial-foam-4.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<strong>C&Aacute;CH D&Ugrave;NG&nbsp;</strong><br />\r\n<br />\r\n-&nbsp;Lấy một lượng th&iacute;ch hợp, tạo bọt kỹ với nước hoặc nước ấm v&agrave; rửa, sau đ&oacute; rửa sạch.<br />\r\n-&nbsp;Rửa mặt 2 lần v&agrave;o buổi s&aacute;ng v&agrave; tối.<br />\r\n-&nbsp;Massge nhẹ nh&agrave;ng theo chuyển động v&ograve;ng tr&ograve;n.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"sua rua mat hatomugi the medicated facial foam\" src=\"https://cocoshop.vn/uploads/shops/2020_12/sua-rua-mat-hatomugi-the-medicated-facial-foam.jpg\" style=\"height:600px; width:600px\" /></p>\r\n', 0, '1619072946_sua-rua-mat-hatomugi-the-medicated-facial-foam.jpg', 121000, 0, 13),
(29, 'Sữa Rửa Mặt Neutrogena Làm Sạch Sâu Dạng Gel Facial Cleanser Deep Clean', '<p>- T&aacute;c động dịu nhẹ l&agrave;m sạch s&acirc;u to&agrave;n diện, cải thiện v&agrave; mang lại l&agrave;n da sạch khỏe, tươi mới.<br />\r\n- Kh&ocirc;ng để lại cặn bẩn l&agrave;m tắc nghẽn lỗ ch&acirc;n l&ocirc;ng.<br />\r\n- Dạng gel dịu nhẹ, mang lại l&agrave;n da mềm mại v&agrave; mịn m&agrave;ng.</p>\r\n', '<p>&agrave; d&ograve;ng sữa rửa mặt đến từ thương hiệu mỹ phẩm Neutrogena của Mỹ, với th&agrave;nh phần hydroxy acid gi&uacute;p lấy đi lượng dầu thừa, nhờn, lớp bụi bẩn v&agrave; lớp trang điểm tr&ecirc;n da đồng thời loại bỏ tế b&agrave;o chết g&acirc;y kh&ocirc; v&agrave; l&agrave;m sạm da.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"sua rua mat neutrogena lam sach sau dang gel 1\" src=\"https://cocoshop.vn/uploads/shops/2020_09/sua-rua-mat-neutrogena-lam-sach-sau-dang-gel-1.jpg\" style=\"height:896px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<strong>Đối tượng sử dụng:</strong><br />\r\nSản phẩm ph&ugrave; hợp với mọi loại da.<br />\r\nDa tiếp x&uacute;c bụi bẩn, &ocirc; nhiễm m&ocirc;i trường.<br />\r\nThường xuy&ecirc;n trang điểm.<br />\r\n<br />\r\n<strong>TH&Agrave;NH PHẦN</strong><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"sua rua mat neutrogena lam sach sau dang gel 2\" src=\"https://cocoshop.vn/uploads/shops/2020_09/sua-rua-mat-neutrogena-lam-sach-sau-dang-gel-2.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<br />\r\n<strong>C&Ocirc;NG DỤNG</strong><br />\r\n<br />\r\n- T&aacute;c động dịu nhẹ l&agrave;m sạch s&acirc;u to&agrave;n diện, cải thiện v&agrave; mang lại l&agrave;n da sạch khỏe, tươi mới.<br />\r\n- Kh&ocirc;ng để lại cặn bẩn l&agrave;m tắc nghẽn lỗ ch&acirc;n l&ocirc;ng.<br />\r\n- Dạng gel dịu nhẹ, mang lại l&agrave;n da mềm mại v&agrave; mịn m&agrave;ng.</p>\r\n', 0, '1619073013_sua-rua-mat-neutrogena-lam-sach-sau-dang-gel-2.jpg', 154000, 10, 13);
INSERT INTO `products` (`id`, `name`, `description`, `content`, `hot`, `photo`, `price`, `discount`, `category_id`) VALUES
(30, 'Phấn Phủ Trang Điểm Bobbi Brown Soft Sand 11g', '<ul>\r\n	<li>\r\n	<p>Finish: mỏng nhẹ, tự nhi&ecirc;n, kiềm dầu cho da.<br />\r\n	Thiết kế: Vỏ ngo&agrave;i đen b&oacute;ng sang trọng c&ugrave;ng d&ograve;ng chữ của h&atilde;ng đ&uacute;ng chuẩn thiết kế Bobbi Brown với hộp vu&ocirc;ng dễ d&agrave;ng mang theo trong t&uacute;i x&aacute;ch</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>hấn phủ Bobbi Brown Sheer Finish Pressed Powder: D&ograve;ng&nbsp;<a href=\"https://cocoshop.vn/phan-phu/phan-bot-kiem-dau-innisfree.html\">phấn phủ kiềm dầu</a>&nbsp;của Bobbi Brown với c&ocirc;ng thức cải tiến mới, nhẹ nh&agrave;ng che phủ ho&agrave;n hảo, cho bạn l&agrave;n da mịn mượt suốt cả ng&agrave;y d&agrave;i. Đặc biệt,&nbsp;<a href=\"https://cocoshop.vn/group/bobbi-brown/\">Bobbi Brown</a>&nbsp;Sheer Finish Pressed Powder c&oacute; kết cấu hạt phấn si&ecirc;u mịn, kh&ocirc;ng chứa dầu gi&uacute;p lớp nền lu&ocirc;n r&aacute;o mịn, lỗ ch&acirc;n l&ocirc;ng sạch tho&aacute;ng, giảm nguy cơ nổi mụn do trang điểm thường xuy&ecirc;n. Ngo&agrave;i ra, Bobbi Brown Sheer Finish Pressed Powder c&ograve;n chứa Vitamin E cho l&agrave;n da cảm gi&aacute;c mềm mượt, mịn m&agrave;ng mỗi khi sử dụng.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"phan phu trang diem bobbi brown 3\" src=\"https://cocoshop.vn/uploads/shops/2020_12/phan-phu-trang-diem-bobbi-brown-3.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n- Phấn phủ n&agrave;y lu&ocirc;n lu&ocirc;n lọt top 10 sản phẩm được ưu th&iacute;ch nhất, b&aacute;n chạy nhất của nh&atilde;n h&agrave;ng Bobbi Brown. Phấn c&oacute; thiết kế cổ điển với hộp vu&ocirc;ng sang trọng, gọn g&agrave;ng k&egrave;m k&iacute;nh v&agrave; b&ocirc;ng dặm với chất lượng rất xứng tầm t&ecirc;n hiệu.<br />\r\n<br />\r\nPhấn c&oacute; m&atilde; m&agrave;u 1 Pale Yellow t&ocirc;ng trắng s&aacute;ng, kiểu trắng ng&agrave; v&agrave;ng sang trọng, lộng lẫy cho c&aacute;c n&agrave;ng c&oacute; l&agrave;n da trắng. Phấn d&ugrave;ng l&ecirc;n da vẫn trắng mịn, mượt m&agrave; trong suốt rất đẹp (kh&ocirc;ng phải kiểu trắng &quot;thạch cao&quot;).<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"phan phu trang diem bobbi brown 5\" src=\"https://cocoshop.vn/uploads/shops/2020_12/phan-phu-trang-diem-bobbi-brown-5.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nPhấn phủ Bobbi Brown Sheer Finish Pressed Powder c&oacute; 4 tone m&agrave;u:<br />\r\n&ndash; 01 Pale Yellow: d&agrave;nh cho da trắng s&aacute;ng<br />\r\n&ndash; 02 Sunny Beige: da trung b&igrave;nh<br />\r\n&ndash; 05 Soft Sand: da trung b&igrave;nh tới ngăm<br />\r\n&ndash; 06 Warm Natural: da ngăm<br />\r\n<br />\r\n<strong>Coco Shop đang c&oacute; 3 tone m&agrave;u</strong>:&nbsp;Soft Sand, Warm Natural, Sunny Beige<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"phan phu trang diem bobbi brown 1\" src=\"https://cocoshop.vn/uploads/shops/2020_12/phan-phu-trang-diem-bobbi-brown-1.jpg\" style=\"height:364px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<strong>Loại da ph&ugrave; hợp:</strong>&nbsp;Sản phẩm th&iacute;ch hợp với mọi loại da.<br />\r\n<br />\r\n<strong>Finish:&nbsp;</strong>mỏng nhẹ, tự nhi&ecirc;n, kiềm dầu cho da.<br />\r\n<br />\r\n<strong>Thiết kế:</strong>&nbsp;Vỏ ngo&agrave;i đen b&oacute;ng sang trọng c&ugrave;ng d&ograve;ng chữ của h&atilde;ng đ&uacute;ng chuẩn thiết kế Bobbi Brown với hộp vu&ocirc;ng dễ d&agrave;ng mang theo trong t&uacute;i x&aacute;ch</p>\r\n', 0, '1619073085_phan-nuoc-ysl-le-cushion-encre-de-peau-luminous-matte-1.jpg', 1350000, 10, 20),
(31, 'Phấn Phủ Kiềm Dầu Dạng Nén Innisfree Pore Blur Pact', '<p><strong>Phấn Phủ Kiềm Dầu Dạng N&eacute;n Innisfree Pore Blur Pact 12.5g</strong><br />\r\n- Kh&oacute;a chặt lớp trang điểm, ngăn ch&uacute;ng bị oxi h&oacute;a khi tiếp x&uacute;c với m&ocirc;i trường ngoại cảnh<br />\r\n- C&acirc;n chỉnh v&agrave; l&agrave;m dịu m&agrave;u sắc khi lớp trang điểm của bạn qu&aacute; đậm.<br />\r\n- Gi&uacute;p lớp nền đều m&agrave;u, b&aacute;m chặt tr&ecirc;n da v&agrave; l&acirc;u xuống t&ocirc;ng.</p>\r\n', '<p>ản phẩm&nbsp;<a href=\"https://cocoshop.vn/phan-phu/\">Phấn Phủ</a>&nbsp;Dạng N&eacute;n Innisfree Pore Blur Pact chỉ c&oacute; 1 t&ocirc;ng m&agrave;u duy nhất l&agrave; m&agrave;u nude tự nhi&ecirc;n c&oacute; &aacute;nh hồng nhẹ (do c&oacute; chứa bột hồng Calamine). Khi apply l&ecirc;n da, m&agrave;u phấn sẽ tiệp v&agrave;o da nhưng vẫn &aacute;nh hồng nhẹ, tạo hiệu ứng lớp nền s&aacute;ng mịn, hồng h&agrave;o, khỏe mạnh hơn.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"phan phu kiem dau dang nen innisfree pore blur pact 6\" src=\"https://cocoshop.vn/uploads/shops/2020_10/phan-phu-kiem-dau-dang-nen-innisfree-pore-blur-pact-6.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nPhấn Phủ Dạng N&eacute;n Innisfree Pore Blur Pact c&oacute; kết cấu dạng n&eacute;n với c&aacute;c hạt phấn li ti, c&oacute; khả năng lấp đầy c&aacute;c khoảng hở từ lỗ ch&acirc;n l&ocirc;ng, tạo hiệu ứng che phủ ho&agrave;n hảo, gi&uacute;p lớp nền của bạn trở n&ecirc;n mướt mịn hơn, lỗ ch&acirc;n l&ocirc;ng cũng được se kh&iacute;t hơn.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"phan phu kiem dau dang nen innisfree pore blur pact 4\" src=\"https://cocoshop.vn/uploads/shops/2020_10/phan-phu-kiem-dau-dang-nen-innisfree-pore-blur-pact-4.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nPh&ugrave; hợp với cả l&agrave;n da mẫn cảm với c&aacute;c loại phấn&nbsp;<a href=\"https://cocoshop.vn/trang-diem-makeup/\">trang điểm</a>&nbsp;th&ocirc;ng thường. &ndash; Sản phẩm l&agrave; phấn phủ trang điểm dạng phấn kho&aacute;ng, gi&uacute;p KIỀM DẦU HIỆU QUẢ, giữ được lớp makeup l&acirc;u tr&ocirc;i đ&ecirc;́n 24 giờ &ndash; Sản phẩm tone tự nhi&ecirc;n, giúp bạn có làn da sáng nhẹ, cho bạn lớp nền tươi s&aacute;ng nhưng vẫn tự nhi&ecirc;n<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"phan phu kiem dau dang nen innisfree pore blur pact 5\" src=\"https://cocoshop.vn/uploads/shops/2020_10/phan-phu-kiem-dau-dang-nen-innisfree-pore-blur-pact-5.jpg\" style=\"height:900px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<strong>TH&Agrave;NH PHẦN</strong><br />\r\n<br />\r\n?????? (??????? ???????) &ndash; hoạt động như một chất keo đặc biệt, n&oacute; nằm b&ecirc;n trong collagen, cung cấp sức mạnh, t&iacute;nh linh hoạt v&agrave; khả năng phục hồi cho c&aacute;c m&ocirc; li&ecirc;n kết, giữ nước, sửa chữa collagen, tăng độ đ&agrave;n hồi cho da.<br />\r\n<br />\r\n?????????? ??????? ?????? - ??̣̂? ??? ???̂́? ??́ &ndash; t&aacute;c dụng rất tốt trong việc chống oxy h&oacute;a, bảo vệ da khỏi sự tấn c&ocirc;ng của c&aacute;c gốc tự do c&oacute; hại.<br />\r\n<br />\r\n???? l&agrave; một loại bột kho&aacute;ng nghiền từ một nh&oacute;m kho&aacute;ng chất silicat, c&oacute; t&aacute;c dụng tương tự như một chất tạo m&agrave;u v&agrave; tăng hiệu ứng lấp l&aacute;nh, bắt s&aacute;ng da nhưng l&agrave;nh t&iacute;nh v&agrave; hạn chế tối đa khả năng g&acirc;y k&iacute;ch ứng da.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"phan phu kiem dau dang nen innisfree pore blur pact 7\" src=\"https://cocoshop.vn/uploads/shops/2020_10/phan-phu-kiem-dau-dang-nen-innisfree-pore-blur-pact-7.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<strong>C&Ocirc;NG DỤNG</strong><br />\r\n<br />\r\n- Kh&oacute;a chặt lớp trang điểm, ngăn ch&uacute;ng bị oxi h&oacute;a khi tiếp x&uacute;c với m&ocirc;i trường ngoại cảnh<br />\r\n- C&acirc;n chỉnh v&agrave; l&agrave;m dịu m&agrave;u sắc khi lớp trang điểm của bạn qu&aacute; đậm.<br />\r\n- Gi&uacute;p lớp nền đều m&agrave;u, b&aacute;m chặt tr&ecirc;n da v&agrave; l&acirc;u xuống t&ocirc;ng.<br />\r\n- H&uacute;t nhờn, kiềm dầu, gi&uacute;p lớp nền kh&ocirc;ng bị b&oacute;ng bẩy, giữ cho da kh&ocirc;ng bị bết r&iacute;ch kh&oacute; chịu<br />\r\n- Gi&uacute;p lỗ ch&acirc;n l&ocirc;ng th&ocirc;ng tho&aacute;ng v&agrave; kh&ocirc;ng g&acirc;y mụn.<br />\r\n- L&agrave;m dịu da v&agrave; l&agrave;m m&aacute;t cục bộ<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"phan phu kiem dau dang nen innisfree pore blur pact 2\" src=\"https://cocoshop.vn/uploads/shops/2020_10/phan-phu-kiem-dau-dang-nen-innisfree-pore-blur-pact-2.jpg\" style=\"height:637px; width:600px\" /></p>\r\n', 0, '1619073234_phan-phu-kiem-dau-dang-nen-innisfree-pore-blur-pact-1.jpg', 452000, 10, 20),
(32, 'Bảng Phấn Mắt Maybelline New York The City Mini Palette', '<p>Bảng Phấn Mắt Maybelline 6 &Ocirc; The City Mini Palette l&agrave; bảng phấn mắt đến từ thương hiệu mỹ phẩm nổi tiếng Maybelline của Mỹ, với 6 &ocirc; m&agrave;u theo nhiều tone m&agrave;u thời thượng, l&acirc;u tr&ocirc;i bền m&agrave;u từ m&agrave;u nhũ tới m&agrave;u l&igrave; gi&uacute;p bạn dễ d&agrave;ng s&aacute;ng tạo ra những phong c&aacute;ch trang điểm thật độc đ&aacute;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>ảng<a href=\"https://cocoshop.vn/phan-mat-phan-ma/\">&nbsp;Phấn Mắt</a>&nbsp;Maybelline 6 &Ocirc; The City Mini Palette l&agrave; bảng phấn mắt đến từ thương hiệu mỹ phẩm nổi tiếng Maybelline của Mỹ, với 6 &ocirc; m&agrave;u theo nhiều tone m&agrave;u thời thượng, l&acirc;u tr&ocirc;i bền m&agrave;u từ m&agrave;u nhũ tới m&agrave;u l&igrave; gi&uacute;p bạn dễ d&agrave;ng s&aacute;ng tạo ra những phong c&aacute;ch trang điểm thật độc đ&aacute;o. Sản phẩm c&oacute; chất phấn thuần m&agrave;u h&ograve;a tan tr&ecirc;n bầu mắt cho độ l&ecirc;n m&agrave;u chuẩn hơn, c&aacute;c hạt phấn si&ecirc;u mịn, b&aacute;m m&agrave;u cao.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"bang phan mat maybelline new york the city mini palette 1\" src=\"https://cocoshop.vn/uploads/shops/2020_12/bang-phan-mat-maybelline-new-york-the-city-mini-palette-1.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"bang phan mat maybelline new york the city mini palette 2\" src=\"https://cocoshop.vn/uploads/shops/2020_12/bang-phan-mat-maybelline-new-york-the-city-mini-palette-2.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"bang phan mat maybelline new york the city mini palette 3\" src=\"https://cocoshop.vn/uploads/shops/2020_12/bang-phan-mat-maybelline-new-york-the-city-mini-palette-3.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hiện sản phẩm&nbsp;Bảng Phấn Mắt Maybelline 6 &Ocirc; 450 Graffiti Pop The City Mini Palette<strong>&nbsp;</strong>đ&atilde; c&oacute; mặt tại<strong>&nbsp;</strong>với c&aacute;c m&agrave;u:&nbsp;</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>450 Graffiti Pop</p>\r\n	</li>\r\n	<li>\r\n	<p>420 Urban Jungle</p>\r\n	</li>\r\n	<li>\r\n	<p>400 Rooftop Bronzes</p>\r\n	</li>\r\n	<li>\r\n	<p>410 Chill Brunch Neutrals</p>\r\n	</li>\r\n	<li>\r\n	<p>Westside Roses<br />\r\n	&nbsp;</p>\r\n\r\n	<p><img alt=\"bang phan mat maybelline new york the city mini palette 6\" src=\"https://cocoshop.vn/uploads/shops/2020_12/bang-phan-mat-maybelline-new-york-the-city-mini-palette-6.jpg\" style=\"height:600px; width:600px\" /></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"bang phan mat maybelline new york the city mini palette 10\" src=\"https://cocoshop.vn/uploads/shops/2020_12/bang-phan-mat-maybelline-new-york-the-city-mini-palette-10.jpg\" style=\"height:600px; width:600px\" /></p>\r\n', 0, '1619073306_bang-phan-mat-maybelline-new-york-the-city-mini-palette-12.jpg', 288000, 10, 19),
(33, 'Tạo Khối 2 Đầu Silkygirl Photosharp Contour & Highlighte', '<p>&nbsp;Gi&uacute;p thon gọn, tạo chiều s&acirc;u v-line.<br />\r\n- Tạo hiệu ứng bắt s&aacute;ng cho khu&ocirc;n mặt.<br />\r\n- Cho l&agrave;n m&ocirc;i trở n&ecirc;n căng mọng hơn.<br />\r\n- Che phủ quầng th&acirc;m, bọng mắt ng</p>\r\n', '<p>Quả kh&ocirc;ng thể phủ nhận xu hướng&nbsp;<a href=\"https://cocoshop.vn/che-khuyet-diem-concealer/tao-khoi-thoi-2-dau-w7-contour-stick.html\">tạo khối</a>&nbsp;đang thịnh h&agrave;nh trong cộng đồng makeup, ch&uacute;ng rất hữu &iacute;ch trong việc định h&igrave;nh, l&agrave;m khu&ocirc;n mặt trở n&ecirc;n thon gọn, thanh tho&aacute;t hơn m&agrave; kh&ocirc;ng cần d&ugrave;ng đến dao k&eacute;o. B&uacute;t Tạo Khối Photosharp Contour &amp; Highlighter đến từ thương hiệu SILKYGIRL với thiết kế 2 đầu tiện dụng gồm 1 đầu gi&uacute;p tạo khối, đầu c&ograve;n lại l&agrave;m highlight nhằm bắt s&aacute;ng khu&ocirc;n mặt trở n&ecirc;n rạng rỡ, tỏa s&aacute;ng hơn.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"tao khoi 2 dau silkygirl 02 natural medium 3\" src=\"https://cocoshop.vn/uploads/shops/2020_11/tao-khoi-2-dau-silkygirl-02-natural-medium-3.jpg\" style=\"height:494px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nSILKYGIRL l&agrave; thương hiệu nổi tiếng của Malaysia với c&aacute;c d&ograve;ng mỹ phẩm, nước hoa, sản phẩm chăm s&oacute;c t&oacute;c v&agrave; sản phẩm chăm s&oacute;c c&aacute; nh&acirc;n kh&aacute;c.&nbsp;<a href=\"https://cocoshop.vn/group/silkygirl/\">SILKYGIRL</a>&nbsp;kh&aacute; mạnh tại thị trường c&aacute;c nước Đ&ocirc;ng Nam &Aacute; như Malaysia, Sing, Brunei, Indonesia...SILKYGIRL cung cấp phong ph&uacute; c&aacute;c loại mỹ phẩm chất lượng cao, thể hiện t&iacute;nh c&aacute;ch vui vẻ, trẻ trung, đầy m&agrave;u sắc v&agrave; sự tự tin cho mỗi bạn g&aacute;i. Hiện tại, SILKYGIRL đ&atilde; c&oacute; mặt tại thị trường Việt Nam v&agrave; được đ&ocirc;ng đảo mọi người tin d&ugrave;ng.<br />\r\n<br />\r\nSản phẩm c&oacute; kết cấu dạng kem mềm mại, dễ t&aacute;n, kh&ocirc;ng bết d&iacute;nh v&agrave; kh&ocirc;ng g&acirc;y kh&oacute; chịu. Photosharp Contour &amp; Highlighter th&agrave;nh phần tự nhi&ecirc;n l&agrave;nh t&iacute;nh, kh&ocirc;ng chứa Paraben hay kh&ocirc;ng g&acirc;y hương liệu g&acirc;y hại cho da. Đặc biệt, SILKYGIRL cam kết kh&ocirc;ng thử nghiệm tr&ecirc;n động vật. Hiện Coco Shop c&oacute; 2 tone m&agrave;u:<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"tao khoi 2 dau silkygirl 02 natural medium 1\" src=\"https://cocoshop.vn/uploads/shops/2020_11/tao-khoi-2-dau-silkygirl-02-natural-medium-1.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n- 01 Natural Light - D&agrave;nh cho da s&aacute;ng<br />\r\n- 02 Natural Medium - D&agrave;nh cho da tự nhi&ecirc;n<br />\r\n<br />\r\n<strong>Loại da ph&ugrave; hợp:</strong>&nbsp;Sản phẩm th&iacute;ch hợp với mọi loại da.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"tao khoi 2 dau silkygirl 02 natural medium 4\" src=\"https://cocoshop.vn/uploads/shops/2020_11/tao-khoi-2-dau-silkygirl-02-natural-medium-4.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<strong>C&Ocirc;NG DỤNG</strong><br />\r\n<br />\r\n- Gi&uacute;p thon gọn, tạo chiều s&acirc;u v-line.<br />\r\n- Tạo hiệu ứng bắt s&aacute;ng cho khu&ocirc;n mặt.<br />\r\n- Cho l&agrave;n m&ocirc;i trở n&ecirc;n căng mọng hơn.<br />\r\n- Che phủ quầng th&acirc;m, bọng mắt ngay.<br />\r\n<br />\r\n<strong>C&Aacute;CH D&Ugrave;NG</strong><br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"tao khoi 2 dau silkygirl 02 natural medium 2\" src=\"https://cocoshop.vn/uploads/shops/2020_11/tao-khoi-2-dau-silkygirl-02-natural-medium-2.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nXương g&ograve; m&aacute;: Đ&aacute;nh phấn tạo khối bronzer theo một đường ch&eacute;o từ h&otilde;m m&aacute; đến th&aacute;i dương. Th&ecirc;m một ch&uacute;t v&agrave;o mặt b&ecirc;n xương g&ograve; m&aacute; sẽ gi&uacute;p gương mặt mảnh mai hơn.<br />\r\n<br />\r\nMũi: Tạo đường n&eacute;t bắt đầu từ đầu ch&acirc;n m&agrave;y tạo hiệu ứng V-line cho sống mũi. K&eacute;o d&agrave;i đường n&agrave;y xuống ph&iacute;a dưới mũi cho một đầu mũi tinh tế hơn (v&agrave; cả 2 b&ecirc;n c&aacute;nh mũi nữa nh&eacute;). Bạn cũng c&oacute; thể th&ecirc;m một ch&uacute;t v&agrave;o trong lỗ mũi gi&uacute;p mũi mỏng hơn.<br />\r\n<br />\r\nQuai h&agrave;m: Đ&aacute;nh phấn tạo khối bắt đầu từ cằm l&ecirc;n ph&iacute;a xương g&ograve; m&aacute;. Th&ecirc;m một ch&uacute;t v&agrave;o 2 mặt b&ecirc;n xương h&agrave;m.</p>\r\n', 0, '1619073386_anh-1.jpg', 189000, 10, 2),
(34, 'Phấn má L\'OREAL True match 110', '<p><strong>Phấn m&aacute; L&#39;OREAL True match 110</strong><br />\r\n- Kh&ocirc;ng g&acirc;y bết d&iacute;nh v&agrave; khả năng b&aacute;m m&agrave;u tốt.<br />\r\n-&nbsp;L&ecirc;n m&agrave;u tự nhi&ecirc;n, gam m&agrave;u thời thượng<br />\r\n- C&oacute; khả năng kiềm dầu hiệu quả, da mặt kh&ocirc;ng bị đổ dầu, b&oacute;ng nhờn, l&acirc;u phai.</p>\r\n', '<p>hồng mịn da&nbsp;<a href=\"https://cocoshop.vn/group/loreal/\">L&#39;Oreal</a>&nbsp;Paris True Match Blusher c&oacute; sắc m&agrave;u trẻ trung, nhẹ nh&agrave;ng v&agrave; c&oacute; t&aacute;c dụng kiềm dầu, gi&uacute;p da mặt c&aacute;c n&agrave;ng Eva lu&ocirc;n kh&ocirc; tho&aacute;ng, kh&ocirc;ng bị b&oacute;ng nhờn. Đồng thời hộp phấn được thiết kế bắt mắt n&ecirc;n rất được nhiều bạn g&aacute;i ưa chuộng.<br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"my pham gia re 2322\" src=\"https://cocoshop.vn/uploads/shops/2019_12/my_pham_gia_re_2322.jpg\" style=\"height:534px; width:530px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<strong>TH&Agrave;NH PHẦN&nbsp;</strong><br />\r\n<br />\r\nVitamin C v&agrave; c&aacute;c sắc tố ngọc trai h&ograve;a hợp với sắc tố da tựa như nụ h&ocirc;n m&agrave;u sắc trong suốt lướt nhẹ bề mặt da.<br />\r\n<br />\r\n<strong>C&Ocirc;NG DỤNG</strong><br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"8158e5c4dcaac2397d5314c3439844f6 tn\" src=\"https://cocoshop.vn/uploads/shops/2019_12/8158e5c4dcaac2397d5314c3439844f6_tn.jpg\" style=\"height:320px; width:320px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nPhấn m&aacute; hồng mịn da L&#39;Oreal Paris True Match Blusher c&oacute; th&agrave;nh phần l&agrave; Vitamin C v&agrave; c&aacute;c sắc tố ngọc trai, c&ugrave;ng c&aacute;c dưỡng chất cần thiết để l&ecirc;n m&agrave;u tự nhi&ecirc;n, trẻ trung v&agrave; nu&ocirc;i dưỡng l&agrave;n da khỏe đẹp. Loại phấn n&agrave;y sở hữu nhiều điểm cộng khiến c&aacute;c n&agrave;ng Eva cảm thấy m&ecirc; mẩn như:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Phấn m&aacute; hồng L&#39;Oreal True Match c&oacute; chất phấn mỏng mịn, kh&ocirc;ng g&acirc;y bết d&iacute;nh v&agrave; khả năng b&aacute;m m&agrave;u tốt.</li>\r\n	<li>L&ecirc;n m&agrave;u tự nhi&ecirc;n, gam m&agrave;u thời thượng, tạo n&ecirc;n lớp trang điểm ho&agrave;n hảo gi&uacute;p c&aacute;c n&agrave;ng lu&ocirc;n tươi tắn, trẻ trung v&agrave; sẵn s&agrave;ng tham gia bất kỳ cuộc vui, bữa tiệc n&agrave;o.</li>\r\n	<li>Phấn m&aacute; hồng mịn da L&#39;Oreal Paris True Match Blusher review c&oacute; khả năng kiềm dầu hiệu quả, da mặt kh&ocirc;ng bị đổ dầu, b&oacute;ng nhờn, l&acirc;u phai.</li>\r\n	<li>Th&agrave;nh phần l&agrave;nh t&iacute;nh, tự nhi&ecirc;n v&agrave; an to&agrave;n cho l&agrave;n da, d&ugrave; da v&ocirc; c&ugrave;ng nhạy cảm.</li>\r\n	<li>Kiểu d&aacute;ng của hộp phấn bắt mắt, tiện lợi, dễ d&ugrave;ng v&agrave; bảo quản.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"16085353431070\" src=\"https://cocoshop.vn/uploads/shops/2019_12/16085353431070.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p>Bảng m&agrave;u gồm c&oacute;&nbsp;<br />\r\n&nbsp;T&ugrave;y v&agrave;o l&agrave;n da hay lối trang điểm m&agrave; c&aacute;c n&agrave;ng c&oacute; thể chọn m&agrave;u ph&ugrave; hợp nhất để c&oacute; lớp make-up ho&agrave;n hảo.</p>\r\n\r\n<ul>\r\n	<li>Mẫu 1: #102 &ndash; True Rose &ndash; Hồng cổ điển</li>\r\n	<li>Mẫu 2: #110 &ndash; Rose Guimauve &ndash; Hồng phấn</li>\r\n	<li>Mẫu 3: #114 &ndash; Pink Grapefruit &ndash; Hồng cam</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"5ca30c378fb7e 02042019141607\" src=\"https://cocoshop.vn/uploads/shops/2019_12/5ca30c378fb7e-02042019141607.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&Aacute;CH D&Ugrave;NG</strong><br />\r\n<br />\r\nC&aacute;c n&agrave;ng c&oacute; thể d&ugrave;ng&nbsp;<a href=\"https://cocoshop.vn/phan-mat-phan-ma/\">phấn m&aacute;&nbsp;</a>hồng mịn da L&#39;Oreal Paris True Match Blusher sau khi đ&atilde; thoa phấn phủ trong bước&nbsp;<a href=\"https://cocoshop.vn/trang-diem-makeup/\">make-up</a>, bằng c&aacute;ch d&ugrave;ng cọ qu&eacute;t đường nhỏ tr&ecirc;n m&aacute;, rồi qu&eacute;t từ xương g&ograve; m&aacute; l&ecirc;n ph&iacute;a t&oacute;c mai.</p>\r\n', 0, '1619073551_16085353431070.jpg', 208000, 5, 3),
(35, 'Chì Kẻ Mày 2 Đầu Silkygirl Perfect Brow Liner & Powder', '<p>- Đầu ch&igrave; vặn, kh&ocirc;ng cần chuốt. Chất ch&igrave; &ecirc;m như kem .<br />\r\n- Đầu bột t&aacute;n với bột phấn k&iacute;ch thước si&ecirc;u nhỏ, si&ecirc;u mịn tạo hiệu ứng m&agrave;u tự nhi&ecirc;n, n&eacute;t m&agrave;y đầy đặn.<br />\r\n- Rất dễ d&agrave;ng điều khiển v&agrave; sử dụng.<br />\r\n- Sản phẩm kh&ocirc;ng m&ugrave;i hương, kh&ocirc;ng g&acirc;y k&iacute;ch ứng da.</p>\r\n', '<p>Ch&acirc;n m&agrave;y thực sự l&agrave; một phần quan trọng gi&uacute;p t&ocirc;n l&ecirc;n n&eacute;t đẹp tr&ecirc;n gương mặt nếu bạn biết c&aacute;ch chọn d&aacute;ng m&agrave;y ph&ugrave; hợp với khu&ocirc;n mặt, ch&iacute;nh v&igrave; thế n&ecirc;n ch&igrave; kẻ ch&acirc;n m&agrave;y chắc chắn l&agrave; vật dụng kh&ocirc;ng thể thiếu đối với c&aacute;c c&ocirc; g&aacute;i, đặc biệt l&agrave; những c&ocirc; g&aacute;i c&oacute; cặp ch&acirc;n m&agrave;y thưa v&agrave; nhạt m&agrave;u. Nhưng kh&ocirc;ng phải bạn nữ n&agrave;o cũng biết c&aacute;ch kẻ ch&acirc;n m&agrave;y đẹp. Việc chọn được một c&acirc;y ch&igrave; kẻ m&agrave;y tốt sẽ gi&uacute;p cho việc kẻ ch&acirc;n m&agrave;y được dễ d&agrave;ng hơn.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"chi ke may 2 dau silkygirl perfect brow liner powder 3\" src=\"https://cocoshop.vn/uploads/shops/2020_11/chi-ke-may-2-dau-silkygirl-perfect-brow-liner-powder-3.jpg\" style=\"height:357px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nKhi chọn ch&igrave; kẻ ch&acirc;n m&agrave;y th&igrave; bạn n&ecirc;n lưu &yacute; chọn loại c&oacute; độ mềm th&iacute;ch hợp, đủ mềm để dễ t&aacute;n nhưng kh&ocirc;ng qu&aacute; bết, dễ l&agrave;m cho c&aacute;c sợi l&ocirc;ng m&agrave;y v&oacute;n cục v&agrave;o nhau, c&ograve;n nếu ch&igrave; qu&aacute; cứng sẽ khiến m&agrave;u kh&ocirc;ng l&ecirc;n đủ đậm, dễ bị phai m&agrave;u trong thời gian ngắn. Perfect Brow Liner &amp; Powder l&agrave; sản phẩm ch&igrave; kẻ v&agrave; tạo d&aacute;ng ch&acirc;n m&agrave;y với thiết kế gồm 2 đầu tiện dụng 1 đầu l&agrave; ch&igrave; kẻ v&agrave; 1 đầu l&agrave; bột chải l&ocirc;ng m&agrave;y, gi&uacute;p bạn khai th&aacute;c được nhiều t&iacute;nh năng hơn chỉ trong một sản phẩm.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"chi ke may 2 dau silkygirl perfect brow liner powder 1\" src=\"https://cocoshop.vn/uploads/shops/2020_11/chi-ke-may-2-dau-silkygirl-perfect-brow-liner-powder-1.jpg\" style=\"height:460px; width:460px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\nĐầu ch&igrave; kẻ dạng tr&ograve;n nhỏ gi&uacute;p bạn dễ d&agrave;ng điều chỉnh c&aacute;c g&oacute;c cạnh của đường kẻ để ph&ugrave; hợp với từng vị tr&iacute;, d&agrave;y, mỏng của ch&acirc;n m&agrave;y, hỗ trợ bạn trong việc định h&igrave;nh cho ch&acirc;n m&agrave;y. Chất ch&igrave; mềm mại, c&oacute; độ b&aacute;m cao, l&ecirc;n m&agrave;u đều đặn cho cặp l&ocirc;ng m&agrave;y của bạn trở n&ecirc;n sắc sảo một c&aacute;ch tự nhi&ecirc;n. Đầu c&ograve;n lại l&agrave; đầu bột phấn 3D gi&uacute;p dặm v&agrave; l&agrave;m đầy đặn ch&acirc;n m&agrave;y như h&igrave;nh d&aacute;ng m&agrave; bạn đ&atilde; kẻ sẵn. Chất bột mịn dễ t&aacute;n v&agrave; độ b&aacute;m tốt c&ugrave;ng với bột phấn c&oacute; k&iacute;ch thước si&ecirc;u nhỏ, si&ecirc;u mịn sẽ tạo n&ecirc;n hiệu ứng m&agrave;u thật tự nhi&ecirc;n v&agrave; n&eacute;t m&agrave;y đầy đặn.<br />\r\n<br />\r\n<strong>Coco Shop c&oacute; 2 m&agrave;u:</strong>&nbsp;01 Natural Brown v&agrave; 02 Dark Brown.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"chi ke may 2 dau silkygirl perfect brow liner powder 4\" src=\"https://cocoshop.vn/uploads/shops/2020_11/chi-ke-may-2-dau-silkygirl-perfect-brow-liner-powder-4.jpg\" style=\"height:500px; width:500px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<strong>C&Ocirc;NG DỤNG</strong><br />\r\n<br />\r\n- Đầu ch&igrave; vặn, kh&ocirc;ng cần chuốt. Chất ch&igrave; &ecirc;m như kem .<br />\r\n- Đầu bột t&aacute;n với bột phấn k&iacute;ch thước si&ecirc;u nhỏ, si&ecirc;u mịn tạo hiệu ứng m&agrave;u tự nhi&ecirc;n, n&eacute;t m&agrave;y đầy đặn.<br />\r\n- Rất dễ d&agrave;ng điều khiển v&agrave; sử dụng.<br />\r\n- Sản phẩm kh&ocirc;ng m&ugrave;i hương, kh&ocirc;ng g&acirc;y k&iacute;ch ứng da. &nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"chi ke may 2 dau silkygirl perfect brow liner powder 2\" src=\"https://cocoshop.vn/uploads/shops/2020_11/chi-ke-may-2-dau-silkygirl-perfect-brow-liner-powder-2.jpg\" style=\"height:450px; width:600px\" /></p>\r\n\r\n<p><br />\r\n<br />\r\n<strong>C&Aacute;CH D&Ugrave;NG</strong><br />\r\n<br />\r\n- Sử dụng đầu ch&igrave; định h&igrave;nh ch&acirc;n m&agrave;y từ đầu đến đu&ocirc;i&nbsp;<br />\r\n- Sử dụng đầu cọ m&uacute;t để t&aacute;n đều trong khung ch&acirc;n m&agrave;y đ&atilde; được định h&igrave;nh, t&aacute;n nhạt ở đầu ch&acirc;n m&agrave;y v&agrave; đậm dần về phần đu&ocirc;i&nbsp;<br />\r\n&nbsp;</p>\r\n', 0, '1619073708_chi-ke-may-2-dau-silkygirl-perfect-brow-liner-powder.jpg', 220000, 5, 19);

